INSERT INTO `eating` (`id`, `name`, `material`, `making`, `tips`, `idType`, `bookmark`, `image`) VALUES
(1, 'Bún ốc', '- 2kg thịt ốc\r\n- 1/2kg bún tươi\r\n- Nước dùng: 1,5 lít\r\n- Cà chua: 2 trái\r\n- Đậu hũ chiên: 1 miếng\r\n- Rau thơm: tía tô, kinh giới\r\n- Ngò gai, xà lách\r\n- Ớt khô, ớt hiểm, hành tím băm\r\n- Hành lá, chanh, ớt sa tế, tiêu\r\n- Bột nghệ, mắm tôm\r\n- Giấm gạo lên men\r\n- Hạt nêm\r\n\r\n', '- Ốc ngâm nước vo gạo với ớt hiểm đập dập 1 đêm cho nhả hết chất dơ và nhớt, rửa sạch, đập lấy thịt, ướp ốc với 2 trái ớt hiểm băm, 1 ít tiêu, 1m hành tím băm, 1/2m Hạt nêm 1/2m bột nghệ và 1/2m mắm tôm.\r\n\r\n- Cà chua bổ hình múi cau. Rau xà lách, tía tô, kinh giới cắt sợi. Đậu hũ chiên cắt miếng vuông. Ngâm 1M ớt khô với nước cho mềm. Hành, ngò gai cắt nhuyễn. Chanh cắt miếng nhỏ. Ớt sừng đập dập.\r\n\r\n- Chuẩn bị 1 nồi nước dùng đặt trên bếp.\r\n\r\n- Phi thơm hành tím băm với 1M dầu ăn, cho ốc vào xào sơ, xào ốc vừa chín tới rồi trút ra dĩa.\r\n\r\n- Cho 1M dầu ăn vào chảo, thêm hành tím vào phi thơm, cho cà chua vào xào sơ rồi trút qua nồi nước dùng, thêm đậu hũ, nêm 2M Hạt nêm, 1m mắm tôm, 2M Giấm gạo lên men và ớt khô đã ngâm.\r\n\r\n- Cho 1/2 lượng ốc vào nồi nước dùng, nêm thêm 2M Hạt nêm  và 1M mắm tôm, nếm vị cho vừa ăn.\r\n\r\n - Cho bún, ốc vào tô, chan nước dùng rồi gạn ra lại, cho hành lá và ngò gai vào, chan nước dùng ngập bún. Ăn kèm rau, chanh, ớt sa tế và mắm tôm tùy khẩu vị.\r\n', '- Nên rửa ốc với ít muối và giấm để ốc hết nhớt.\r\n', 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(1).jpg'),
(2, 'Bún Thang', '- ½ con gà ta\r\n- 2 quả trứng vịt\r\n- 100g giò lụa\r\n- 500g xương hom hay xương ống heo\r\n- 1,5 kg bún sợi nhỏ\r\n- 100g tôm khô, 2-3 cái râu mực khô (hoặc sá sùng)\r\n- 200g tôm sú\r\n- Hành lá, rau răm, hành khô và gừng nướng, nấm hương, củ cải khô\r\n- Mắm tôm, gia vị, nước mắm, 1 chút đường phèn, giấm, đường cát trắng\r\n', '- Gà làm sạch, luộc với nước, khi nước sôi cho một chút gia vị và hành khô, gừng đã nướng thơm vào, hạ nhỏ lửa để gà chín bên trong. Khi gà chín vớt ra bát nước lạnh rửa sạch, xé hoặc thái miếng nhỏ.\r\n\r\n- Xương lợn cho vào nồi đun sôi với nước, sau đó đổ hết nước đó đi, cho ra vào nước lạnh rửa sạch vụn xương. Đem xương rang với gia vị đến khi thơm thì đổ nước vào ninh để xương mềm, chắt lấy nước.\r\n\r\n- Tôm khô rửa sạch cho vào chảo rang thơm, râu mực/ sá sùng đem nướng chín.\r\n\r\n- Sau khi vớt gà ra thì cho tôm khô, râu mực, nấm hương rửa sạch, 1 chút đường phèn vào ninh 2-3 tiếng, khi nào chuẩn bị ăn thì trụng lẫn nước hầm xương vào đun thêm 30 phút nữa\r\n\r\n- Trứng vịt đánh tan, cho một lớp dầu chỉ đủ láng chảo rồi đổ trứng vào tráng mỏng. Để trứng thật nguội rồi mới đem thái chỉ.\r\n\r\n- Tôm sú luộc với chút nước đem bóc vỏ, giã nhỏ rồi sao vàng với dầu ăn và nước mắm cho thơm làm ruốc tôm.\r\n\r\n- Giò lụa thái chỉ.\r\n\r\n- Củ cải ngâm nước ấm cho nở, rửa lại bằng nước đun sôi để nguội rồi trộn với giấm và đường để 30 phút.\r\n\r\n- Rau răm và hành lá thái nhỏ.\r\n\r\n- Khi ăn chần bún, xếp các loại nhân lên trên rồi chan nước dùng. \r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(2).jpg'),
(3, 'Bún sườn non', '- 1 kg sườn non chặt khúc nhỏ\r\n- 1 củ hành tây\r\n- 30g bông kim châm \r\n- 3 lát dứa cắt bỏ mắt\r\n- Nửa quả bí đỏ nhỏ\r\n- Vài nhánh hành lá, rau mùi\r\n- Muối, một viên đường phèn, nước mắm\r\n- Rau xà lách xoăn, rau thơm, ớt quả, chanh ăn kèm\r\n- Bún\r\n', '- Sườn non rửa sạch, cho sườn vào nồi đun sôi khoảng 2 phút để loại bỏ tạp chất, rửa sườn lại cho thật sạch.\r\n\r\n- Hoa kim châm rửa sạch, ngâm nở cắt bỏ chân rửa lại nhiều lần nước cho thật sạch.\r\n\r\n - Bí đỏ, gọt vỏ, rửa sạch, thái lát vừa ăn.\r\n\r\n - Dứa cắt bỏ mắt, thái lát mỏng.\r\n\r\n - Hành lá lấy phần đầu hành cắt làm đôi, phần hành xanh thái nhỏ. Rau mùi rửa sạch.\r\n\r\n - Rau xà lách xoăn rửa sạch, thái nhỏ.\r\n\r\n- Rau thơm lặt lấy lá, bỏ cọng, rửa sạch.\r\n\r\n- Cho sườn vào nồi lớn, hành tây bóc bỏ vỏ khô cắt làm đôi, thả hành tây vào nồi sườn, thêm vào một thìa canh muối, một viên đường phèn nhỏ, đun khoảng từ 45 phút đến 1 tiếng.\r\n\r\n- Thêm dứa vào nồi nước dùng.\r\n\r\n- Cho hoa kim châm vào, đun tiếp khoảng 15 phút.\r\n\r\n - Lần lượt cho bí đỏ vào đun cùng, đun lửa nhỏ, nêm gia vị vừa ăn đến khi bí đỏ và phần sườn non mềm.\r\n\r\n- Tắt bếp thêm phần đầu hành vào nồi nước dùng.\r\n\r\n- Khi dùng thêm một ít bún vào bát lớn, múc sườn, thêm bí đỏ, bông kim châm và vài lát dứa vào bát, chan nước dùng, bên trên rắc một ít hành lá, hạt tiêu và rau mùi đã thái nhỏ. Dùng nóng, ăn kèm với rau xà lách xoăn và rau thơm.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(3).jpg'),
(4, 'Phở Bò', '- 2 miếng gừng (khoảng 8cm), 2 củ hành tây\r\n- Khoảng 2kg xương bò, 1kg nạc vai bò\r\n- 100g hành lá\r\n- 1/3 chén nước mắm, 70g đường phèn hoặc 2,5 thìa cafe đường cát\r\n- 8 hoa hồi, 6 nụ hoa đinh hương, 1 miếng quế\r\n- Thảo quả: 2 quả (nếu cần)\r\n- 2 thìa cafe hạt thì là, 2 thìa cafe hạt rau mùi, 1 thìa cafe muối\r\n- 500g bánh phở\r\n- 150g thịt bò thăn, thái lát mỏng\r\n- Ớt, hành khô, hành lá, rau mùi, giá đỗ, húng quế, chanh\r\n', '- Nướng gừng và hành trong khoảng 3 phút, đến khi hơi cháy đen và có mùi thơm nhẹ. Sau khi nướng xong rửa sạch và loại bỏ những chỗ cháy đen.\r\n\r\n- Rửa sạch xương bò, cho vào một nồi lớn và thêm nước. Đun sôi trong 5 phút. Tắt bếp, dùng rây lọc, đổ nước, rửa sạch xương bằng nước lạnh,\r\n\r\n- Thêm 5 lít nước vào nồi và đun sôi. Cho xương, thịt nạc vai vào nồi cùng với gừng và hành củ, thêm hành lá, nước mắm, đường. Giảm lửa và đun cho đến khi thịt mềm, khoảng 40 phút. Trong lúc hầm, thường xuyên vớt bọt.\r\n\r\n- Cho một miếng thịt vào bát nước lạnh sau đó vớt ra cất vào tủ lạnh. Miếng còn lại vẫn để trong nồi.\r\n\r\n- Cho các loại gia vị (hoa hồi, đinh hương, quế, thảo quả, hạt thì là và hạt rau mùi) vào một cái chảo khô rang ở nhiệt độ vừa phải trong khoảng 3 phút, cho đến khi dậy mùi thơm. Cho tất cả vào một miếng vài rồi buộc lại và thả trong nồi nước dùng.\r\n\r\n- Tiếp tục hầm trong 4 tiếng. Thêm muối và đun sôi. Chú ý điều chỉnh gia vị bằng cách thêm muối, đường hoặc nước mắm cho vừa miệng.\r\n\r\n- Khi ăn, nấu nồi nước sôi trụng sơ bánh phở để vào bát, xếp hết các thứ thịt mỗi thứ một ít lên rồi chan nước dùng vào. Trang trí với hành, giá đỗ, rau thơm.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(4).jpg'),
(5, 'Phở bò sốt vang', '- 800 g xương bò.\r\n- 300g gân bò.\r\n- 500 g bánh phở.\r\n-100ml rượu vang đỏ.\r\n- 4 củ hành khô, 1 củ gừng, 2 củ tỏi.\r\n- 5 cánh hoa hồi, 3 quả thảo quả, 1 miếng quế, 7 con sá sùng khô.\r\n- Hành, rau mùi.\r\n- Gia vị: Muối, hạt nêm, bột bắp, ngũ vị hương, bột nghệ.\r\n', '- Xương bò rửa sạch, chần qua với nước sôi. Sau đó, cho vào nồi ninh khoảng 2 tiếng.\r\n\r\n- Gừng rửa sạch thái lát mỏng, tỏi bóc vỏ băm nhỏ.\r\n\r\n- Gân bò rửa sạch, thái miếng nhỏ vừa miệng. Ướp gân bò với 1/2 chỗ gừng tỏi đã giã cùng với gia vị, ngũ vị hương, bột nghệ và 50ml rượu vang. Để khoảng 30 phút cho ngấm.\r\n\r\n- Hành lá, rau mùi rửa sạch chẻ nhỏ.\r\n\r\n- Làm nóng dầu ăn trong nồi, trút gân bò vào xào sơ. Rồi đổ nước vừa ngập mặt gân.\r\n\r\n- 1/3 số gừng nguyên vỏ còn lại nướng chín cùng với hành khô, quế, hoa hồi, thảo quả. Ninh trong khoảng 1 giờ đồng hồ.\r\n\r\n- Khi gân bò đã nhừ, hòa bột bắp với chút nước cho tan hết rồi trút vào nồi gân, vừa trút vừa khuấy đều tay. Cuối cùng cho 50ml rượu vang còn lại vào nồi gân, đun sôi là được.\r\n\r\n- Bánh phở chần qua nước sôi, sắp hành, rau mùi lên trên. Tiếp đó múc 1 muôi gân hầm rải đều lên, sau đó chan nước dùng lên thật đều và thưởng thức.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(5).jpg'),
(6, 'Bánh bột lọc', '- 200gr bột năng\r\n- 100ml nước đang sôi (bắt buộc)\r\n- 1/3 muỗng cà phê muối\r\n- Phần nhân: \r\n	+ 200gr tôm loại nhỏ\r\n	+ 200gr thịt ba chỉ \r\n	+ 1 muỗng canh phần trắng hành lá băm \r\n	+ 1 muỗng canh tỏi hành băm \r\n	+ 1 muỗng cà phê nước mắm\r\n	+ 1 muỗng canh đường\r\n	+ 1 muỗng cà phê bột nêm\r\n	+ 1/4 muỗng cà phê tiêu.\r\n- Nước mắm tỏi ớt: \r\n	+ 2 muỗng canh nước mắm\r\n	+ 4 muỗng canh nước\r\n	+ 2 muỗng canh đườn\r\n	+ 1 muỗng canh nước cốt chanh hoà tan trong 1 cái chén, sau đó cho tỏi và ớt băm vào.\r\n- 1/2 chén hành lá thái nhỏ (dành để làm mỡ hành)\r\n', '- Thịt heo rửa sạch, thái miếng nhỏ vừa ăn. Tôm cắt đầu và râu rửa sạch để ráo.\r\n\r\n- Bắc chảo lên bếp, cho vào 1 muỗng canh dầu và đường thắng nước đường với lửa vừa. Khi đường có màu nâu cánh dán thì cho thịt, tỏi, hành và hành lá vào xào săn 5-7 phút. Sau đó cho tôm vào xào chung với các gia vị. Xào cho thịt tôm rút nước là tắt bếp.\r\n\r\n- Bột, muối cho vào âu trộn đều. Sau đó cho nước sôi vào mang bao tay nhồi cho bột quyện thành 1 khối không dính tay.\r\n\r\n- Rắc chút bột năng lên bàn, để cục bột lên cán mỏng. Dùng khuôn tròn hay miệng ly cắt khoanh. Cho thịt và tôm vào từng miếng bột gấp đôi bóp dính chặt mép.\r\n\r\n- Nấu 1 nồi nước sôi, cho bánh vào luộc với lửa vừa. Khi bánh nổi lên bạn cần luộc thêm 5 phút nữa cho bánh chín. Sau đó vớt bánh cho vào âu nước đá lạnh 1 phút trước khi vớt bánh ra dĩa. Ngâm nước đá lạnh giúp bánh trong, dai hơn rất nhiều.\r\n\r\n- Bắc chảo lên bếp, cho vào 2 muỗng canh dầu, chờ dầu nóng thì cho hành lá vào xào thơm 30 giây. Sau đó cho bánh bột lọc vào đảo nhẹ đều cho bánh không dính mà bóng mượt hấp dẫn lại thơm ngon.\r\n\r\n- Xếp bánh ra dĩa. Có ít xà lách và hoa tỉa từ dưa chuột, kèm với chén nước mắm ớt tỏi.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(6).jpg'),
(7, 'Bánh trôi tàu', '- 1kg bột bánh trôi\r\n- 1 gói đường phên\r\n- Nước hoa bưởi\r\n- Mè trắng, dừa sợi.\r\n', '- Đường phên cắt hạt lựu (mỗi chiều 1cm).\r\n\r\n- Nhào bột đều tay đến khi dẻo, mịn, chia bột thành những viên nhỏ, ấn dẹt bột, cho đường vào giữa, bao kín lại, vê tròn.\r\n\r\n- Đun sôi nước, thả bánh vào luộc, đun lửa vừa phải, bao giờ những viên bánh nổi đều, vớt ra thả vào chậu nước sôi để nguội. Bánh nguội, cho ra đĩa, khi ăn rắc nước hoa bưởi và mè trắng, sợi dừa lên trên.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(7).jpg'),
(8, 'Bánh bao khoai lang tím', '- Bột bánh bao: 200gr\r\n- Sữa tươi: 80ml\r\n- Nước cốt dừa: 30ml\r\n- Đường 50gr\r\n- Khoai lang tím: 50gr\r\n- Dầu ăn: 10ml, men khô\r\n', '- Khoai lang tím mua về các bạn rửa sạch bùn đất bám ngoài, xếp vào xửng hấp chín.\r\n \r\n- Đợi khoai nguội các bạn tiến hành bóc vỏ.\r\n \r\n- Dùng thìa tán nhuyễn khoai qua 1 chiếc rây lưới.\r\n \r\n- Đun hoặc cho sữa vào lò vi sóng quay đến khi sữa hơi ấm, lấy ra hòa với đường + nước cốt dừa + men rồi chia hỗn hợp làm đôi.\r\n \r\n- Chia bột làm 2 phần, phần bột 120gr các bạn nhào trộn với 1/2 lượng hỗn hợp sữa vừa chuẩn bị, phần bột 80gr các bạn bóp trộn đều với khoai lang tím rồi nhào nốt với lượng hỗn hợp sữa còn lại. Sau khi nhào xong, đợi bột nghỉ khoảng 5 phút, các bạn mới cho dầu ăn vào rồi nhào tiếp 1 lần nữa.\r\n \r\n- Phủ 1 chiếc khăn mặt ẩm hoặc bọc màng bọc thực phẩm lên trên bát bột vừa nhào, ủ 1 giờ cho bột nở gấp đôi.\r\n \r\n- Cán 2 miếng bột màu trắng và tím thành 2 phần có diện tích tương đương, đặt chồng lên nhau, cán nhẹ cho 2 lớp bột thêm kết dính rồi cuộn tròn lại, cắt khối bột thành từng khúc dày khoảng 2-3cm.\r\n\r\n- Xếp vào xửng và hấp từ 15 - 20 phút sau khi nước sôi là bánh chín. \r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(8).jpg'),
(9, 'Bánh xèo', '– 200g bột gạo\r\n– 250ml nước\r\n– 100ml bia\r\n– 50ml nước cốt dừa\r\n– ½ thìa café muối\r\n– 1/2 thìa café bột nghệ\r\n– Hành lá, dầu ăn\r\n– 200g thịt bò\r\n– 200g tôm\r\n– 1 củ hành tây\r\n– 100g giá đỗ\r\n– Gừng, hành khô, tiêu, gia vị, nước mắm\r\n', '– Trộn đều bột gạo, muối, bột nghệ. Pha 250ml nước với 100ml bia, 50ml nước cốt dừa để hòa bột. Khuấy đều bột.\r\n\r\n– Hành lá xắt nhỏ. Cho hành lá vào bột, thêm chút dầu ăn, sau đó để bột nghỉ 30 phút – 1 tiếng.\r\n\r\n– Thịt bò thái mỏng, ướp với dầu ăn, tỏi, gừng, gia vị, hạt tiêu.\r\n\r\n– Tôm cắt râu, rửa sạch. Ướp tôm với hành khô, gừng, gia vị. Bạn có thể để hành và gừng to một chút để lúc xào nhân nhặt ra cho khỏi bị cháy.\r\n\r\n– Làm nóng dầu ăn trong chảo, cho hành tây vào xào thơm cùng chút gia vị. Tiếp theo cho tôm vào đảo cùng. Khi tôm gần chín thì cho thịt bò vào, hỗn hợp nhân chín bạn múc ra để riêng.\r\n\r\n– Cho dầu ăn vào chảo, chờ chảo thật nóng và dầu sôi. Đổ bột láng xung quanh chảo cho thật mỏng, đậy vung khoảng nửa phút.\r\n\r\n– Cho hỗn hợp nhân và giá lên trên, đậy vung khoảng 2 phút nữa. Gập đôi bánh và chờ đến khi bánh giòn hẳn, vàng đều 2 mặt.\r\n\r\n– Hành tây thái múi cau, giá đỗ rửa sạch. Rau sống và cải xanh nhặt rửa sạch.\r\n\r\n– Nước chấm pha chua ngọt theo tỷ lệ: 5 nước, 2 đường, 1.5 mắm, 1 nước cốt chanh, gia giảm cho vừa miệng rồi thêm tỏi, ớt băm vào.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(9).jpg'),
(10, 'Bánh cuốn nhân thịt', '- 500 gram bột gạo\r\n- 1 lít nước lọc\r\n- 500 gram thịt nạc dăm\r\n- 300 gram chả lụa\r\n- 100 gram nấm mèo\r\n- 100 gram củ hành tím\r\n- 1 củ hành tây\r\n- Mắm, muối, đường, hạt tiêu, hạt nêm\r\n- Giá đỗ, rau thơm, ớt, tỏi\r\n', '- Cho 1 lít nước vào bột gạo khuấy đều, cho thêm 1/4 muỗng cafe muối, để bột nở trong 30 phút.\r\n\r\n- Thịt nạc dăm rửa sạch, để ráo, thái miếng nhỏ rồi dùng dao băm nhuyễn.\r\n\r\n- Hành tím bóc vỏ, 1/2 thái miếng nhỏ băm đều, 1/2 bào nhỏ. Xin lưu ý với những món thịt heo các bạn nên dùng hành tím để nấu cùng thay vì tỏi vì vị tỏi làm cho mùi thịt hơi ngái ngái, ăn không ngon. Cho 1 muỗng canh hành tím vừa băm vào thịt để ướp.\r\n\r\n- Cho chảo lên bếp, đổ 2 muỗng canh dầu ăn vào chảo. Cho tiếp 1/2 hành tím bào nhỏ vào phi thơm đến khi hành tím chín vàng thì vớt ra để ráo dầu làm hành phi.\r\n\r\n- Hành tây bóc vỏ, rửa sạch, thái hạt lựu để làm phần nhân bánh. Nấm mèo ngâm nước, cắt cuống chân rồi cắt sợi, băm nhỏ.\r\n\r\n-  Cho chảo lên bếp, cho 2 muỗng canh dầu ăn vào. Dầu ăn nóng thì cho phần hành tím còn lại vào chảo phi thơm đến khi hành ngả vàng. Cho tiếp phần thịt băm đã ướp vào chảo xào, đảo đều tay. Đến khi thịt gần chín, cho tiếp phần hành tây thái hạt lựu vào chảo, xào đến khi hành tây chín rồi mới nêm gia vị.\r\n\r\n- Lúc này nước trong chảo đã gần cạn, sệt sệt, bắt đầu cho 1 muỗng canh nước mắm, 2 muỗng cafe hạt nêm, 1 muỗng cafe đường vào trộn đều. Khi nước cạn, cho phần nấm mèo băm nhỏ vào, đảo đều lần nữa, cho 1 muỗng cafe hạt tiêu vào trước khi tắt bếp.\r\n\r\n-  Bắc chảo chống dính lên bếp, thoa lên chảo một lớp dầu ăn thật mỏng bằng bông gòn hoặc cọ, để lửa vừa, không to quá hoặc nhỏ quá. Cho 1 muỗng canh lớn nước bột gạo vào chảo, lắc đều cho nước lan đều mặt chảo. Đậy vung chảo lại, để trong 5 giây là bánh chín, úp chảo lên một cái đĩa lớn có phết một lớp dầu ăn.Cho 1 muỗng canh phần nhân trực tiếp lên bánh, không nên để nhân ở giữa bánh, mà để nhân ở gần phần đầu của bánh. Dùng tay cuộn đều bánh lại rồi xếp ra đĩa. \r\n\r\n-  Pha nước mắm theo tỉ lệ 1 mắm:2 nước:1 đường. Ớt và tỏi băm nhỏ, cho 1 muỗng cafe ớt và 1 muỗng cafe tỏi vào nước mắm, khuấy đều.\r\n\r\n-  Giá đỗ rửa sạch, ngâm với chút muối, trụng qua nước sôi. Chả lụa cắt miếng vừa ăn. Rắc 1 muỗng canh hành phi rắc lên bánh cuốn ăn cho ngon. \r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(10).jpg'),
(11, 'Canh sườn hạt sen', '- Sườn non : 300g\r\n- Hạt sen tươi : 150g\r\n- Bắp non : 50g\r\n- Bông cải xanh : 100g\r\n- Cà rốt : 50g\r\n- Nấm hương : 5 tai\r\n- Tiêu, nước mắm, hành lá, ngò rí\r\n- Hạt nêm\r\n', '- Sườn non chặt miếng vừa cỡ 3cm, ướp 1m hạt nêm và một ít tiêu.\r\n\r\n- Bắp non, bông cải, cà rốt cắt miếng vừa ăn. Nấm hương sơ chế sạch, cắt đôi.\r\n\r\n- Đun sôi 1.5 lít nước, cho sườn vào nấu, nêm 1M hạt nêm nấu sườn khoảng 15 phút, cho bắp non vào, cà rốt, hạt sen, nấm hương vào, cuối cùng cho bông cải nấu vừa chín. Tắt lửa, thêm 1/2M nước mắm, hành ngò.\r\n\r\n- Múc canh ra tô, rắc thêm tiêu. Dọn kèm nước mắm nguyên chất và ớt cắt lát. Dùng với cơm.\r\n', '- Chọn sườn non dầy thịt, ít mỡ, cọng xương nhỏ, nhiều sụn giúp món ăn ngon hơn.\r\n- Chọn hạt sen tươi có bên ngoài trắng, sáng, không bị thâm là hạt sen ngon.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(11).jpg'),
(12, 'Canh gà cải bó xôi', '- Cánh gà : 2 cánh\r\n- Cải bó xôi : 400g\r\n- Cà rốt : 100g\r\n- Hành lá\r\n- Gừng\r\n- Tiêu, muối, bột ngọt, hạt nêm\r\n', '- Cánh gà chần qua nước sôi có gừng đập dập và 1/2M muối, chặt khúc vừa ăn, ướp với 1m hạt.\r\n\r\n- Cải bó xôi cắt khúc 4cm. Cà rốt tỉa hoa cắt lát. Gừng cắt lát, đập dập. Hành lá cắt khúc.\r\n\r\n- Cho cánh gà vào 1.2 lít nước nấu chín với gừng, cho cà rốt vào nấu khoảng 5 phút, cho tiếp cải bó xôi vào nấu chín, nêm 1M hạt nêm , 1/2m muối và 1/2m bột ngọt, tắt lửa.\r\n\r\n- Cho canh ra tô, rắc ít tiêu lên trên, dùng nóng với cơm\r\n', '- Chần cánh gà qua nước sôi có gừng và muối để cánh gà thơm hơn.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(12).jpg'),
(13, 'Canh vịt nấu khoai sọ', '- Thịt vịt : 600g\r\n- Khoai sọ : 300g\r\n- Cà rốt : 1/2 củ\r\n- Hành tím : 5 củ\r\n- Ngò gai, rau om\r\n- Tiêu, nước mắm, dầu ăn, hạt nêm\r\n', '- Thịt vịt sơ chế sạch, khứa vài đường trên da, áp chảo cho ra bớt mỡ. Chặt miếng vừa ăn, ướp với 1m hạt nêm.\r\n\r\n- Khoai sọ gọt vỏ, cắt miếng vừa ăn. Cà rốt cắt miếng. Ngò gai, rau om cắt nhỏ.\r\n\r\n- Phi thơm hành tím, cho thịt vịt vào xào săn, cho 1.2 lít nước sôi vào, nêm 1M hạt nêm nấu vịt khoảng 15-20 phút cho thịt vịt vừa chín, thêm khoai sọ và cà rốt vào nấu chín. Tắt lửa, thêm ngò gai, rau om.\r\n\r\n- Cho canh ra tô rắc tiêu, dọn kèm nước mắm nguyên và ớt cắt lát. Dùng với cơm.\r\n', '- Xào vịt với củ hành tím để vịt có mùi thơm, khi nấu chín mềm, ngọt và không bị nát. ', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(13).jpg'),
(14, 'Canh chua cá lóc', '- Cá lóc: 2 khứa\r\n- Bạc hà: 1/2 cây\r\n- Cà chua\r\n- Đậu bắp\r\n- Thơm (Dứa): 1/4 trái\r\n- Giá đỗ: 100g\r\n- Me vắt: 50g\r\n- Rau om, ngò gai, ớt sừng\r\n- Tỏi và sả bằm\r\n- Đường, muối, nước mắm, dầu ăn, bột ngọt\r\n', '- Cá lóc rửa sạch, ướp với 1m muối, 1m bột ngọt, 1m ớt bằm, để thơm.\r\n\r\n- Đậu bắp, bạc hà rửa sạch, cắt xéo. Cà chua cắt múi. Thơm, ớt cắt lát.\r\n\r\n- Rau om, ngò gai rửa sạch, cắt nhỏ. Me dằm lấy nửa chén nước me.\r\n\r\n- Phi thơm tỏi và sả bằm, xào sơ cá cho săn, trút ra dĩa.\r\n\r\n- Đun sôi 1,5 lít nước với nước me, cho cá vào nấu chín, sau đó thêm đậu bắp, thơm, cà chua, giá. Tắt lửa, cho bạc hà vào, nêm 1m đường trước, nêm 2m muối, 1/2M nước mắm.\r\n\r\n- Múc canh ra tô, thêm rau om, ngò gai, ớt cắt lát và tỏi phi lên trên. Dùng nóng.\r\n', '- Cá vốn có mùi tanh, xào sơ cá với gia vị trước khi nấu sẽ loại bỏ mùi tanh của cá.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(14).jpg'),
(15, 'Canh sườn non nấu sấu', '- 400g sườn non sụn\r\n- 100g sấu xanh\r\n- 150g cà chua\r\n- Rau nhút\r\n- Hành hoa, mùi tàu\r\n- Muối, dầu ăn, gia vị, hạt nêm, nước mắm\r\n', '-  Sườn sụn non xát muối, rửa sạch, chặt khúc vừa ăn, chần qua nước sôi, rửa sạch.\r\n\r\n- Rau nhút nhặt lấy phần non, ngắt khúc 4-5cm, rửa sạch.\r\n\r\n- Cà chua rửa sạch, bổ hình múi cau, xào sơ.\r\n\r\n- Sấu cạo vỏ, đun chín mềm, lọc lấy nước.\r\n\r\n- Hành hoa và rau mùi tàu rửa sạch, thái nhỏ.\r\n\r\n- Đun nóng 1 lít nước, cho sườn vào nấu chín mềm. Nêm bột gia vị, nước sấu, hạt nêm, nước mắm vừa ăn. Cho rau nhút và cà chua vào nấu thêm khoảng 1-2 phút, tắt bếp, cho hành và rau mùi tàu.\r\n\r\n- Múc canh ra tô, dùng nóng, chấm kèm nước mắm.\r\n', NULL, 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(15).jpg'),
(16, 'Gà chiên bơ', '- Thịt ức gà : 250g\r\n- Khoai tây : 250g\r\n- Lá quế : 10 lá\r\n- Bột năng\r\n- Bơ tươi, tiêu, dầu ăn, sữa tươi có đường, sữa đặc có đường\r\n- Xốt mayonnaise \r\n- Bột chiên gà giòn\r\n', '- Gà cắt khối vuông 1.5cm. Lá quế rửa sạch, để ráo.\r\n\r\n- Khoai tây cắt khối vuông 1.5cm, luộc sơ trong nước có ít bột năng, vớt ra để ráo.\r\n\r\n- Đun nóng dầu, cho lá quế vào chiên đến khi vàng.\r\n\r\n- Xóc gà với bột chiên gà giòn, đem chiên chín vàng vớt ra cho vào 1 cái rổ, cho vào 1 ít tiêu & 1m bơ vào xóc đến khi bơ thấm vào gà. Tiếp tục xóc khoai tây với bột chiên gà giòn còn lại, đem chiên chín vàng và vớt ra xóc với 1m bơ, 1 ít tiêu.\r\n\r\n- Pha xốt: trộn đều hỗn hợp 3M xốt mayonnaise, 1M sữa tươi, 1m sữa đặc và một ít tiêu.\r\n\r\n- Xếp gà và khoai ra dĩa. Trang trí với ít lá quế chiên. Khi ăn chấm kèm xốt pha.\r\n', '- Xóc gà và khoai với một ít bơ để tạo vị thơm hấp dẫn.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(16).jpg'),
(17, 'Chả mực chiên', '- Mực nang: 200g\r\n- Mỡ gáy: 20g\r\n- Củ năng: 20g\r\n- Bột xù: 50g\r\n- Xà lách xoong: 50g\r\n- Nấm mèo: 1 tai\r\n- Trứng gà: 1 quả\r\n- Cà chua bi: 8 trái\r\n- Đầu hành băm, mè trắng\r\n- Tiêu, dầu ăn, tương xí muội, bột ngọt\r\n- Xốt Mayonnaise\r\n', '- Mực xay phần thịt, phần vè và râu mực cắt nhỏ, băm sơ. Mỡ gáy cắt hạt lựu. Nấm mèo ngâm nở, cắt sợi. Củ năng gọt vỏ, băm nhỏ. Trứng gà đập lấy lòng đỏ.\r\n\r\n- Cho mực, mỡ gáy, củ năng, nấm mèo và lòng đỏ trứng vào tô, nêm 1/2m bột ngọt, 1m tiêu và 1M đầu hành băm, quết cho dai và thầm gia vị.\r\n\r\n - Thoa tay với bột xù, vo viên chả thành hình tròn, ấn dẹp tạo lỗ nhỏ ở giữa, rắc ít mè lên mặt, chiên vàng giòn.\r\n\r\n- Trộn xốt chấm: trộn đều 2M xốt Mayonnaise và 1M tương xí muội.\r\n\r\n - Xếp chả mực chiên ra dĩa đã bày xà lách xoong, ấn trái cà chua bi vào giữa, khi ăn chấm xốt Mayonnaise trộn tương xí muội.\r\n', '- Chọn mực nang làm chả thì chả sẽ dai hơn.\r\n- Quết thật nhuyễn để món chả được dai hơn khi chiên.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(17).jpg'),
(18, 'Củ sen chiên tôm xù', '- Củ sen : 600g\r\n- Tôm tươi: 200g\r\n- Giò sống: 250g\r\n- Trứng gà: 2 quả\r\n- Bột mì: 150g\r\n- Bột xù: 100g\r\n- Hành, tỏi bằm, ngò rí, ớt\r\n- Đường, tiêu, dầu ăn, hạt nêm\r\n- Xốt mayonnaise.\r\n', '- Củ sen cạo sạch sẽ, cắt lát chéo dày 20mm, ngâm vào nước có pha nước cốt chanh 5 phút cho không bị đen rồi rữa sạch, để ráo.\r\n\r\n- Tôm bóc vỏ, chừa đuôi, khứa nhẹ dưới bụng tôm rồi nắn cho tôm thẳng, ướp với 1m hành, tỏi bằm, 1m tiêu, ½ m đường và 1m hạt nêm, để thấm.\r\n\r\n- Ngò rí rửa sạch, lấy phần cọng cắt nhỏ. Ớt băm nhỏ. Trứng gà đập vào tô đánh tan.\r\n\r\n- Múc giò sống trét vào 1 mặt miếng củ sen, đặt con tôm lên ló đuôi ra khoảng 1,5cm, trét giò sống lên miếng thứ hai rồi đặt lên trên con tôm, kẹp lại.\r\n\r\n- Lăn củ sen kẹp tôm qua bột mì, nhúng qua trứng rồi tẩm bột xù, cho vào chảo dầu nóng chiên chín vàng, vớt ra để ráo dầu.\r\n\r\n- Pha xốt chấm: trộn đều 4M xốt mayonnaise với 1M ngò và 1M ớt băm.\r\n\r\n- Xếp củ sen chiên tôm ra đĩa, khi ăn chấm với xốt mayonnaise trộn ngò và ớt.\r\n', NULL, 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(18).jpg'),
(19, 'Thịt heo chiên giòn sốt chua ngọt', '- 350g thịt thăn\r\n- 120g xốt cà chua\r\n- 60g giấm táo \r\n- 60g đường\r\n- 1/3 củ cà rốt; 1 nhánh gừng; 1 đầu hành boa rô\r\n- 5g bột ngô; 55g bột mỳ;\r\n- Muối, dầu.\r\n', '- Cà rốt và gừng gọt vỏ, cắt sợi nhỏ (trong đó gừng cắt càng nhỏ càng tốt).\r\n\r\n- Boa rô cắt thành dạng sợi nhỏ, khúc dài tương tự với cà rốt và gừng.\r\n\r\n- Thịt thăn cắt thành miếng thịt lớn dày cỡ 4 – 5mm, ướp với chút xíu muối khoảng 15 phút cho thấm.\r\n\r\n- Trong lúc chờ đợi, bạn tiến hành trộn các loại xốt. Xốt cà chua hòa tan với chút nước cho loãng bớt. Đổ đường ra bát con, thêm giấm vào khuấy đều cho đến khi đường tan hoàn toàn.\r\n\r\n- Trong một bát khác, khuấy tan bột bắp, bột mỳ, dầu ăn và nước thành một hỗn hợp bột nhão rồi cho thịt vào đảo cho hỗn hợp bột bao đều quanh miếng thịt.\r\n\r\n- Đun nóng chảo dầu đủ ngập mặt thịt, thả từng miếng thịt vào chiên vàng đều. Vớt thịt ra đĩa cho ráo dầu.\r\n\r\n- Chắt bớt dầu ra khỏi chảo, chỉ để lại một lớp láng bề mặt, thêm gừng, cà rốt và tỏi tây vào xào thơm. Đổ xốt cà chua, hỗn hợp xốt giấm chua ở bước 2 vào chảo, tiếp tục đảo đều.\r\n\r\n- Khi hỗn hợp xốt bắt đầu sôi thì trút thịt chiên vào, đun lửa vừa trong khoảng vài phút là được, thỉnh thoảng đảo đều cho thịt ngấm xốt.\r\n\r\n- Gắp thịt ra đĩa, rưới nước xốt lên trên mặt, trang trí bằng hành lá thái sợi. \r\n', NULL, 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(19).jpg'),
(20, 'Đậu hũ chiên sốt tam sắc', '- Đậu hũ non 1 cây\r\n- Cà chua 1 trái\r\n- Ớt Đà Lạt xanh vàng 30g mỗi loại\r\n- Bột chiên xù 50g\r\n- Trứng gà 1 quả\r\n- Dầu mè 1/4 muỗng càphê\r\n- Mè trắng rang 1 muỗng càphê.\r\n- Gia vị: hạt nêm, tiêu, đường, tỏi băm, giấm, dầu ăn.\r\n', '- Đậu hũ cắt khoanh dày 1,5 cm. \r\n\r\n- Trứng gà cho ra chén đánh tan. \r\n\r\n- Bột mì, bột xù cho ra dĩa. \r\n\r\n- Cà chua, ớt Đà Lạt bỏ hạt cắt hạt lựu. \r\n\r\n- Phi dầu với tỏi băm cho ớt và cà chua vào xào nhanh, nêm hạt nêm, đường, giấm vị chua ngọt vừa ăn, tắt lửa thêm ít dầu mè trộn đều. \r\n\r\n- Lăn đậu hũ qua bột mì rồi nhúng vào trứng và lăn qua bột xù, chiên vàng. \r\n\r\n- Trình bày đậu hũ ra đĩa, rưới xốt tam sắc lên, rắc thêm mè rang.\r\n', NULL, 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(20).jpg'),
(21, 'Cháo thịt bò trứng muối', '- Thịt bò: 150g\r\n- Gạo tẻ: 70g\r\n- Gạo nếp: 30g\r\n- Gừng: 1 củ nhỏ\r\n- Hành tím: 2 củ\r\n- Trứng vịt muối: 4 quả\r\n- Hành lá, ngò rí, tiêu, muối, dầu ăn, hạt nêm\r\n', '- Gạo và nếp vo sạch, để ráo. Gừng gọt vỏ, 1/2 cắt lát, 1/2 băm nhỏ. Hành tím băm nhỏ. Hành lá, ngò rí cắt nhỏ.\r\n\r\n- Trứng vịt muối rửa sạch, luộc chín, sau đó cắt đôi, nạo lấy lòng đỏ, bẻ miếng nhỏ.\r\n\r\n- Thịt bò băm nhỏ, ướp với 1m hạt nêm, 1/4m muối, 1/4m tiêu, 1m hành tím băm và 1m gừng băm, để thấm.\r\n\r\n- Cho gạo và nếp vào nồi, thêm 750ml nước, nêm 1 ít muối, nấu đến khi đặc, thêm tiếp 750ml nước lạnh vào nồi nấu đến khi cháo nhừ.\r\n\r\n- Phi thơm hành tím, cho thịt bò vào chảo thật nóng rồi tắt bếp ngay để xào săn, trút vào nồi cháo nấu sôi, tắt lửa, nêm 1m hạt nêm.\r\n\r\n- Múc cháo ra tô, rải lòng đỏ trứng muối, rắc tiêu, hành lá, ngò rí và gừng lên mặt, dùng nóng.\r\n', '- Chọn gạo mới để nấu thì cháo sẽ thơm và dẻo hơn.', 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(21).jpg'),
(22, 'Cháo lươn xứ Nghệ', '- 50g gạo nếp\r\n- 150g gạo tẻ\r\n- 300g lươn\r\n- Hành hoa, rau răm\r\n- 30g hành khô băm\r\n- 1 nhánh nghệ (50g)\r\n- 2 quả chanh\r\n- Gia vị, hạt nêm, nước mắm hạt tiêu\r\n- Dầu ăn.\r\n', '- Lươn chà chanh cho hết nhớt, thấm khô, lọc xương để riêng, lấy thịt thái miếng nhỏ.\r\n\r\n- Nghệ gọt vỏ, giã nhỏ, vắt lấy nước cốt.\r\n\r\n- Bạn ướp lươn với nước nghệ, 1 thìa cà-phê bột gia vị,1/2 thìa cà-phê bột nêm, tiêu.\r\n\r\n- Xương lươn bạn rang cho khô, rồi đổ nước vào ninh lấy nước ngọt. Cho dạo vào nước lươn ninh cho chín mềm tới khi sánh lại thành cháo. Nêm 1 thìa cà-phê bột gia vị, 1,5 thìa cà-phê hạt nêm, nước mắm cho vừa ăn.\r\n\r\n- Phi thơm hành khô với ít dầu ăn, cho lươn vào xào săn.\r\n\r\n- Rau răm, hành hoa rửa sạch, thái nhỏ.\r\n\r\n- Múc cháo ra bát, cho hành hoa, rau răm và lươn lên.\r\n', '- Để cháo không bị tanh, dùng nước sôi để ninh xương.', 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(22).jpg'),
(23, 'Cháo hàu nấu sen', '- 4 con hàu.\r\n- 1/2 củ cà rốt, 5 tai nấm rơm.\r\n- 1/2 củ sen nhỏ.\r\n- 30gr hạt sen.\r\n- Hạt nêm, muối, đường.\r\n- 1 nắm gạo.\r\n', '- Vo gạo nấu cháo.\r\n\r\n- Hàu tách lấy phần thịt.\r\n\r\n- Nấm rơm thái lát mỏng, củ sen thái hạt lựu lớn, cà rốt gọt vỏ, thái hạt lựu.\r\n\r\n- Hàu trụng sơ qua nước sôi để không làm dục nước cháo.\r\n\r\n- Cháo vừa chín, cho hàu, hạt sen, củ sen, cà rốt vào nấu mềm, nêm hạt nêm, muối, đường vừa ăn.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(23).jpg'),
(24, 'Cháo trứng thịt bằm', '- Thịt thăn heo: 150g\r\n- Gạo tẻ: 70g\r\n- Gạo nếp: 30g\r\n- Trứng gà: 4 quả\r\n- Hành lá: 2 cây\r\n- Ngò rí, hành tím băm\r\n- Muối, tiêu, nước mắm, dầu ăn, hạt nêm\r\n- Giấm gạo lên men \r\n\r\n\r\n', '- Gạo tẻ và nếp vo sạch, cho vào nồi nấu với 750ml nước đến khi cháo nở đặc sệt. Hành lá phần đầu trắng băm nhuyễn, phần lá cắt nhỏ cùng với ngò rí.\r\n\r\n- Thịt heo băm nhuyễn, ướp với 1m hành tím băm, 1/2m hạt nêm, 1/4m tiêu và 5M nước lọc, để thấm 15 phút.\r\n\r\n- Đun sôi 1 lít nước với 3M giấm gạo lên men, giảm lửa, đập trứng thả nhẹ vào nồi sao cho lòng trắng trứng bao quanh lòng đỏ, luộc đến khi trứng nổi lên, vớt ra rửa qua nước lạnh, bóc bớt màng lòng trắng trứng còn dính xung quanh.\r\n\r\n- Thêm 750ml nước lạnh, nấu tiếp đến khi cháo nhừ. Phi thơm 1M hành tím băm với 2M dầu ăn, xào thịt heo vừa chín và tơi đều, cho vào nồi cháo, có độ đặc vừa, nêm thêm 1m hạt nêm và 1M nước mắm, nếm vị vừa ăn.\r\n\r\n- Múc cháo ra tô, cho trứng vào, rắc hành lá, ngò rí và tiêu lên mặt, dùng nóng.\r\n', '- Ướp thịt với ít nước lạnh để khi xào thịt tơi đều.', 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(24).jpg'),
(25, 'Cháo hột vịt bắc thảo', '- 100gr thịt heo nạc\r\n- 1 trứng vịt bắc thảo\r\n- 1 chén gạo\r\n- Hành lá, tiêu, hành tím, gia vị, gừng.\r\n', '- Xắt nhỏ thịt heo, băm nhuyễn. Nếu cầu kỳ hơn, bạn có thể xé nhuyễn thịt.\r\n\r\n- Ngâm gạo trong nước lạnh khoảng 30 phút, sau đó xào qua dầu nóng với hành phi, tiêu, bột ngọt, muối rồi cho vào nồi cháo. Hoặc bạn có thể dùng cơm nguội hay cho gạo vừa xào trên bếp, ít nước vào máy xay.\r\n\r\n- Khi gạo mềm, cho thịt vào cháo. Dằm nhuyễn để thịt không vón cục.\r\n\r\n- Khi cháo sôi, cho tiếp hột vịt bắc thảo vào, dầm sơ cho trứng bể nhưng không dầm quá nhiều, tránh làm nát vụn, mất ngon.\r\n\r\n- Sau khi cháo chín, nêm nếm gia vị vừa ăn.\r\n\r\n- Múc cháo ra tô, cho thêm ít gừng xắt sợi, hành lá xắt nhuyễn và tiêu lên trên.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(25).jpg'),
(26, 'Miến xào chay', '- Thịt cua chay: 100g\r\n- Miến: 120g\r\n- Nấm mèo trắng: 5 tai\r\n- Nấm rơm búp: 50g\r\n- Cà rốt: 50g\r\n- Ớt Đà Lạt đỏ: 50g\r\n- Cần tây: 1 bẹ\r\n- Poa-rô: 1 cây\r\n- Cải bẹ xanh: 1 cây\r\n- Ớt sừng\r\n- Tiêu sọ xay, dầu ăn, dầu mè, ước tương\r\n- Hạt nêm từ Nấm Hương và Hạt Sen\r\n', '- Cà rốt, ớt đà lạt, cần tây, cọng cải xanh cắt sợi, poa-rô cắt lát. Lá cải xanh cắt ngang 3mm. Nấm rơm cắt đôi.\r\n\r\n- Miến trụng nhanh qua nước sôi, vớt ra xả nước, để ráo, trộn đều với 1m hạt nêm từ Nấm Hương và Hạt Sen và 1M nước tương.\r\n\r\n- Thịt cua trụng qua nước sôi, cắt dọc. Nấm mèo trụng nhanh qua nước sôi, cắt sợi, ướp với chút tiêu.\r\n- Chiên giòn phần lá cải, vớt ra để ráo dầu.\r\n\r\n- Phi thơm poa-rô, cho nấm rơm, cà rốt, ớt và cần vào xào sơ, nêm 1m hạt nêm từ Nấm Hương và Hạt Sen, 1/2m đường, 1 ít tiêu, cho tiếp miến vào trộn đều, tiếp tục cho nấm mèo, thịt cua chay vào trộn đều, thêm 1/2m dầu mè, tắt lửa.\r\n\r\n- Cho miến ra đĩa, rắc tiêu và lá cải chiên giòn lên trên. Dọn kèm nước tương và ớt cắt lát.\r\n', '- Miến được xào rất nhanh trên chảo nên cần trộn miến trước với gia vị để gia vị thấm đều.\r\n- Nấm mèo sẽ cho vào sau cùng và xào nhanh, nên trụng trước qua nước sôi, ướp chút tiêu để tạo mùi thơm.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(26).jpg'),
(27, 'Đậu hũ bao bố', '- Đậu hủ trắng: 3 miếng\r\n- Nấm đông cô: 3 tai\r\n- Củ năng cắt hạt lựu nhỏ: 20g\r\n- Chả chay cắt hạt lựu nhỏ: 60g\r\n- Cà rốt cắt hạt lựu nhỏ: 20g\r\n- Bắp cải cắt sợi nhỏ: 30g\r\n- Bông hẹ chần: 8 cọng\r\n- Nước lạnh: 1/2 chén\r\n- Poa-rô băm nhỏ: 1M\r\n- Cải thìa luộc chín: 4 cây\r\n- Dầu hào chay: 1M\r\n- Dầu ăn, tương ớt, muối, tiêu, dầu mè, đường, bột năng pha loãng\r\n- Nước tương, hạt nêm từ Nấm Hương và Hạt Sen\r\n', '- Đậu hủ cắt khối hình chữ nhật 6x4 cm, chiên vàng, múc phần ruột ra để tạo thành những \"bao bố\" nhỏ. Phần ruột tán nhuyễn.\r\n\r\n- Nấm đông cô ngâm mềm, cắt hạt lựu.\r\n\r\n- Nhồi \"bao bố\": Phi thơm poa-rô, cho rau củ vào xào, nêm 2m hạt nêm 1m đường, 1/2m tiêu, trút ra dĩa, trộn với phần ruột đậu hủ tán nhuyễn. Cho nhân vào \"bao bố\" và dùng bông hẹ cột nhẹ tay.\r\n\r\n- Phi thơm tỏi, xào cải thìa nêm 1 ít hạt nêm.\r\n\r\n- Làm xốt: Hòa tan 1 chén nước với 1M dầu hào chay, 2M nước tương, 1/2M đường, 1/2m từ Nấm Hương và Hạt Sen và 1M tương ớt, nấu sôi, giảm lửa, thêm 1/2M nước bột năng khuấy sệt. Sau cùng cho 1/2m dầu mè vào đảo đều.\r\n\r\n- Cho \"bao bố\" vào nước xốt kho 5 phút, tắt lửa.\r\n\r\n- Xếp \"bao bố\" lên dĩa có lót cải thìa và chan xốt lên trên trước khi ăn\r\n', '- Lấy kỹ phần ruột đậu hũ ở phía trên cho thật mỏng để tạo hình bao bố đẹp hơn.\r\n- Giảm lửa khi cho bột năng pha loãng vào để xốt không bị vón cục.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(27).jpg'),
(28, 'Củ hũ dừa kho nấm chay', '- Củ hũ dừa tười: 200g\r\n- Nấm rơm búp: 100g\r\n- Đậu hũ chiên: 100g\r\n- Nước dừa: 1/2 chén\r\n- Đậu bắp: 100g\r\n- Ớt hiểm đập dập: 2 trái\r\n- Poiro băm, sả băm, ngò rí\r\n- Đường, tiêu, dầu ăn\r\n- Nước tương, Hạt nêm từ nấm hương và hạt sen\r\n', '- Củ hũ dừa cắt miếng chữ nhật dày 1cm kích thước 4cmx2,5cm.\r\n\r\n- Đậu bắp cắt khúc như củ hũ dừa.\r\n\r\n- Nấm rơm rửa sạch, cắt hình chữ thập ở đầu nấm.\r\n\r\n- Ngò rí cắt khúc, ớt hiểm đập dập.\r\n\r\n- Phi thơm sả băm và poiro, cho lần lượt củ hũ dừa, nấm rơm vào xào. Cho thêm 2M Nước tương, 1m Hạt nêm, 1m đường, 1/2 chén nước dừa.\r\n\r\n- Cho đậu hũ và ớt hiểm vào kho với lửa nhỏ khoảng 10 phút, rồi cho đậu bắp vào thêm khoảng 3 phút. Nấu đến khi nước hơi sánh thì tắt lửa.\r\n\r\n- Cho ra đĩa, trang trí với ngò, rắc thêm tiêu, dùng nóng với cơm.\r\n', '- Ngâm củ hũ dừa sau khi cắt vào nước có pha cốt chanh để củ hũ dừa không bị thâm đen.\r\n- Cho đậu bắp vào sau cùng để đậu bắp vừa chin tới, không bị mềm và bể.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(28).jpg'),
(29, 'Sườn chay rim mật ong', '- Sườn chay đã ngâm nở: 300g\r\n- Mật ong: 2/3M\r\n- Nước dừa tươi: 1 chén\r\n- Ớt hiểm: 2 trái\r\n- Ngò rí, boaro phi\r\n- Tiêu, dầu điều, dầu ăn, nước tương, bột ngọt\r\n', '- Sườn chay rửa sạch, ngâm nước ấm 30 phút cho nở mềm, vớt ra để ráo, cắt miếng vừa ăn. Ướp sườn với 1M boaro phi, 1/2 m bột ngọt, 3M nước tương , 2/3M 1/3M mật ong, 1M dầu điều, 1 chút tiêu, để thấm đều.\r\n\r\n- Lá boaro cắt nhỏ. Ớt hiểm đập dập.\r\n\r\n- Xào săn sườn chay với 1 ít dầu, thêm 1/3M mật ong, 1 ít nước tương cho thấm gia vị, tiếp theo cho nước dừa sâm sấp, rim với lửa riu riu đến khi nước sệt lại. Thêm 2 trái ớt hiểm đập dập vào, cuối cùng thêm ngò rí, lá boaro và rắc thêm tiêu.\r\n\r\n- Cho sườn rim ra dĩa, dùng với cơm trắng và rau dưa.\r\n', '- Dùng nước dừa rim sườn tạo độ ngọt thanh và thơm', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(29).jpg'),
(30, 'Bún gạo xào chay', '- Miến đậu xanh hoặc bún gạo ngâm nước lạnh cho nở\r\n- 100g đỗ quả, rửa sạch, tước xơ, bào mỏng\r\n- 100g bắp cải, thái ngang thành sợi\r\n- 100g giá đỗ, rửa sạch để ráo.\r\n- 1 củ cà rốt, thái chỉ\r\n- Rau cải ngọt, rửa sạch thái khúc\r\n- Hạt nêm, xì dầu.\r\n ', '- Bún gạo ngâm vài tiếng cho ngấm nước và mềm.\r\n\r\n- Đun nóng chút dầu ăn, cho cà rốt thái chỉ vào xào trong 1 phút.\r\n\r\n- Cho tiếp bắp cải và đỗ quả vào xào cùng, nêm chút hạt nêm cho ngấm.\r\n\r\n- Tiếp đến là rau cải rồi đổ ra bát.\r\n\r\n- Đặt chảo trở lại bếp, thêm chút dầu ăn rồi cho bún gạo vào xào. Nêm chút xíu hạt nêm. Bún gạo mềm thì cho chỗ rau củ xào vừa nãy vào chung.\r\n\r\n- Đảo đều, nêm nếm lại cho vừa ăn, có thể thêm tí xì dầu để màu sắc đẹp hơn.\r\n\r\n- Cuối cùng cho giá đỗ. Xào thêm chừng 2 phút thì tắt bếp.', NULL, 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(30).jpg'),
(31, 'Cơm chiên trái thơm', '- Cơm trắng: 2 chén\r\n- Thơm còn nguyên phần đầu: 1 trái\r\n- Tôm tươi: 100g\r\n- Tôm khô, hành tây, hành lá, ớt\r\n- Xà lách, cà chua, dưa leo\r\n- Hành tím băm, tỏi băm, bột năng\r\n- Đường, muối, tiêu, dầu ăn\r\n- Nước tương, hạt nêm\r\n', '- Thơm bổ dọc trái, giữ nguyên phần đầu để trang trí, lấy ruột cắt hạt lựu khoảng 3M thơm, ướp với 1 ít hạt nêm.\r\n\r\n- Hành tây cắt hạt lựu. Hành lá rửa sạch, cắt nhỏ.\r\n\r\n- Tôm tươi bóc vỏ cắt hạt lựu, ướp 1 ít hạt nêm và tiêu, sau đó trộn đều với 1ít bột năng. Phi thơm hành tỏi, xào tôm cho săn lại, thêm 1 ít nước dùng cho tôm chín.\r\n\r\n- Tôm khô ngâm nước ấm, xào sơ với hành, tỏi băm và tiêu.\r\n\r\n- Phi thơm hành tỏi, cho cơm vào xào săn, nêm gia vị gồm 1 ít đường, hạt nêm và muối, tiếp tục cho tôm khô, tôm tươi, thơm và hành tây vào đảo nhanh tay. Cuối cùng, tắt lửa và cho hành lá vào trộn đều.\r\n\r\n- Múc cơm vào trái thơm, trang trí ngò rí và ớt tỉa hoa lên trên. Ăn kèm xà lách, dưa leo, nước tương và ớt cắt lát.\r\n', '- Cách chọn tôm khô: màu đỏ tự nhiên, thịt trong, đều con, vị nhạt muối, ngọt thịt.\r\n- Rửa tôm khô bằng nước ấm, để ráo, rang sơ với hành tím băm để khử mùi khai trong tôm khô.\r\n- Khi nấu cơm để chiên, cho 1 ít dầu hoặc nấu cơm bằng nước dùng thì cơm chiên sẽ bóng, rời hạt và thơm béo.', 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(31).jpg'),
(32, 'Cơm Hoàng bào', '- Chả quế: 100g\r\n- Cà rốt: 100g\r\n- Xá xíu: 60g\r\n- Tôm khô: 30g\r\n- Gạo ngon: 1 chén\r\n- Trứng gà: 3 quả\r\n- Trứng muối: 3 quả\r\n- Hạt sen, đậu Hà Lan, tỏi băm\r\n- Hành lá, ngò rí, ớt sừng\r\n- Tiêu, dầu ăn, mỡ gà\r\n- Nước tương, hạt nêm\r\n', '- Hành lá băm nhỏ, ớt cắt lát. Tôm khô ngâm nước, xào sơ với gia vị cho thấm.\r\n\r\n- Chả quế, xá xíu, cà rốt cắt hạt lựu. Trứng muối luộc chín, lấy lòng đỏ tán nhuyễn. Luộc chín đậu Hà Lan, cà rốt và hạt sen.\r\n\r\n- Gạo vo sạch, để ráo. Phi thơm 1M tỏi băm với 1M mỡ gà, rang gạo cho thơm, nêm 1m hạt nêm, 1M nước tương, cho nước dùng vào nấu chín sơ, xới cơm cho tơi, để nguội rồi trộn với lòng đỏ trứng muối.\r\n\r\n- Trứng gà đánh tan, chiên trứng thành miếng tròn, mỏng đường kính 25-30cm.\r\n\r\n- Phi thơm tỏi với mỡ gà, xào thơm tôm, chả, đậu Hà Lan, xá xíu và cà rốt. Sau đó cho cơm vào đảo đều, chiên cho hạt cơm săn lại, thêm hạt sen, nêm 1m hạt nêm và 1m nước tương thêm hành lá, 1 ít tiêu rồi tắt bếp.\r\n\r\n- Múc cơm vào miếng trứng đã tráng, túm vào, cột lại, dùng kéo cắt và lật các cạnh ra ngoài như cánh hoa, dấu mối cột ở dưới.\r\n', '- Rang gạo với mỡ gà khi nấu cơm sẽ thơm và ngon hơn.\r\n- Dùng lòng đỏ trứng gà muối tán đều vào cơm giúp cơm bùi và không bị dính.', 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(32).jpg'),
(33, 'Cơm trái dừa', '- Tôm sú: 3 con\r\n- Chả lụa: 50g\r\n- Cần tây: 1 cọng\r\n- Cà rốt: 1/2 củ\r\n- Hành tây: 1/4 củ\r\n- Cơm: 1 chén\r\n- Dừa : 1 trái\r\n- Tỏi, hành tím băm: mỗi thứ 1/4 muỗng cà phê\r\n- Gia vị: muối, tiêu, đường, bột nêm.\r\n', '- Tôm luộc chín, lột vỏ cắt hạt lựu.\r\n\r\n- Chả lụa cắt hạt lựu. Cần tây, cà rốt, hành tây cắt hạt lựu.\r\n\r\n- Dùng nước dừa tươi trụng chín cà rốt, cần tây, hành tây.\r\n\r\n- Phi thơm hành tỏi, cho cơm vào xào, cho chả lụa, tôm vào xào chung.\r\n\r\n- Thấy cơm săn mặt cho rau củ vào trộn đều. Nêm gia vị vừa ăn. Múc hỗn hợp cơm cho vào trái dừa.\r\n\r\n- Trước khi ăn hấp nóng nguyên trái dừa. Cơm trái dừa ăn với nước tương ớt.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(33).jpg'),
(34, 'Cơm mimosa hải sản', '- Gạo ngon 2 chén\r\n- Nước dừa tươi 1,5 chén\r\n- Thịt cua 80g\r\n- Chem chép 200g\r\n- Mực ống cắt khúc 100g\r\n- Tôm sú 100g\r\n- Rượu vang trắng 1 muỗng súp.\r\n- Gia vị: nước nghệ tươi, mỡ gà, dầu, hạt nêm, tỏi băm, ngò rí.\r\n', '- Nấu cơm bằng nước dừa, 2 muỗng súp mỡ gà thắng, 1 muỗng súp nước nghệ tươi cho cơm vàng bóng, thơm. \r\n\r\n- Hải sản ướp hạt nêm, tỏi băm và rượu vang trắng. \r\n\r\n- Phi dầu xào qua hải sản vừa chín. Phi dầu, tỏi băm, đầu hành băm, cho cơm vào đảo săn, nêm hạt nêm vừa ăn. Bày cơm ra dĩa cùng với hải sản. Ăn với nước tương.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(34).jpg'),
(35, 'Cơm sen cốm', '- Hạt sen tươi: 50g\r\n- Đậu bi: 30g\r\n-Cà rốt: 30g\r\n- Thit nguội chay: 30g\r\n- Cốm xanh: 30g\r\n- Hành boaro: 30g\r\n- Cơm nguội: 2 chén\r\n- Nấm hương khô: 3 tai\r\n- Hoa sen: 2 cái\r\n- Muối, tiêu, đường, bột ngọt\r\n- Dầu ăn, ngò rí, ớt, giấm gao lên men, nước tương\r\n', '- Hòa 1M nước nóng với 1/2m Bột ngọt, 1/2m đường và 1/5m muối cho tan rồi trộn đều với cơm.\r\n\r\n- Hạt sen, đậu bi, cà rốt cắt hạt lựu luộc chín. Nấm hương ngâm nở, cắt hạt lựu. Thịt nguội chay cắt hạt lựu. Cốm chiên phồng. Hành boaro băm nhỏ. Ngò rí rửa sạch, cắt nhỏ. Ớt cắt lát.\r\n\r\n- Phi thơm hành boaro với 2M dầu ăn, múc bớt 1M dầu ra rồi cho cà rốt, đậu bi, nấm hương, hạt sen và thịt nguội vào xào, nêm 1/2m Bột ngọt và 1/3m muối, trộn đều, trút ra dĩa.\r\n\r\n- Cho 1M dầu phi hành boaro vào chảo, cho cơm vào xào săn với lửa nhỏ, nêm 1/2M Nước tương, cho rau củ đã xào vào trộn đều, tắt bếp.\r\n\r\n- Pha nước tương ăn kèm: trộn đều 1M Nước tương với 1/3m Giấm gạo lên men, 1/3m đường và ớt cắt lát.\r\n\r\n- Múc cơm chiên ra dĩa, rải cốm lên mặt, trang trí với cánh hoa sen xung quanh dĩa, dùng kèm nước tương pha.\r\n', '- Trộn cơm trước với gia vị đã hòa tan trong nước giúp cơm khi chiên sẽ mềm và gia vị thấm đều hơn.\r\n- Nấu cơm với nước dừa hoặc nước dùng giúp cơm ngọt và ngon hơn.', 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(35).jpg'),
(36, 'Gỏi cuốn bún thịt mắm tôm', '- Thịt ba chỉ\r\n- Mắm tôm chua, đường, tỏi, ớt quả, chanh\r\n- Rau ăn kèm: xà lách xoăn, dưa leo, bạc hà, rau thơm\r\n- Bún\r\n- Bánh đa nem hay bánh tráng dùng để làm gỏi cuốn \r\n', '- Thịt ba chỉ rửa sạch, cho vào nồi đổ nước lạnh ngập mặt thịt, đun sôi, khi đun bạn cho vào một ít muối để miếng thịt đậm đà hơn.\r\n\r\n- Tùy theo miếng thịt lớn hay bé mà bạn đun đến khoảng 10-15 phút, thì tắt bếp, dùng nắp nồi đậy kín để miếng thịt tiếp tục chín.\r\n\r\n- Giã tỏi, ớt, đường, cho vào bát tôm chua, dùng thìa trộn đều, nêm nếm lại gia vị cho vừa ăn. Khi dùng thì bạn vắt vào bát tôm chua một vài giọt chanh.\r\n\r\n- Xà lách xoăn, bạc hà, rau thơm rửa sạch, để ráo.\r\n\r\n - Dưa leo gọt sơ vỏ, bổ đôi, bỏ hạt, thái lát mỏng.\r\n\r\n- Vớt thịt ra cho vào âu nước lạnh để phần thịt được trắng, phần bì được trong và dai, để khoảng 3 phút.\r\n\r\n - Khi miếng thịt ráo, dùng dao thái lát mỏng vừa ăn.\r\n\r\n- Bánh tráng nhúng qua nước ấm, để lên vỉ cho ráo nước.\r\n\r\n - Xếp 1-2 vài nhánh nhỏ rau xà lách xoăn, bên trên thêm bún, rau thơm, dưa leo, lên bề mặt.\r\n\r\n- Dùng tay xếp hai mép và cuộn tròn lại.\r\n\r\n- Dùng dao bén cắt thành những khoanh tròn, xếp ra đĩa, bên trên đặt một lát thịt và gắp một ít tôm chua đặt lên bề mặt.\r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(36).jpg'),
(37, 'Nem hải sản', '- 200g mực chần sơ, thái nhỏ\r\n- 200gr tôm\r\n- ½ củ cà rốt thái lựu\r\n- ½ củ hành tây thái lựu\r\n- Hành lá thái nhỏ\r\n- 2 muỗng Mayonaise\r\n- Bánh tráng \r\n- Dầu ăn, gia vị nêm nếm\r\n- Bột chiên xù, 1 quả trứng gà\r\n- Mayonaise, tương ớt\r\n', '- Tôm lột vỏ, vỏ đầu, thái nhỏ.\r\n\r\n- Làm nóng chảo với cho một ít dầu ăn. Cho hành lá, hành tây xào thơm rồi cho tiếp tôm, mực, cà rốt. Nêm gia vị vừa ăn (tiêu, muối, hạt nêm, đường).Chờ nhân vừa chín (tôm đã chuyển sang màu hồng) thì cho ra thố để nguội. Sau đó trộn cùng 2 muỗng mayonaise.\r\n\r\n- Cuốn nem: trải bánh tráng ra, đặt nhân lên cuộn lại. Có thể làm dính phần mép cuốn bằng một ít trứng (hay nước).\r\n\r\n-  Trứng gà đập ra bát, đánh tan. \r\n\r\n- Lăn cuốn nem qua trứng gà và bột chiên xù, để sang một bên.\r\n\r\n- Đun sôi dầu ăn, lượng dầu sao cho vừa ngập cuốn nem. Cho nem vào chiên vàng 2 mặt, vớt ra để cho ráo dầu.\r\n', '- Trộn Mayonaise và tương ớt hoà vào nhau, sau đó bạn có thể cho vào túi bắt bông kem để trang trí đĩa nem.', 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(37).jpg'),
(38, 'Tàu hũ ky cuốn thịt chiên', '- Tàu hủ ky lớn : 4 lá\r\n- Thịt nạc dăm xay : 200g\r\n- Bột năng : 50g\r\n- Cà rốt cắt sợi : 1/3 củ\r\n- Nấm mèo ngâm nở cắt sợi : 2 tai\r\n- Củ năng cắt nhuyễn : 50g\r\n- Tiêu, đường, tương ớt, dầu ăn\r\n- Hạt nêm , bột ngọt, bột chiên gà giòn\r\n', '- Thịt xay ướp với 1/2m hạt nêm , 1/3m tiêu, 1/2m đường, 1m hành tím băm và 1/3m bột ngọt trộn đều.\r\n\r\n- Cho thịt, củ năng, nấm mèo, cà rốt và 1m bột năng vào trộn đều, vo thành viên tròn ấn dẹp.\r\n\r\n- Dùng lá tàu hủ ky gói viên thịt lại thành hình vuông.\r\n\r\n- Lăn gói tàu hũ ky qua bột chiên gà giòn, chiên vàng giòn rồi vớt ra để ráo dầu.\r\n\r\n- Dùng nóng với tương ớt.\r\n', '- Trộn nhân với bột năng giúp nhân khô ráo và định hình tốt\r\n- Củ năng cần cắt nhuyễn để kết dính tốt với các nguyên liệu khác.', 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(38).jpg'),
(39, 'Gà cuốn xôi xoài', '- Đùi gà: 2 cái\r\n- Pate gan: 2M\r\n- Bó xôi to: 4 lá\r\n- Cà rốt bào mỏng: 8 lát\r\n- Xôi trắng: 1 chén\r\n- Dừa xiêm: 1 trái\r\n- Xoài cắt vuông 8mm: ½ chén\r\n- Hành tím băm: 1M\r\n- Muối, tiêu, ngũ vị hương, dầu ăn\r\n- Hạt nêm , nước tương, xốt Mayonnaise\r\n', '- Đùi gà rút xương, chừa phần đầu đùi tỏi, lạng bớt thịt bên trong đem băm nhuyễn, khứa ngang sớ, đập mỏng, ướp 1m hạt nêm, 1/2M hành tím, 1/4m ngũ vị hương, 1m nước tương và 1/4m tiêu, để thấm.\r\n\r\n- Trộn đều xôi, xoài với 1M xốt mayonnaise. Trộn thịt gà băm với 2M pate gan và ít muối.\r\n\r\n- Bó xôi, cà rốt chần sơ, để ráo. Dừa chặt lấy nước.\r\n\r\n- Cuộn tròn đùi gà theo thứ tự từ ngoài vào trong gồm: thịt gà, nhân pate, cà rốt, bó xôi và xôi xoài, dùng dây buộc lại, đem hấp khoảng 15 phút.\r\n\r\n- Phi thơm hành tím, cho nước dừa vào thắng vàng và sánh lại, cho cuộn gà vào quay chảo đến khi thịt gà chin, vỏ ngoài có màu cánh gián thì tắt bếp.\r\n\r\n- Cuộn gà cắt khoanh, xếp ra dĩa, thêm nước quay lên trên, dùng nóng.\r\n', '- Khứa ngang sớ để khi quay xong cuộn gà được đẹp.\r\n- Bọc lá bó xôi để xôi không bị thấm nước khi quay chảo, khi dùng sẽ giúp món ăn ngon hơn..', 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(39).jpg'),
(40, 'Cuốn đậu hũ kho gừng', '- Đậu hủ trắng : 2 miếng\r\n- Cà rốt cắt que : 8 que\r\n- Khoai lang vàng cắt que : 8 que\r\n- Ơt sừng cắt sợi : 1/2 trái\r\n- Ớt hiểm : 3 trái\r\n- Đậu cove : 8 trái\r\n- Nước dừa : 1/2 chén\r\n- Gừng non : 50g\r\n- Bông hẹ chần : 16 cọng\r\n- Poa-rô băm\r\n- Tiêu, đường, muối, dầu ăn, dầu điều\r\n- Bột ngọt, hạt nêm, nước tương\r\n', '- Đậu hũ cắt ngang bề dày thành 4 miếng, đem chiên hơi vàng rồi nhúng qua nước, để ráo.\r\n\r\n- Gừng non cắt quả trám, phần dư băm lấy 1m. Ớt sừng cắt sợi to.\r\n\r\n- Ướp đậu với hỗn hợp gồm: 1m poa-rô, 1/3m muối, 1/2m hạt nêm, 1/3m bột ngọt, 1/2M nước tương, 1/3m tiêu và 1m gừng băm.\r\n\r\n- Pha hỗn hợp gia vị kho: 2M đường, 1/3m tiêu, 1m hạt nêm, 1M dầu điều, 1/2 chén nước tương và 1/2 chén nước dừa.\r\n\r\n - Xếp cà rốt, khoai lang, đậu cove, ớt sợi lên miếng đậu, cuốn tròn lại, dùng bông hẹ chần cột lại. Đem chiên sơ để định hình.\r\n\r\n- Phi thơm gừng và poa-rô với một ít dầu, cho đậu vào đảo nhẹ tay. Cho tiếp hỗn hợp gia vị kho và 3 trái ớt hiểm vào, đun sôi, giảm lửa, kho đến khi nước kho sánh lại, đậu thấm gia vị thì tắt lửa, thêm tiêu, ngò rí.\r\n\r\n- Cho cuốn đậu ra đĩa, dùng nóng với cơm.\r\n', '- Chiên đậu vừa vàng sau đó nhúng vào nước để đậu dai, dễ cuốn.\r\n- Dùng gừng non kho giúp món ăn có vị cay nhẹ, thơm, không đắng, ít xơ và ăn được cả phần gừng.', 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(40).jpg'),
(41, 'Gà hấp cải bẹ xanh', '- Gà ta: 1/2 con\r\n- Cải bẹ xanh: 15 cọng\r\n- Ớt sừng: 1 trái\r\n- Ngò rí: 3 cây\r\n- Muối, tiêu, đường, bột năng\r\n- Dầu mè, dầu hào, thuốc muối\r\n- Nước tương, hạt nêm\r\n', '- Gà làm sạch, xẻ vài đường trên thịt gà cho dễ thấm gia vị, ướp với 2m hạt nêm 1/2m muối và 1/2m tiêu, để thấm 30 phút.\r\n\r\n- Cải xanh cắt bỏ bớt phần lá, lấy phần cọng, rửa sạch. Đun sôi nước, cho vào 1 ít thuốc muối, luộc cọng cải xanh khoảng 3 phút, vớt ra ngâm vào nước đá, để ráo.\r\n\r\n- Hấp gà vừa chín tới, lấy ra xé thành miếng lớn cỡ 6*2cm, ướp với 1/2 hạt nêm 1/3m muối và 1/3m tiêu. Dùng 1 tô cỡ vừa xếp thịt gà và cải xen kẽ nhau, ém chặt, đem hấp lại 5 phút.\r\n\r\n- Chắt nước hấp trong gà ra, cho vào chảo, thêm vào 2/3 chén nước dùng nữa, nêm 1/2M đường, 1/2M dầu hào, 1/2M hạt nêm 1/2M nước tương đun sôi rồi cho nước bột năng vào từ từ cho sánh lại, thêm 1M dầu mè, tắt bếp.\r\n\r\n- Úp gà ra dĩa, rưới nước xốt lên trên, trang trí với ngò rí và bông hoa ớt, chấm kèm nước tương và ớt cắt lát tùy khẩu vị.\r\n', '- Có thể dùng muối ăn thay thuốc muối khi trụng để cải có màu xanh.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(41).jpg'),
(42, 'Trứng hấp tôm nấm', '- Trứng gà : 3 quả\r\n- Tôm sú lột vỏ : 50g\r\n- Nấm rơm : 50g\r\n- Cà rốt : 1/2 củ\r\n- Hành lá : 2 nhánh\r\n- Nước dùng gà : 1M\r\n- Hành tím băm\r\n- Tiêu, đường, dầu mè\r\n- Hạt nêm, nước tương, bột ngọt\r\n', '- Tôm cắt hạt lựu, ướp 1/3m hạt nêm, 1m hành tím băm, 1/4m tiêu, 1/4m đường, 1/3m bột ngọt.\r\n\r\n- Cà rốt bào sợi nhuyễn, cắt nhỏ. Hành lá cắt nhuyễn. Củ hành tím băm nhỏ. Nấm rơm cắt bỏ gốc, rửa sạch, ngâm nước muối loãng 10 phút, cắt lát mỏng.\r\n\r\n- Trứng gà đánh tan, lược sơ, nêm 1M nước dùng gà, 1m nước tương, 1/3m bột ngọt.\r\n\r\n- Phi thơm hành tím, xào tôm, cà rốt và nấm, thêm hành lá và 1/2m dầu mè trộn đều.\r\n\r\n- Đặt xửng hấp lên bếp, cho trứng và hỗn hợp xào vào chén hấp chín.\r\n\r\n- Dùng nóng với cơm trắng.\r\n', '- Khi hấp trứng để lửa đều, ổn định, thỉnh thoảng mở nắp để xả hơi giúp trứng mịn và không bị xốp.\r\n- Cho dầu mè sau cùng để giữ hương vị cho món ăn.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(42).jpg');
INSERT INTO `eating` (`id`, `name`, `material`, `making`, `tips`, `idType`, `bookmark`, `image`) VALUES
(43, 'Cá diêu hồng hấp củ sen', '- Cá diêu hồng: 1 con\r\n- Củ sen: 1 củ\r\n- Bông cải xanh: 100g\r\n- Kỉ tử: 10 hạt\r\n- Hành lá cắt khúc: 2 cọng\r\n- Gừng cắt sợi: 5 lát\r\n- Cần tàu: 2 cọng\r\n- Ớt sừng: 2 trái\r\n- Muối, tiêu, rượu trắng, dầu ăn\r\n- Nước tương, bột ngọt\r\n', '- Cá diêu hồng sơ chế sạch, khứa xéo 2 đường 2 bên mình cá, ướp 2m rượu, 1m bột ngọt, 2m muối, 1/2m tiêu, nhồi hành lá cắt khúc và 1/2 lượng gừng vào bụng cá, để thấm.\r\n\r\n- Củ sen gọt vỏ, cắt khoanh mỏng 3mm. Cần tàu cắt ngắn. Bông cải xanh cắt miếng vừa ăn. Kỉ tử ngâm mềm. Ơt cắt sợi, 1 ít cắt lát.\r\n\r\n- Xếp cá ra dĩa sứ, cho bông cải và củ sen xung quanh, đem hấp khoảng 20 phút, rắc kỉ tử, ớt, cần tàu và phần gừng cắt sợi còn lại lên trên, đem hấp tiếp khoảng 10 phút, lấy cá ra, dội 2M dầu đun nóng lên mình cá.\r\n\r\n- Xếp cá ra dĩa, dùng nóng với cơm, chấm nước tương và ớt cắt lát.\r\n', '- Rượu, tiêu và hành gừng khi ướp cá sẽ giúp cá không bị tanh và giữ được mùi vị tự nhiên.\r\n- Các loại rau gia vị như ớt, gừng, cần cho vào sau để chín cùng với cá nhưng vẫn giữ được hương vị màu sắc tự nhiên.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(43).jpg'),
(44, 'Chim cút nhồi cốm hấp', '- Chim cút: 2 con\r\n- Cốm dẹp: 20g\r\n- Giò sống: 150g\r\n- Hạt sen luộc chín: 50g\r\n- Nấm hương luộc chín: 5 tai\r\n- Cà rốt: 50g\r\n- Nước dùng: 1 chén\r\n- Hành tím băm, hành lá, ngò rí\r\n- Tiêu, hắc xì dầu, dầu ăn, hạt nêm \r\n', '- Chim cút mổ moi, làm sạch, ướp 1/2m hạt nêm, 1/2m tiêu, để thấm.\r\n\r\n- Cà rốt tỉa hoa cắt lát 5mm. Đầu hành lá cắt khúc.\r\n\r\n- Trộn đều giò sống, 2M cốm, 1m hành tím băm và 1/2m tiêu, sau đó nhồi vào bụng chim cút, cài tăm lại. Bôi da chim cút bằng hắc xì dầu.\r\n\r\n- Đun nóng dầu ăn, chiên sơ cho da cút hơi vàng, vớt ra để ráo.\r\n\r\n- Đặt chim cút vào dĩa sâu lòng, xếp nấm, cà rốt, hạt sen, đầu hành lá và thêm nước dùng, đem hấp chín khoảng 20 phút.\r\n\r\n- Trang trí món ăn với ngò rí và tiêu, dùng nóng.\r\n', '- Cốm khi quết chung với giò sống sẽ giúp nhân có màu đẹp và kết dính tốt hơn.\r\n- Hắc xì dầu giúp da chim cút có màu nâu đẹp và tạo mùi thơm cho món ăn.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(44).jpg'),
(45, 'Vịt hấp lá sen', '- 500g thịt vịt\r\n- 3 hoa sen\r\n- 2 lá sen\r\n- 100g hạt sen\r\n- 1/4 củ gừng (20g)\r\n- 30ml rượu trắng\r\n- 1 thìa cà-phê hành tỏi băm\r\n- 50ml nước dùng\r\n- 1/4 thìa cà-phê ngũ vị hương\r\n- 2 thìa cà-phê hạt nêm\r\n- 1 thìa cà-phê đường\r\n- 1/4 thìa cà-phê muối\r\n- 1 thìa cà-phê bột năng\r\n- 1,5 thìa súp dầu hào.\r\n', '- Dùng rượu và gừng giã nhuyễn để khử mùi thịt vịt, ướp ngũ vị hương, hành tỏi băm, hạt nêm, đường muối.\r\n\r\n- Hoa sen tách rời từng cánh, rửa sạch, để riêng.\r\n\r\n- Hạt sen tách làm đôi, bỏ tim, hấp chín.\r\n\r\n- Lá sen rửa sạch. Trải lá sen ra, cho vào một lớp cánh hoa sen lên, để miếng thịt vịt vào giữa, phía trên phủ một lớp cánh hoa sen nữa, dùng lá sen bọc kín lại, sau đó bọc thêm một lá sen bên ngoài, cho vào dĩa, hấp khoảng một giờ cho thịt vịt chín đều.\r\n\r\n- Đun sôi nước dùng, cho hạt sen, dầu hào vào, sau đó cho bột năng hòa nước vào để tạo độ sệt.\r\n\r\n- Vịt chặt miếng vừa ăn, xếp vào đĩa, rưới sốt dầu hào và hạt sen lên.\r\n', NULL, 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(45).jpg'),
(46, 'Thịt heo quay kho củ hũ dừa', '- Thịt heo quay : 600g\r\n- Củ hũ dừa : 300g\r\n- Nước dừa tươi : 1 chén\r\n- Ớt sừng : 2 trái\r\n- Ớt hiểm : 2 trái\r\n- Hành tím băm, hành lá\r\n- Muối, tiêu, đường, nước mắm, màu điều\r\n- Nước tương, bột ngọt\r\n', '- Cắt thịt quay và củ hũ dừa thành khối vuông cạnh 2cm. Ớt sừng cắt khúc. Ớt hiểm đập dập. Hành lá cắt khúc.\r\n\r\n- Ướp thịt với 1M hành tím băm, 1/3m tiêu, ớt hiểm, 1M dầu điều, 2M nước mắm, 2M nước tương, 1m đường, 1/2m muối và 1m bột ngọt.\r\n\r\n- Phi thơm hành tím băm, cho thịt vào đảo cho thơm, cho củ hũ dừa, ớt sừng vào đảo đều, cho nước dừa săm sấp mặt. Kho với lửa riu riu cho thịt và củ hũ dừa thấm gia vị. Nước kho sánh lại. Thêm hành lá, tiêu, tắt lửa.\r\n', '- Chọn thịt heo quay có phần da và mỡ kết dính chặt chẽ, kho sẽ ngon và đẹp.\r\n- Chọn củ hũ dừa chắc, màu trắng, tươi mới.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(46).jpg'),
(47, 'Thịt gà kho kim chi', '- Đùi gà góc tư : 300g\r\n- Kim chi cải thảo Hàn quốc : 250g\r\n- Củ cải trắng : 150g\r\n- Hẹ lá : 5 cây\r\n- Gừng cắt sợi : 2M\r\n- Hành tỏi băm : 2M\r\n- Tiêu, đường, dầu ăn, hạt nêm\r\n', '- Kim chi tách riêng phần nước, để riêng, phần lá cắt khúc dài 3cm. Củ cải trắng tỉa hoa, cắt lát dày 3mm. Hẹ lá cắt khúc 3cm.\r\n\r\n- Thịt gà chặt miếng vừa ăn, ướp 2M nước kim chi, 1M hành tỏi băm, 1M gừng, 1/2m tiêu, 1m đường và 2m hạt nêm, để thấm.\r\n\r\n- Phi thơm hành tỏi gừng, xào thịt gà thật săn, cho tiếp củ cải trắng và nước kim chi vào kho lửa vừa cho thịt gà và củ cải chín, thêm cải thảo, nêm lại gia vị vừa ăn, kho đến khi kim chi cải thảo vừa chín, thêm hẹ lá vào trộn đều, tắt lửa.\r\n \r\n', '- Ướp thịt gà với nước kim chi có độ chua, giúp sau khi kho, da thịt gà vẫn còn giữ độ giòn nhẹ.\r\n- Cho kim chi cải thảo vào sau cùng để giữ độ giòn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(47).jpg'),
(48, 'Cá thu kho cà tím', '- Cá thu : 200g\r\n- Thịt ba rọi : 100g\r\n- Cà tím : 2 trái\r\n- Hành lá : 6 nhánh\r\n- Ớt sừng cắt quả trám : 1 trái\r\n- Hành tím băm : 3M\r\n- Sa tế : 2m\r\n- Chanh : 1 trái\r\n- Dầu ăn, nước mắm, tiêu, đường\r\n- Hạt nêm, giấm gạo lên men, bột ngọt \r\n', '- Cá thu ướp 1M hành tím băm, 3 cọng đầu hành đập dập, 1m bột ngọt, 1/3m tiêu, 2m sa tế, 3M nước mắm.\r\n\r\n- Thịt ba rọi cắt lát mỏng, ướp 1/2m hạt nêm, 1/3m tiêu, 1m hành tím băm, 1m sa tế và 1/2m đường, để thấm.\r\n\r\n- Cà tím cắt khúc, ngâm nước có vắt 1 miếng chanh. Hành lá cắt khúc.\r\n\r\n- Đặt chảo lên bếp, cho 3M dầu và 1M đường vào làm caramel, tiếp tục phi thơm hành tím, xào săn thịt ba rọi, cho cá thu vào xốc đều, thêm 1 chén nước sôi và 1M giấm gạo lên men rồi cho cà tím vào kho với lửa nhỏ đến khi nước cạn hơi sệt, cho ớt cắt quả trám và hành lá vào, tắt lửa.\r\n', '- Cắt cà tím thành khúc lớn để cà không bị nát trong quá trình kho.\r\n- Cho giấm vào món kho để khử mùi tanh của cá và tăng hương vị cho món ăn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(48).jpg'),
(49, 'Sườn non kho khế', '- Sườn non: 600g\r\n- Khế chua: 3 trái\r\n- Nước dừa xiêm: 1 chén\r\n- Ớt hiểm: 2 trái\r\n- Ớt sừng: 1 trái\r\n- Sả băm, ớt băm, hành lá\r\n- Đường, tiêu, dầu ăn, nước mắm, dầu điều, muối, hạt nêm\r\n\r\n', '- Sườn cắt miếng 3-4 cm. Ướp sườn với 1/2m muối, 1M nước mắm, 1M dầu điều, 1M hạt nêm.\r\n\r\n- Khế cắt khoanh dày 3 li. Hành lá cắt khúc 2cm. Ớt sừng cắt hình thoi. Ớt hiểm đập dập.\r\n\r\n- Đun nóng chảo cho 1M dầu và 2M đường vào thắng caramen sau đó cho tỏi, sả, ớt băm vào phi thơm. Cho sườn vào đảo đều cho sườn săn, thêm nước dừa, xiêm, đậy nắp, kho lửa nhỏ cho sườn chín mềm.\r\n\r\n- Cho khế chua, ớt hiểm vào trộn đều. Kho thêm 5 phút nữa với lửa nhỏ cho sườn thấm vị, khế chín mềm, nước kho sanh sánh. Rắc hành lá cắt khúc, ớt sừng và tiêu lên, tắt bếp.\r\n\r\n', '- Chọn sườn heo ít xương, dày thịt, ít mỡ, có sụn.\r\n- Thắng caramen trước khi cho gia vị tỏi, sả, ớt vào giúp gia vị thơm mà không bị cháy.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(49).jpg'),
(50, 'Nghêu kho tộ', '- Nghêu sống, còn vỏ: 2kg\r\n- Ớt hiểm: 2 trái\r\n- Hành tỏi băm\r\n- Hành lá\r\n- Tiêu, đường, dầu điều\r\n- Hạt nêm, nước tương\r\n', '- Nghêu rửa sạch, luộc sơ, lấy thịt ướp với 4M nước tương , 1/2m hạt nêm, 1m tiêu, 1M hành tỏi băm, 1M đường để thấm.\r\n\r\n- Hành lá rửa sạch, cắt khúc 4cm. Ớt hiểm đập dập.\r\n\r\n- Phi thơm hành tỏi băm, cho nghêu và ớt vào đảo đều, kho nhỏ lửa đến khi nước kho sền sệt, thêm hành lá và tiêu vào, tắt lửa.\r\n', '- Không nên kho nghêu quá lâu nghêu sẽ bị mất nước và dai.\r\n- Luộc nghêu cho tới khi nghêu vừa mở vỏ thì vớt ra gỡ lấy thịt ngay để thịt nghêu được ngon hơn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(50).jpg'),
(51, 'Sườn nướng tiêu', '- Sườn non : 800g\r\n- Tiêu xanh đập dập : 20g\r\n- Tiêu đen bể : 20g\r\n- Tiêu trắng bể : 20g\r\n- Dừa tươi : 1 trái\r\n- Mù tạt vàng\r\n- Tỏi băm, hành tím băm\r\n- Dầu ăn, dầu điều, dầu hào, nước mắm, mật ong\r\n- Hạt nêm, nước tương , xốt mayonnaise\r\n', '- Sườn non cắt thanh dài, cho vào nồi áp suất cùng với nước dừa, 1M tiêu đen bể, 1m hạt nêm. Đậy nắp nấu trong 10 phút rồi tắt lửa, cho sườn ra dĩa. Giữ lại nước hấp sườn.\r\n\r\n- Nấu xốt ướp: Phi thơm tỏi băm và hành tím, cho tiêu xanh, tiêu trắng, tiêu đen vào xào thơm, nêm 2M dầu hào, 1/2M nước mắm, 2M nước tương, 3M mật ong và 1M dầu điều. Cho 1/2 nước hấp sườn vào, đun nhỏ lửa cho đến khi xốt sánh lại. Tắt lửa.\r\n\r\n- Ướp sườn với xốt ướp trong 20 phút cho thấm vị.\r\n\r\n- Xếp sườn lên vĩ, nướng 180°C trong 20 phút.\r\n\r\n- Làm xốt: trộn đều hỗn hợp gồm 3M xốt mayonnaise với 1M mù tạt vàng.\r\n\r\n- Cho sườn ra dĩa, trang trí thêm xà lách, ăn kèm với xốt đã pha.\r\n', '- Sự kết hợp giữa 3 loại tiêu tạo nên mùi thơm đặc trưng cho món nướng.\r\n- Nấu sườn với nước dừa trước khi nướng giúp sườn chín mềm và không bị khô.', 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(51).jpg'),
(52, 'Sò huyết nướng mỡ hành', '- 400gr sò huyết.\r\n- Hành lá, muối, nước mắm.\r\n- Dầu ăn hoặc mỡ nước.\r\n- Đậu phộng (lạc).\r\n', '- Sò huyết rửa sạch, ngâm vào thố nước lạnh có pha chút muối để sò ra hết cát rồi rửa lại lần nữa.\r\n\r\n- Đun nồi nước sôi, thả sò vào luộc sơ đến khi sò há miệng thì bạn tắt bếp, đợi sò nguội bớt thì cạy nửa phần vỏ sò bỏ đi. \r\n\r\n- Hành lá rửa sạch, thái nhỏ.\r\n\r\n- Đậu phộng nướng sơ ở nhiệt độ 180ºC khoảng 5 phút hoặc rang trên lửa nhỏ, xát bỏ vỏ lụa bên ngoài.\r\n\r\n- Cho đậu phộng vào cối, giã dập.\r\n\r\n-Cho hành lá vào bát, trộn với hai thìa cafe dầu ăn hoặc mỡ nước, đun trên bếp khoảng 1 phút hoặc để vào lò vi sóng quay khoảng 30 giây để hành chín sơ. Trộn hành với nửa thìa nhỏ muối, nửa thìa nhỏ nước mắm.\r\n\r\n- Xếp sò ra khay nướng, dùng thìa phết ít tương ớt lên bề mặt con sò.\r\n\r\n- Dùng thìa múc ít hỗn hợp mỡ hành rưới đều lên trên từng con sò. Để dành lại một ít mỡ hành, khi nướng xong bạn phết đều lên trên bề mặt sò cho bóng đẹp.\r\n\r\n- Sau đó, rắc một ít đậu phộng lên trên bề mặt sò.\r\n\r\n- Cho khay sò vào lò nướng ở nhiệt độ 190ºC khoảng 5 - 8 phút, không nướng lâu sò sẽ bị dai. \r\n\r\n- Sò huyết chín, lấy ra phết ít hỗn hợp mỡ hành còn dư lên bề mặt sò, dùng nóng với tương ớt.\r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(52).jpg'),
(53, 'Vịt nướng', '- Vịt - 1 con\r\n- Gia vị - tiêu, dầu ăn, đường, rượu trắng\r\n- Sa tế\r\n- Hành, tỏi, giềng, mẻ, xả.\r\n', '- Vịt sau khi làm sạch đem mổ phanh, để có thể xiên nướng, hoặc kẹp vỉ.\r\n\r\n- Sả, hành, tỏi bóc vỏ rửa sạch rồi băm nhỏ, trộn hỗn hợp này với dầu ăn, sa tế, gia vị, hạt tiêu.\r\n\r\n- Ướp vịt đã rửa sạch vào cùng hỗn hợp gia vị trên, để khoảng 20 phút cho thịt vịt ngấm đều các loại gia vị.\r\n\r\n- Sau đó kẹp vịt vào vỉ, nướng trên bếp than hoa 20 phút cho đến khi thịt vịt chín đều từ trong ra ngoài, da vịt có màu vàng cánh gián, không cháy là được. Thịt vịt nướng đạt chuẩn sẽ có màu vàng cánh gián rất hấp dẫn.\r\n\r\n- Món này thưởng thức khi còn nóng là ngon nhất, chấm cùng nước sốt chao, ăn kèm rau thơm, rau húng.\r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(53).jpg'),
(54, 'Ếch nướng giấy bạc', '- 500g đùi ếch\r\n- 3 cây sả\r\n- 10 lá chanh\r\n- 10g hành, tỏi khô\r\n- 2 thìa súp dầu hào\r\n- 2 thìa súp nước hàng\r\n- 1 thìa súp dầu màu điều\r\n- 1 ít rau ngổ\r\n- 1,5 thìa cà-phê bột g ia vị\r\n- 1 thìa cà-phê hạt nêm\r\n- 1/5 thìa cà-phê tiêu xay\r\n- Muối, dầu ăn.\r\n', '- Đùi ếch xát muối, rửa sạch, để ráo.\r\n\r\n- Hành tỏi kho bóc vỏ, rửa sạch, băm nhỏ.\r\n\r\n- Sả lấy phần thân, đập giập, băm nhỏ\r\n\r\n- Lá chanh rửa sạch, để ráo, thái chỉ theo chiều ngang.\r\n\r\n- Ướp đùi ếch với bột gia vị, tiêu xay, hạt nêm, sả, lá chanh, hành tỏi băm, dầu màu điều, 1 thìa cà-phê dầu ăn, nước hàng, dầu hảo để 15 phút.\r\n\r\n- Cho ếch vào giấy bạc bọc kín lại rồi để vào lò, nướng ở nhiệt độ 200độ C, thời gian 20 phút để ếch chín vàng đều.\r\n\r\n- Cho ếch ra đĩa, trang trí cùng rau ngổ. Bạn có thể chấm ếch với tương ớt hoặc muối tiêu chanh.\r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(54).jpg'),
(55, 'Cá nướng muối ớt', '- Cá tai tượng( 800g): 1 con\r\n- Ớt sừng: 2 trái\r\n- Ớt hiểm: 6 trái\r\n- Lá sen: 2 lá\r\n- Bánh tráng mè\r\n- Muối hột, ớt bột\r\n- Dưa leo, xà lách, cà chua\r\n- Dụng cụ: Giấy nhôm\r\n- Bột ngọt \r\n- Xốt Mayonnaise\r\n', '- Bánh tráng mè nướng vàng, sau đó giã nhuyễn thành thính. Ớt sừng, ớt hiểm giã nát.\r\n\r\n- Cá làm sạch, khứa 2 bên mình cá, ướp 2M thính, 1M muối hột, 1m ớt bột, ớt sừng và ớt hiểm giã, 1m bột ngọt, 2M xốt mayonnaise, để thấm.\r\n\r\n- Dưa leo cắt lát. Cà chua cắt lát. Lá sen lau sạch, để ráo.\r\n\r\n - Gói cá vào lá sen, bọc giấy nhôm bên ngoài, nướng chín trong lò nướng nhiệt độ 250 độ khoảng 20 phút, khi cá vừa chín, mở giấy bạc, tiếp tục nướng cá trực tiếp trên bếp gas để cá được ráo và thơm.\r\n\r\n - Dùng kèm xà lách, dưa leo và cà chua, chấm muối ớt chanh.\r\n', '- Chọn ớt hiểm sắp chín, mới chuyển màu sẽ cay và thơm hơn. \r\n- Thính bánh tráng mè giúp cá ráo và thơm hơn khi nướng.', 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(55).jpg'),
(56, 'Sườn xào chua ngọt', '- 600gr sườn lợn\r\n- 3 thìa nước mắm\r\n- 4 thìa đường\r\n- 4 thìa giấm\r\n- 1/2 bát nước\r\n- 1 chút bột nêm hoặc bột ngọt\r\n- 1 chút tiêu\r\n- 2 củ hành khô\r\n- 1 thìa tương cà\r\n- 2 nhánh hành lá\r\n', '- Sườn mua về chặt miếng vừa ăn, sau đó cho 1 chút muối vào bóp kỹ rồi rửa lại cho sạch sẽ, hành khô băm nhỏ.\r\n\r\n- Đun 1 nồi nước sôi sau đó cho sườn vào chần sơ qua, vớt ra để ráo nước. \r\n\r\n- Cho các gia vị: giấm, đường, nước mắm, bột nêm, tiêu, tương cà vào 1 cái bát con rồi khuấy đều cho tan đường.\r\n\r\n- Cho chảo lên bếp cùng với 2 thìa dầu ăn, đun nóng dầu mới xếp từng miếng sườn vào chảo, chiên cho sườn chín vàng thì gắp ra đĩa.\r\n\r\n- Vẫn sử dụng cái chảo ấy, cho hành băm nhỏ vào phi cho thơm sau đó cho bát nước sốt vào nấu cho sôi mới cho sườn vào. Đảo đều và thêm nước, đậy nắp vung xào cho sườn vừa chín mềm, nước sốt sệt lại là được, rắc chút hành lá lên trên là tắt bếp.\r\n', NULL, 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(56).jpg'),
(57, 'Lươn xào lăn', '- Thịt lươn: 200g\r\n- Dừa khô nạo: 400g\r\n- Hành tây: 1 củ\r\n- Đậu phộng rang: 50g\r\n- Bún tàu, nấm mèo\r\n- Ớt hiểm, ớt sừng, sả\r\n- Rau om, ngò gai\r\n- Hành tỏi băm\r\n- Muối, tiêu, đường,bột cà ri, hạt nêm\r\n- Dầu ăn, dầu điều\r\n- Giấm gạo lên men \r\n', '- Hòa tan 1m bột cà ri với 1M nước. 3 cây sả cắt khúc, đập dập, 1 cây băm nhỏ.\r\n\r\n- Nấm mèo ngâm nở, cắt làm 4.Hành tây cắt múi. Rau om cắt khúc. Ngò gai cắt nhỏ. Bún tàu ngâm mềm, cắt khúc dài 6cm.\r\n\r\n- Đậu phộng rang giã dập. Dừa nạo vắt lấy 1/2 chén nước cốt và 1,5 chén nước dão.\r\n\r\n- Lươn dùng giấm gạo lên men rửa cho sạch nhớt, bỏ đầu, ruột, cắt khúc dài 3cm. Ướp thịt lươn với 1m hạt nêm, 1m đường, 1m muối, 1m tiêu, 1m ớt sừng băm, 1m ớt hiểm băm, 1m sả băm và 1m cà ri hòa tan với nước, đảo đều, để thấm 20 phút.\r\n\r\n- Phi thơm hành tỏi cho lươn vào xào săn với lửa lớn, cho nước dão dừa vào, nêm 1/2M đường, 1/2m hạt nêm, 1/2m muối, và 1M dầu điều, đảo đều, đun với lửa nhỏ.Khi lươn gần chín thì cho hành tây và nấm mèo vào, thêm nước cốt dừa, đợi sôi lại cho bún tàu vào,đảo đều tắt bếp.\r\n\r\n- Múc lươn xào lăn ra dĩa sâu lòng, trang trí với 1 ít đậu phộng, rau om, ngò gai và ớt cắt lát lên mặt. Dùng nóng với bánh mì.\r\n', '- Nên chọn lươn béo, có thân ngắn, lưng nâu, bụng vàng sẫm là lươn ngon.\r\n- Bột cà ri hòa với ít nước ấm trước khi ướp sẽ có màu đẹp và mùi thơm hơn.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(57).jpg'),
(58, 'Chân nấm xào thịt gà', '- Chân nấm hương: 30g\r\n- Thịt đùi gà ta góc tư: 200g (lóc bỏ xương)\r\n- Măng le luộc: 200g\r\n- Mực khô: 1 con nhỏ\r\n- Ớt sừng: 1 trái\r\n- Hành tỏi băm: 3M\r\n- Tiêu, muối, hành ngò, mỡ nước\r\n- Xốt mayonnaise, nước tương, bột ngọt\r\n', '- Chân nấm hương ngâm nước ấm 2-3 giờ, luộc vừa chín, cắt bỏ phần cứng, già, rửa lại, xả 3-4 lần cho sạch. Cho vào cối giã dập, xé tơi.\r\n\r\n- Thịt gà lọc bỏ xương, cắt lát dày 2-3mm. Ướp với 1M hành tỏi băm, 1m bột ngọt, 1/2m muối, 1/2m tiêu.\r\n\r\n- Măng luộc chẻ mỏng như thịt gà. Mực khô nướng chín, xé sợi nhỏ.\r\n\r\n- Hành ngò cắt khúc. Ớt sừng cắt sợi.\r\n\r\n- Phi thơm hành tỏi với mỡ nước, cho thịt gà vào xào, thêm măng vào đảo đều đến khi gà vừa chín thì trút ra dĩa.\r\n\r\n- Tiếp tục xào chân nấm cho thơm, nêm 1/4m muối, 1/4m bột ngọt, thêm chút nước vào xào cho nấm mềm, thấm vị. Trút thịt gà và măng vào trở lại, đảo đều, tắt lửa. Thêm hành ngò, ớt sừng cắt sợi và rắc tiêu.\r\n\r\n- Cho món ăn ra dĩa, khi ăn rắc thêm mực khô xé sợi. Dùng nóng với cơm, chấm kèm nước tương. \r\n', '- Ngâm trước chân nấm hương sau đó luộc rồi giã dập trước khi xé sẽ giúp chân nấm mềm và ngon hơn khi xào.\r\n- Xào riêng chân nâm hương để chân nấm mềm, thơm và thấm gia vị hơn.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(58).jpg'),
(59, 'Mực xào xả ớt', '- Mực ống làm sạch: 500g\r\n- Sả non bào mỏng xéo: 5 cây\r\n- Hành lá: 3 cây\r\n- Ớt hiểm, tỏi băm\r\n- Muối, đường, dầu ăn, bột cà ri\r\n- Bột ngọt\r\n', '- Hành lá cắt khúc 2-3cm. Ớt hiểm đập dập.\r\n\r\n- Mực cắt khoanh ướp với 1/2m muối, 1/2m bột ngọt, 1m đường, 3 trái ớt hiểm đập dập, 1/2m bột cà ri, 1m tỏi băm.\r\n\r\n- Chiên sả bào cho vàng thơm với nhiều dầu, vớt ra. Tiếp tục dùng dầu vừa chiên sả, phi thơm phần tỏi băm còn lại, trút mực đã ướp vào xào chín với lửa to, mực gần chín cho hành lá vào xóc đều. Tắt lửa. Cho mực ra dĩa, rắc sả chiên lên trên.\r\n', '- Chọn mực ống tươi: mực xanh trong, còn da.\r\n- Chiên vàng sả bào với dầu giúp sả thơm và hấp dẫn hơn sử dụng sả băm.\r\n', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(59).jpg'),
(60, 'Cá lóc xào lưu ly', '- Phi lê cá lóc: 250g\r\n- Ớt chuông: 100g\r\n- Hành tây: 50g\r\n- Bột năng: 20g\r\n- Thì là, ngò rí: 3 nhánh\r\n- Hành tím băm, tỏi băm\r\n- Tiêu, đường, dầu mè, dầu ăn\r\n- Bột Chiên Gà Giòn\r\n- Giấm gạo lên men \r\n- Hạt nêm\r\n', '- Ớt chuông, hành tây cắt hạt lựu. Thì là cắt nhỏ.\r\n\r\n- Cá phi lê lạng bỏ xương cắt miếng vuông cạnh 3cm, để cá ráo, ướp 1m Giấm gạo lên men, 1/2m tiêu, tẩm qua Bột chiên Gà Giòn, chiên chín vàng, vớt ra để ráo dầu.\r\n\r\n- Pha bột năng với 1/2 chén nước.\r\n\r\n- Phi thơm hành tỏi băm với 2M dầu ăn, cho hành tây vào xào, cho tiếp cá vào xốc đều, sau đó cho ớt chuông, 1/2 chén nước vào, nêm 1.5m đường, 1m dầu mè, 1M giấm, 1/2m Hạt nêm , 2m nước bột năng, cuối cùng cho thì là vào, đảo cho gia vị thấm cá.\r\n\r\n- Cho ra đĩa, rắc thêm tiêu, trang trí với ngò.\r\n', '- Dùng giấm ướp cá giúp cá được săn và khử mùi tanh.\r\n- Ướp cá trước với bột chiên gà giòn giúp cá thơm ngon, vừa ăn, không bị nát.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(60).jpg'),
(61, 'Chè khúc bạch', '- 120ml sữa tươi không đường\r\n- 120ml kem sữa tươi\r\n- 40g đường cát trắng\r\n- 1 thìa nhỏ tinh dầu hạnh nhân\r\n- 20ml nước ấm\r\n- 7g bột gelatin (hoặc có thể thay bằng 3 lá gelatin).\r\n- 1 thìa canh hạnh nhân cắt lát.\r\n- 40g đường phèn\r\n- Cơm nhãn đã lấy hột, hoặc vải thiều\r\n', '- Bột gelatin cho ra bát.\r\n\r\n- Thêm vào 20ml nước ấm, hòa cho gelatin tan sơ.\r\n\r\n- Cho sữa tươi, kem tươi, đường cát trắng vào nồi, đun sôi lửa nhỏ đến khi tan hết đường.\r\n\r\n- Đổ từ từ bát bột galatin đã hòa tan với nước ấm ở bước 2 vào nồi sữa đun nóng, dùng muôi quấy nhẹ để gelatin tan hoàn toàn thì cho tinh dầu hạnh nhân vào.\r\n\r\n- Nhấc nồi ra khỏi bếp, đổ vào khuôn thủy tinh sạch, để nguội hẳn rồi cho vào tủ lạnh, để 4-5 tiếng đến khi đông cứng hẳn.\r\n\r\n- Đun đường phèn với một bát con nước lọc, nấu cho đường tan, để nguội.\r\n\r\n- Vải thiều tách lấy hạt, nếu dùng vải đóng hộp thì bạn giữ lại phần nước đường để dành rưới lên bề mặt chè.\r\n\r\n- Hạnh nhân lát nướng ở nhiệt độ 160 độ C khoảng 3-4 phút đến khi hạnh nhân chín, hoặc có thể rang trên bếp đến khi hạnh nhân chín vàng.\r\n\r\n- Phần thạch chè khúc bạch sau khi đông cứng, cho ra ra thớt sạch, cắt thành những miếng vuông vừa ăn.\r\n\r\n - Khi dùng cho một ít thạch trắng hay thạch trà xanh vào bát, thêm vải thiều, chan nước đường, rắc hạnh nhân lên bề mặt, dùng lạnh.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(61).jpg'),
(62, 'Chè vừng đen', '-300g vừng đen\r\n-500g đường\r\n-50g bột gạo\r\n-300g dừa gạo\r\n-1 lít nước\r\n', '-Vừng đen đãi sạch, để ráo, đem phơi khô. Sau đó rang chín vừng trong chảo nóng. Tiếp tục cho vào máy xay sinh tố cùng 1 lít nước , xay nhuyễn. Nếu không dùng máy sinh tố, có thể xay bằng cối xay\r\nDừa nạo cho vào nước, vắt lấy nước cốt. Cho bột gạo vào bát, rưới 0.5 bát nước cốt dừa lên, khuấy đều cho bột gạo tan\r\n\r\n-Bắc nồi vừng đen xay nhuyễn lên bếp, khuấy liền tay khoảng 3 phút, cho đường , nước cốt dừa vào khuấy thêm khoảng 3 phút. Đổ bột gạo vào nồi qua một lớp dây để lược bột, tiếp tục khuấy liền tay. Khi thấy chè đen đậm vừng đã chín, nhắc nồi chè xuống\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(62).jpg'),
(63, 'Chè đậu đỏ', '- Bí đỏ - 120g\r\n- Bột nếp - 80g\r\n- Đậu đỏ - 100g\r\n- Đường phèn.\r\n', '- Đậu đỏ ngâm khoảng 6 – 8 tiếng hoặc qua đêm cho nở mềm. Bí đỏ gọt vỏ, thái miếng vuông nhỏ.\r\n\r\n- Cho đậu đỏ vào nồi, đổ ngập nước và đun sôi. Khi nước sôi, hạ nhỏ lửa và đun trong 40 phút cho đậu chín mềm.\r\n\r\n- Thêm đường phèn và tiếp tục nấu trong 20 phút.\r\n\r\n- Khi đậu nở mềm, múc đậu và nước ra từng bát nhỏ cho nguội.\r\n\r\n- Bí đỏ luộc chín. Sau đó nghiền nhuyễn mịn.\r\n\r\n- Thêm bột gạo nếp...Rồi thêm đường và lượng nước tương ứng. Nhồi bột thành một hỗn hợp khô, dẻo và mịn.\r\n\r\n- Lấy 1/3 lượng bột, cho vào nồi nước sôi và nấu cho đến khi bột nổi lên thì vớt ra.\r\n\r\n- Trộn bột chín với phần bột còn lại, nhào thật mịn. Cách trộn này giúp bột mịn hơn so với cách nhào bột thông thường. Viên bột thành từng viên tròn nhỏ.\r\n\r\n- Đun sôi nồi nước khác, thả những viên bột bí vào luộc cho đến khi nổi lên thì vớt ra, thả viên bột vào bát nước đun sôi để nguội.\r\n\r\n- Sau đó múc từng viên bột bí, đổ lên mặt bát đậu đã múc ra từ trước.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(63).jpg'),
(64, 'Kem cà phê', '- 2 chén cà phê đậm đặc nóng\r\n- 1 hộp sữa đặc có đường\r\n- 2 chén sữa nguyên chất (2%)\r\n- 1 muỗng cà phê vani\r\n- 1 chén whipping cream\r\n- 1 muỗng canh đường cát\r\n- 1/2 chén dừa khô.\r\n', '- Trong 1 bát lớn có khả năng chịu nhiệt tốt, khuấy cà phê nóng với sữa đặc, sữa nguyên chất, và vani. Sau đó bọc lại, để ít nhất 8 giờ hoặc qua đêm trong ngăn mát tủ lạnh.\r\n\r\n- Đổ hỗn hợp vào chảo nông. Cho vào tủ lạnh để đóng băng trong khoảng 4 giờ.\r\n\r\n- Đánh kem whipping cream với đường cho đến khi bông lên trong khoảng từ 2 – 3 phút. Để lạnh cho đến khi sẵn sàng để thưởng thức.\r\n\r\n- Làm nóng lò nướng ở nhiệt độ 350 độ F, cho cùi dừa vào tấm nướng bánh và nướng trong lò, thỉnh thoảng đảo đều cho đến khi nó có màu vàng nâu (khoảng từ 7 – 10 phút).\r\n\r\n- Khi thưởng thức, xúc phần kem cà phê vào cốc, cho kem phủ lên trên rồi rắc một chút dừa khô. Ngoài ra, có thể làm kem cà phê bằng cách cho vào máy làm kem.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(64).jpg'),
(65, 'Kem dưa hấu mật ong', '- 500g dưa hấu\r\n- 15ml mật ong\r\n- 100gr kem tươi\r\n- 1 ống vani.\r\n', '- Cắt dưa hấu thành nhiều miếng nhỏ, rồi dùng bao thực phẩm đậy kín lại.\r\n\r\n- Cho vào ngăn đá tủ lạnh để đông.\r\n\r\n- Lấy dưa hấu đã đông ra, đập nhỏ rồi cho vào máy xay sinh tố, thêm mật ong vào xay nhanh cho đều, rắc vani vào.\r\n\r\n- Sau đó bạn đổ ra ly rồi trang trí thêm với ống bánh quế chocolate, bạc hà... cho đẹp mắt.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(65).jpg'),
(66, 'Mỳ Ý Spaghetti', '– Mì ống dài: 200g\r\n– Thịt bò: 200g\r\n– Cà chua: 200g\r\n– Hành tây : 1/2 củ\r\n– Lá nguyệt quế : 1 lá\r\n– Lá origano: 1 thìa cà phê\r\n– Dầu ăn, muối, đường.\r\n', '– Bắc nồi nước lên bếp, cho vào chút muối và 1 muỗng canh dầu ăn, đun sôi nước, thả mì vào luộc vừa lửa, luộc theo thời gian ghi trên bao bì nhé, khoảng 8-10 phút, đến khi bẻ đôi sợi mì thấy còn lõi nhỏ như sợi tóc là mì vừa chín tới. Đổ mì ra rá để ráo nước.\r\n\r\n– Cà chua rửa sạch, trần qua nước sôi, bóc bỏ vỏ, bỏ hạt, thái miếng vừa cho vào cối xay sinh tố xay nhuyễn.\r\n\r\n– Hành tây bóc bỏ vỏ, băm nhỏ.\r\n\r\n– Thịt bò mua về rủa sach, băm hoặc xay nhỏ, rồi ướp với chút hạt nêm.\r\n\r\n– Đặt nồi chống dính lên bếp, cho vào 1 chút dầu ăn, phi thơm hành tây rồi cho vào 1 lá nguyệt quế, tiếp đó đổ cà chua xay vào đun nhỏ lửa cho cà chua cạn bớt nước và sánh lại, thêm 1/2 thìa cà phê muối, 1 thìa cà phê đường, nêm nếm vừa miệng rồi cho thịt bò vào đảo đều, đun khoảng 2-3 phút cho thịt bò chín. Cho vào nồi thịt bò 1/2 thìa cafe lá origano, đảo đều, ta được phần sốt.\r\n\r\n– Trút 1/2 sốt thịt bò ra bát, cho mì đã luộc vào phần sốt còn lại trong nồi trộn đều, đun khoảng 1, 2 phút cho sợi mì săn lại và ngấm gia vị.\r\n\r\n– Trút mì ra đĩa, bày phần sốt thịt bò trong bát lên trên, và dùng khi còn nóng.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(66).jpg'),
(67, 'Cơm trộn Hàn Quốc - Bibimbap', '– Gạo ( 2/3 gạo tẻ và 1/3 gạo nếp)\r\n– 200g bí ngòi\r\n– 200g cải bó xôi\r\n– 200g cà rốt\r\n– 200g giá đỗ tương\r\n– 200g nấm kim châm\r\n– 150g nấm hương tươi\r\n– Dầu ăn, muối ăn, tỏi và vừng rang\r\n– Ướp thịt bò: 300g thịt bò thăn, 3 muỗng xì dầu, 1 muỗng đường, 1 muỗng dầu vừng, 1 muỗng tỏi băm nhỏ, 1 muỗng dấm gạo,  ½ thìa cà phê hạt tiêu .\r\n– Pha sốt: 3 muỗng Gochujang, 1 muỗng tương ớt Hàn Quốc, 3 muỗng nước, 1 muỗng đường, 1 muỗng dầu vừng, 1 muỗng dấm, 2 muỗng vừng rang.\r\n– 4 quả trứng\r\n– Rong biển sợi khô \r\n', '– Thái lát mỏng thịt bò, ướp thịt bò với với các nguyên liệu trên rồi cho vào tủ lạnh chừng nửa tiếng để thịt bò ngấm gia vị.\r\n\r\n– Cắt gốc nấm kim châm, sau đó rửa sạch cùng các loại rau củ.\r\n\r\n– Thái chỉ cà rốt, bí ngòi và thái mỏng nấm hương.\r\n\r\n– Chuẩn bị sốt Bibimbap. Cho các nguyên liệu làm sốt vào bát và quấy đều lên đến khi sốt mịn.\r\n\r\n– Trần rau cải bó xôi qua nước sôi trong khoảng 30 giây, vớt rau ra bát, để nguội, vắt rau cho bớt lượng nước thừa, sau đó thái nhỏ vừa ăn, ướp rau với chút muối, chút dầu mè , chút vừng rang.\r\n\r\n– Làm tương tự lần lượt với giá đỗ tương và nấm kim châm (Trần và ướp với muối, dầu mè và vừng rang).\r\n\r\n– Lần lượt xào cà rốt và nấm hương với chút muối đến khi chín mềm.\r\n\r\n– Cho tỏi vào xào thơm rồi đổ bí ngòi vào, thêm vào chút muối, xào cho đến khi bí ngòi tái thì đổ ra bát.\r\n\r\n– Lấy thịt bò đã ướp ra cho vào chảo đảo đến khi săn lại, hết đỏ rồi cho ra bát. Không nên xào kĩ quá sẽ khiến thịt bò bị dai.\r\n\r\n– Chuẩn bị 4 bát đá Hàn Quốc, phết một lớp dầu ăn mỏng đều trong lòng bát. Cho vào mỗi bát 1 suất cơm, nén nhẹ.\r\n\r\n– Xếp tất cả các nguyên liệu đã chuẩn bị lên trên cơm.Đặt bát đá lên bếp, thêm lòng đỏ trứng gà vào chính giữa bát (hoặc trứng ốp la). Đun đến khi lớp cơm ở dưới nóng giòn rồi bỏ xuống.\r\n\r\n– Rưới sốt Bibimbap đã chuẩn bị lên, thêm rong biển khô, trộn đều các nguyên liệu lên và thưởng thức.\r\n', '– Có thể thay đổi nguyên liệu rau củ phù hợp với sở thích của mình và cả lượng sốt sao cho hợp khẩu vị.', 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(67).jpg'),
(68, 'Bánh gạo cay Hàn Quốc - Tokbokki', '- 1 túi bánh gạo (khoảng 200gr)\r\n- 3 – 4 miếng chả cá\r\n- 1/4 cải bắp, hành lá\r\n- Dầu vừng, vừng rang\r\n- 3 muỗng canh nước sốt cay\r\n- Cà rốt\r\n', '- Rau bắp cải, hành lá rửa sạch, thái nhỏ, cà rốt rửa sạch thái sợi. Chả cá thái miếng vừa ăn.\r\n\r\n-  Cho nước vào nồi đun sôi rồi cho bánh gạo vào luộc qua 1-2 phút để bánh mềm, sau đó vớt ra xối nước lạnh và để ráo nước.\r\n\r\n-  Cho 3 bát con nước vào chảo, thêm nước sốt cay vào khuấy đều rồi đun sôi.\r\n\r\n-  Thả bánh gạo vào đun nhỏ lửa trong vòng 6-8 phút cho bánh chín mềm và ngấm nước sốt.\r\n\r\n-  Thả rau bắp cải, cà rốt, chả cá vào đảo đều và đun sôi thêm 3-4 phút nữa. Sau đó thêm dầu mè, hành lá vào đảo thêm lần nữa trước khi tắt bếp.\r\n\r\n-  Múc bánh gạo ra đĩa sâu lòng rồi rắc mè rang lên và thưởng thức khi còn nóng.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(68).jpg'),
(69, 'Mì Udon Nhật Bản', '– 300gr thịt bò\r\n– 1 bó mỳ udon\r\n– Tỏi tây, ớt\r\n– Giấm, đường, bột ngũ vị hương, dầu mè, muối ăn, nước tương ', '– Thái thịt bò thành những miếng nhỏ vừa ăn và ướp với ngũ vị hương, nước tương, đường, muối, giấm. Tiếp theo, trộn đều tất cả lên để thịt thật ngấm trong 30 phút.\r\n\r\n– Đun nóng dầu rồi cho tỏi tây, ớt vào xào. Sau đó, cho thịt bò vào xào cùng. Chú ý để lửa to cho thịt bò chín mềm.\r\n\r\n– Khi thịt bò đã bắt đầu chín và ngấm các loại gia vị ở trên, cho thêm một bát con nước lọc vào đun cùng. Khi nước bắt đầu sôi, cho thêm chút giấm.\r\n\r\n– Đun thịt với lửa to trong 30 phút để cạn bớt nước. Sau đó, nêm thêm gia vị cho vừa ăn.\r\n\r\n– Cuối cùng, luộc chín mỳ và bỏ vào bát ăn.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(69).jpg'),
(70, 'Cơm chiên Dương Châu', '- Xá xíu: 80g\r\n- Đậu Hà Lan: 50g\r\n- Cơm trắng: 4 chén\r\n- Lạp xưởng: 1 cây\r\n- Cà rốt: 80g\r\n- Trứng gà: 2 quả\r\n- Tôm khô: 20g\r\n- Tỏi băm, hành lá, ngò rí,\r\n- Muối, tiêu, đường, dầu ăn\r\n- Dầu điều, xốt cà chua\r\n- Nước tương, hạt nêm \r\n', '- Lạp xưởng luộc chín, cắt hạt lựu. Xá xíu, cà rốt cắt hạt lựu. Hành lá cắt nhỏ. Trứng đánh tan. Đậu Hà Lan và cà rốt luộc chín. Tôm khô ngâm mềm.\r\n\r\n- Xào lạp xưởng với chảo nóng cho tươm mỡ, trút ra dĩa. Phi thơm 1m tỏi băm, cho tôm khô và 1m đường vào xào cho bóng, trút ra dĩa lạp xưởng.\r\n\r\n- Trộn đều cơm với xá xíu, đậu Hà Lan, cà rốt, nêm 2m hạt nêm, 1/3m muối, 1m đường, 1M xốt cà chua và 1M dầu điều.\r\n\r\n- Đun nóng dầu, cho trứng vào vừa chiên vừa đánh để tạo thành những miếng mỏng rồi thêm cơm đã trộn vào đảo đều đến khi hạt cơm se mặt.\r\n\r\n - Múc cơm chiên ra dĩa, trang trí với ngò rí, ăn kèm dưa leo, cà chua, nước tương  và ớt cắt lát.\r\n', '- Không chiên cơm quá lâu và đảo nhiều sẽ làm hạt cơm bị nát. Chọn gạo cũ và nấu hơi khô, khi chiên cơm sẽ tơi và ngon hơn.\r\n- Rim tôm khô với đường để món ăn được ngon hơn.', 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(70).jpg'),
(71, 'Bún cá dọc mùng', '- Cá rô đồng\r\n- Dọc mùng\r\n- Xương đuôi\r\n- Dấm bỗng\r\n- Bún, hành khô\r\n- Cà chua\r\n- Hành, thì là, rau sống\r\n- Bột mì, bột năng\r\n', '-  Cá rô lọc xương, tách lấy thịt, ướp với hạt nêm. Cá ngấm gia vị lăn cá qua bột mì và bột năng theo tỉ lệ 2:1 rồi đun nóng dầu, thả cá vào rán vàng.\r\n\r\n- Rửa sạch xương, đun sôi nước rồi thả xương vào đun cho nổi các bọt đen, vớt ra xả lại với nước rồi chế nước mới đem ninh nhỏ lửa, nước dùng sẽ rất trong. Phần xương cá đã xay nhỏ cũng lọc luôn để nước dùng thêm ngọt, đập thêm củ hành nướng thả vào nồi nước dùng sẽ át đi vị tanh của cá.\r\n\r\n-  Dọc mùng tước vỏ, thái lát mỏng, ngâm 2 lần với nước muối pha đậm rồi ngâm xả vài lần với nước lạnh, sau đó vớt ra cho ráo nước.\r\n\r\n-  Cà chua bỏ hạt, thái múi cau, chưng với dầu ăn để tạo màu rồi trút vào nồi nước dùng, nêm nếm gia vị vừa miệng, sau đó cho dấm bỗng tạo vị chua, sau cùng mới thả dọc mùng vào đun sôi.\r\n\r\n-  Hành, thìa là thái nhỏ, khi ăn trần bún rắc hành thì là, cá lên trên rồi chan nước dùng.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(71).jpg'),
(72, 'Bún cá rô', '- 500gr bún.\r\n- 500gr cá rô.\r\n- 1 lít nước dùng.\r\n- Hành lá thái nhuyễn.\r\n- 1 quả ớt sừng.\r\n- 50gr rau thơm.\r\n- 50gr xà lách.\r\n- Gia vị: nước mắm, bột nêm, đường.\r\n- Dầu ăn.\r\n', '- Rửa xà lách, rau thơm, thái sợi lớn.\r\n\r\n- Làm sạch cá rô, để ráo.\r\n\r\n- Đun sôi nước dùng, cho cá vào, luộc chín, lóc lấy thịt.\r\n\r\n- Rán vàng 1/2 cá, phần còn lại cho ra bát.\r\n\r\n- Cho xương cá rô vào nồi nước dùng nấu tiếp, nêm gia vị vừa ăn, lược sạch nước.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(72).jpg'),
(73, 'Bún gạo xào thịt cua', '- Cua\r\n- 1 bịch bún gạo khô\r\n- Tỏi, tiêu, gia vị\r\n', '-  Cua đem luộc, để nguội rồi bóc thị\r\n\r\n- Bún gạo ngâm ngậm nước.\r\n\r\n- Xào 2/3 số thịt cua với dầu và tí gia vị cho vừa ăn.\r\n\r\n- Bún gạo ngâm đến khi hơi mềm vớt ra để hơi ráo nước rồi cũng đem xào với tỏi và tí dầu cho gia vị vừa ăn.\r\n\r\n- Đổ thịt cua vào bún gạo xào chung đến khi mềm.\r\n\r\n-  Dọn ra trang trí với số thịt cua còn lại.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(73).jpg'),
(74, 'Bún thịt nướng', '- 1kg thịt nách.\r\n- 2kg bún.\r\n- 1 trái dừa tươi.\r\n- 1 củ cà rốt.\r\n- 1 củ cải trắng.\r\n- 100gr đậu phộng.\r\n- 2 trái dưa chuột.\r\n- Rau sống, xà lách, giá, hành lá.\r\n- Tiêu, tỏi, ớt, chanh.\r\n- Bột ngọt, dấm, nước mắm, đường\r\n- 1 muỗng cafe nước màu, mỡ nước.\r\n- Than.\r\n', '+ Nướng thịt:\r\n- Thịt cạo rửa sạch, chẻ dọc theo chiều dài, ngang độ 3 cm xong xắt mỏng lại.\r\n- Hành lá lấy phần trắng giã nhỏ.\r\n- Tỏi giã nhỏ.\r\n- Ướp thịt hành và tỏi, nước màu, nước mắm, tiêu, đường, bột ngọt độ 20 phút cho thịt thấm.\r\n- Ghim thịt vào vỉ, nướng trên lửa than cho vàng đều hai mặt. \r\n- Khi nướng nên thoa nước ướp thịt khi nãy cho thấm.\r\n- Thịt vàng lấy ra thoa mỡ hành. \r\n\r\n+ Rau sống: \r\n- Rau sống và xà lách lặt rửa sạch để ráo nước, xắt sợi.\r\n- Dưa bầm theo chiều ngang, xắt nhỏ.\r\n- Giá nhặt rễ cho sạch.\r\n- Tất cả bày chung một đĩa.\r\n\r\n+ Làm đồ chua:\r\n- Cà rốt, củ cải trắng một phần tỉa hoa, rồi xắt mỏng lại để bày lên mặt còn bao nhiêu xắt sợi nhuyễn, bóp sơ với muối rồi xả lại cho sạch.\r\n- Xong đem ngâm dấm với đường.\r\n\r\n+ Pha nước mắm: \r\n- Nước dừa nấu cạn còn phân nửa đổ nước mắm, dấm, đường, bột ngọt, nêm vừa ăn để sôi lại, nhắc xuống để nguội, cho chanh tán nhuyễn (chanh gọt vỏ gỡ từng múi) và ớt tỏi giả nhỏ.\r\n- Trên chén nước mắm bày 1 ít cà rốt và củ cải trắng chẻ bông cho đẹp.\r\n- Đậu phộng rang vàng giã nhỏ.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(74).jpg'),
(75, 'Mì vằn thắn', '- Tôm tươi 200gr, tôm khô 50gr, thịt xay 100gr, gan lợn 100gr, xương lợn 1kg\r\n- Mỳ trứng 1 gói lớn, vỏ bánh gối 200gr\r\n- Hành tây 1 củ, củ cải 1 củ, cà rốt 1 củ, trứng gà 2 quả\r\n- Mộc nhĩ, nấm hương, hành lá, rau cải ngọt.\r\n- Muối, hạt nêm, hạt tiêu, 1 củ hành khô.\r\n\r\n* Để làm thịt xá xíu: 500gr thịt mông, 2 thìa canh đường mạch nha, 1 thìa mật ong, 2 thìa xì dầu, 1,5 thìa dầu hào, ½ thìa cà phê ngũ vị hương, 3 tép tỏi băm nhuyễn, ½ thìa cà phê hạt tiêu.\r\n', '- Làm thịt xá xíu: Rửa thịt sạch, thấm khô, cắt thành 2 – 3 miếng. Cho các nguyên liệu vào nồi (không cho thịt), đến khi thành hỗn hợp đặc, dính thì để nguội. Ướp thịt với hỗn hợp vừa làm, để vài tiếng cho thịt ngấm gia vị. Bật lò nướng, nướng thịt ở nhiệt độ 220 độ C đến khi thịt chín. Thái thịt thật mỏng.\r\n\r\n-  Ninh nước dùng: Đun sôi nước, chần xương, vớt bớt bọt đen, rồi rửa xương cho sạch. Hành tây gọt vỏ, thái nhỏ. Củ cải, cà rốt gọt vỏ, thái miếng. Rửa tôm khô cho sạch.\r\n	Ninh xương với cà rốt, củ cải, hành tây, tôm khô khoảng 2 tiếng. Có thể ninh bằng nồi cơm điện cho tiện, khi thấy bọt đen nổi lên thì vớt hết đi. Nêm thêm muối, hạt nêm cho nước đậm đà.\r\n\r\n-  Làm vằn thắn: Ngâm nấm hương, mộc nhĩ, rồi thái nhỏ. Hành khô băm nhỏ. Tôm tươi bóc vỏ, thái hạt lựu. Trộn thịt xay, nấm hương, mộc nhĩ, tôm, hành khô với nhau. Cho nhân vào giữa vỏ bánh, rồi túm tròn lại để làm vằn thắn luộc, gập thành hình tam giác để làm vằn thắn chiên.\r\n	Luộc vằn thắn: Cho vằn thắn vào nồi nước dùng, khi thấy miếng vằn thắn nổi lên là chín thì vớt ra. Với vằn thắn chiên, cho dầu vào chảo sâu lòng, cho từng miếng vằn thắn vào chiên. Thấy lớp vỏ vàng giòn thì vớt ra.\r\n\r\n-  Làm nhân cho bát mỳ: Luộc chín trứng gà, bóc vỏ, cắt làm đôi, hoặc làm 4. Gan rửa sạch, luộc chín, thái thật mỏng. Tôm tươi hấp chín, bóc vỏ. Rau cải ngọt cần chần nước sôi cho chín. Mỳ trứng đem chần qua nước sôi để loại bớt dầu thừa, luộc mỳ cho chín tới, vớt mỳ ra dội qua nước lạnh để mỳ không bị nát và dính vào nhau. Hành lá rửa sạch, thái khúc hoặc thái nhỏ tùy ý.\r\n\r\n-  Trang trí bát mỳ: Cho chút mỳ, rau cải ngọt, vài miếng vằn thắn luộc và chiên, 2 con tôm, 1 miếng trứng, vài miếng gan, hành lá vào bát. Chan thêm nước dùng rồi thưởng thức. \r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(75).jpg'),
(76, 'Miến lươn xào lăn', '- 300g lươn\r\n- 1 lọn miến đỗ xanh\r\n- 1 - 2 nhánh sả\r\n- Vài tai mộc nhĩ (nấm mèo)\r\n- Ớt quả, muối, hạt nêm, đường, hạt tiêu \r\n- 1 thìa nhỏ bột cà ri\r\n- Hành lá, hành khô, mùi tàu (ngò gai), rau ngổ\r\n- Bánh đa ăn kèm.\r\n', '- Lươn làm sạch, bỏ ruột, cho ra rổ, thêm muối vào, dùng tay chà mạnh để lươn ra hết chất nhớt, cắt thành từng khúc ngắn vừa ăn. Để lươn lên rổ cho ráo nước.\r\n\r\n- Cho lươn vào bát, nêm bột cà ri, một thìa nhỏ muối, một ít hạt tiêu, hành khô thái mỏng, trộn đều, ướp trong vòng 30 phút.\r\n\r\n- Miến ngâm nở, cắt ngắn, chần sơ qua nước sôi rồi xả lại nước lạnh để chống dính.\r\n\r\n- Mộc nhĩ ngâm nở, cắt bỏ chân, thái sợi\r\n\r\n- Hành khô bỏ vỏ, hành lá, mùi tàu, rau ngổ rửa sạch, thái nhỏ.\r\n\r\n- Sả tước bỏ bớt cọng cứng, băm nhuyễn. Ớt thái nhỏ.\r\n\r\n- Đun nóng hai thìa nhỏ dầu ăn, phi thơm hành khô, thêm sả, ớt vào đảo cùng.\r\n\r\n- Cho lươn vào đảo cùng, đậy kín nắp, rưới một ít nước cốt dừa (nếu muốn món ăn có vị béo) hoặc chỉ cẩn một ít nước sôi. Đun đến khi lươn chín.\r\n\r\n- Cho mộc nhĩ vào đảo cùng, nêm nếm gia vị vừa miệng.\r\n\r\n- Sau đó cho miến vào đảo đều, đảo nhẹ tay, nêm nếm lại tùy theo khẩu vị.\r\n\r\n- Tắt bếp, thêm hành lá, mùi tàu, rau ngổ vào đảo cùng. Múc ra đĩa, dùng nóng với bánh đa.\r\n\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(76).jpg'),
(77, 'Miến măng gà', '- 1 con gà khoảng 1 kg\r\n- 300g măng khô hoặc măng tươi\r\n- Miến đỗ xanh\r\n- 1 nhánh gừng\r\n- Hành khô\r\n- Hành lá, muối, nước mắm, rau răm, ớt quả, đường, tỏi.\r\n', '- Măng khô rửa sạch, tước bỏ cọng già, cứng, cọng non dùng tay xé sợi. Ngâm vào âu nước lạnh, ngâm từ 1 - 2 ngày, mỗi ngày thay nước từ 2 - 3 lần để măng ra hết chất chua.\r\n\r\n- Đun nồi nước sôi, đổ măng vào luộc sôi. Sau mỗi lần luộc xả lại nước lạnh cho thật sạch. Lặp lại khoảng 3 - 4 lần rồi cho măng ra rổ, để ráo.\r\n\r\n- Hành khô rửa sạch, để ráo, sau đó nướng đến khi vàng đều.\r\n\r\n- Gừng cạo vỏ, mộp phần thái lát vừa ăn, mộp phần bạn dùng để làm nước mắm gừng ăn kèm.\r\n\r\n- Gà rửa sạch với nước muối pha loãng, đun nồi nước sôi, thả con gà vào chần sơ qua nồi nước sôi để bỏ tạp chất, sau đó vớt gà ra rửa lại cho thật sạch.\r\n\r\n- Phần đầu, cổ của gà chặt để riêng.\r\n\r\n- Tiếp theo cho gà, đầu cổ cạnh vào nồi, thêm gừng thái lát, một thìa nhỏ muối và hành khô, đun sôi. Đun từ 10 - 15 phút (tùy theo con gà bé hay lớn), tắt bếp, đậy kín nắp nồi để gà tiếp tục chín.\r\n\r\n- Đợi đến khi nước hầm gà nguội, tắt bếp, vớt gà ra âu có để vài viên đá lạnh để da gà được giòn.\r\n\r\n- Tiếp theo cho gà ra đĩa, phần lườn gà dùng dao thái lát từng lát vừa ăn, phần đùi gà bạn có thể giữ nguyên hay xé thành từng miếng vừa ăn, xương gà bạn giữ lại cho vào nồi đun tiếp tục đun với hành, gừng và phần đầu, cổ.\r\n\r\n- Măng sau khi luộc mềm, phi hành thơm, cho măng vào xào, thêm vào hai thìa nhỏ muối, một ít hạt tiêu và cho vào một ít nước luộc gà, đậy kín nắp, đun từ 5-10 phút.\r\n\r\n- Sau đó cho măng vào nồi nước dùng gà, đun cùng bạn nêm nếm lại gia vị vừa ăn.\r\n\r\n- Rau răm, hành lá sửa sạch, thái nhỏ.\r\n\r\n- Miến chần sơ qua nước sôi, xả lại miến dưới vòi nước lạnh để miến không bị dính chùm. \r\n\r\n- Tỏi, ớt quả, gừng, đường, giã nhuyễn, pha hỗn hợp nước mắm với hỗn hợp tỏi ớt, nêm vừa miệng.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(77).jpg'),
(78, 'Miến xào hến', '- 200g thịt hến\r\n- 2 lọn miến nhỏ tầm 50g\r\n- Vài tai mộc nhĩ (nấm mèo)\r\n- Muối, hạt nêm, tiêu, và hạt tiêu\r\n- Rau mùi, hành lá, hành khô, bánh đa nướng ăn kèm, dầu điều và ớt bột.\r\n', '- Thịt hến mua về rửa sạch, để lên rổ cho ráo nước, trộn vào một thìa nhỏ muối, một ít hạt tiêu, để khoảng 25 phút.\r\n\r\n- Mộc nhĩ ngâm nở, cắt bỏ chân, thái sợi nhỏ.\r\n\r\n- Hành khô bóc vỏ, thái nhỏ.\r\n\r\n- Miến ngâm vào âu nước lạnh, ngâm khoảng 15 phút.\r\n\r\n- Tiếp theo chần sơ miến qua nồi nước sôi, vớt ra cho miến vào âu nước lạnh để miến không bị dính chùm.\r\n\r\n- Sau đó đun nóng hai thìa nhỏ dầu điều, phi hành khô thơm, cho hến và mộc nghĩ vào xào, nêm vào một thìa nhỏ muối, nửa thìa hạt nêm, đảo đều.\r\n\r\n- Cuối cùng cho miến vào đảo cùng với nửa thìa nhỏ ớt bột, đảo nhanh tay lửa lớn đến khi miến hòa quyện với hến, nêm nếm lại tùy theo khẩu vị của bạn, tắt bếp thêm hành lá, rau mùi đã thái nhỏ và rắc một ít hạt tiêu lên bề mặt.\r\n\r\n- Múc ra đĩa, dùng nóng với bánh đa nướng.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(78).jpg'),
(79, 'Phở gà trộn', '- 1 con gà ta\r\n- 2 nhánh gừng\r\n- Vài củ hành khô\r\n- Muối, nước mắm, đường, tỏi, hắc xì dầu, xì dầu, dấm tiều (hay còn gọi là dấm đỏ)\r\n- Sợi bánh phở\r\n- Vài nhánh hành lá, rau mùi, rau húng, rau răm\r\n- Lạc rang vàng.\r\n', '- Gừng, hành khô rửa sạch, nướng thơm.\r\n\r\n- Hành lá, giá, rau húng, rau răm, rau mùi, rửa sạch. Thái nhỏ hành lá và rau mùi.\r\n\r\n- Tỏi bóc vỏ, giã nhuyễn, phi thơm tỏi đến khi tỏi vàng, đổ ra bát.\r\n\r\n- Sợi bánh phở chần sơ qua nước sôi cho mềm, xả lại nước lạnh, đổ bánh phở ra rổ rưới một ít tỏi phi và một ít dầu ăn lên bề mặt bánh phở để chống dính.\r\n\r\n- Lạc rang vàng, giã thô.\r\n\r\n- Hỗn hợp nước tương: pha hai thìa canh xì dầu, một thìa canh hắc xì dầu, hai thìa canh đường, hai thìa nhỏ dấm tiều và một ít tỏi phi vàng, khuấy đều để đường tan, nêm hơi mặn để dùng chung với bánh phở trộn. 	\r\n\r\n- Thịt gà sau khi chín, vớt ra âu nước lạnh để da gà được giòn lâu, sau đó chặt thành từng lát vừa ăn hay dùng tay xé nhỏ thịt gà.\r\n\r\n- Phần nước dùng bạn nêm gia vị lại cho vừa ăn, múc một ít nước súp ra bát, rắc hành lá thêm rau mùi vào.\r\n\r\n- Khi dùng, cho bánh phở ra bát, thêm thịt gà, rưới một ít tỏi phi, thêm lạc rang, hành lá và rau mùi, rưới một ít nước tương đã pha, dùng kèm với giá, chanh, ớt, các loại rau thơm và bát nước súp. Có thể thêm tương ớt nếu bạn ăn cay.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(79).jpg'),
(80, 'Phở xào tôm thịt', '- 300g thịt bò\r\n- 100g tôm\r\n- 1 quả trứng gà\r\n- Cải thìa, nửa củ hành tây\r\n- Muối, dầu hào, hạt nêm, nước mắm, xì dầu\r\n- Lạc rang chín, giã thô\r\n- Sợi bánh \r\n', '- Thịt bò rửa sạch, thái mỏng, ướp vào bát thịt bò nửa thìa nhỏ muối, một thìa nhỏ dầu ăn, một ít hạt tiêu, ướp khoảng 15 đến 20 phút.\r\n\r\n- Tôm bóc nõn, rút chỉ đen trên thân tôm cho thật sạch, rửa sạch, để ráo.\r\n\r\n- Cải thìa rửa sạch, cắt đoạn ngắn vừa ăn.\r\n\r\n- Hành tây bóc bỏ vỏ, rửa sạch, thái lát.\r\n\r\n- Sợi bánh phở chần sơ qua nước sôi, để ráo. Nếu dùng sợi bánh phở khô bạn phải luộc sợi bánh phở đến khi mềm, và đổ ra rổ, xả lại dưới vòi nước lạnh để sợi bánh phở không bị dính chùm.\r\n\r\n- Trứng đánh tan, thêm vào một ít nước mắm, hạt tiêu, tiếp theo đun nóng một ít dầu ăn, phi hành, tỏi thơm, cho trứng vào rán. Trứng vàng đều, để nguội, thái sợi vừa ăn.\r\n\r\n- Đun nóng dầu ăn, phi tỏi thơm, cho thịt bò vào xào chín, đổ ra bát để riêng.\r\n\r\n- Dùng chảo đó, cho tôm vào xào chín, thêm cải thìa, hành tây vào xào cùng, thêm vào một ít dầu hào, xào đến khi cải chín mềm.\r\n', NULL, 1, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(80).jpg'),
(81, 'Bánh bèo', '- Tôm sú sống: 200g\r\n- Bóng bì: 10g\r\n- Hành lá: 200g\r\n- Bột gạo lọc: 1 chén\r\n- Bột năng: 1M\r\n- Nước ấm: 2 chén\r\n- Ớt xanh: 3 trái\r\n- Muối, đường, nước mắm, dầu ăn\r\n- Hạt nêm, bột ngọt\r\n', '- Hòa bột gạo và bột năng với 2 chén nước ấm, rây lại, nêm 1/4m muối và 1/2m bột ngọt, để 30 phút cho bột nở.\r\n\r\n- Tôm bỏ đầu, luộc chín, bóc vỏ, giã nhuyễn, chấy trên chảo làm chà bông, nêm 1/4m muối. Phần nước luộc tôm giữ lại để pha nước chấm.\r\n\r\n- Bóng bì chiên vàng, cắt hạt lựu. Ớt xanh băm nhỏ.\r\n\r\n- Làm dầu hành: hành lá cắt nhỏ, cho vào chén với 1/5m muối, 1/5m bột ngọt, đun nóng 2M dầu đổ vào chén hành, thêm 1m dầu nguội, đảo đều.\r\n \r\n- Đun nhiều nước trong nồi hấp cho thật sôi, đặt khuôn bánh vào cho thật nóng, quết dầu lên chén. Khuấy đều bột, múc đổ vào 2/3 khuôn, đậy vung, hấp 5 phút là bánh chín.\r\n\r\n- Pha nước chấm: trộn đều 5m nước luộc tôm với 1M đường, 1M nước mắm và ớt xanh băm nhỏ.\r\n \r\n- Xếp bánh bèo ra dĩa có lót lá chuối, rắc tôm chà bong, dầu hành và da heo chiên phông lên trên, ăn kèm nước mắm pha.\r\n', '- Làm dầu hành: Cho thêm dầu nguội vào để giữ màu xanh và độ giòn của hành lá.\r\n- Chọn miếng bóng bì mỏng, có màu vàng sáng và không bị gắt dầu.', 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(81).jpg'),
(82, 'Bánh cuộn chocolate', '- 3 quả trứng (50g/quả)\r\n- 90g đường\r\n- 10g bột ca cao\r\n- Bột mỳ (loại cake flour)\r\n- 25g bơ\r\n- 150g kem tươi\r\n- 5 bột ca cao\r\n- 15g đường\r\nĐồ trang trí:\r\n- Vài miếng trái cây (tùy loại)\r\n- Ít hạt bí.\r\n', '- Lấy trứng ra khỏi tủ lạnh và đặt ở nhiệt độ phòng khoảng 30 phút rồi tách riêng lòng đỏ và trắng. Thêm 40g đường vào lòng đỏ trứng và khuấy tan. Làm tan bơ trong lò vi sóng, chờ nguội rồi đổ vào lòng đỏ trứng và trộn đều.\r\n\r\n- Thêm 50g đường vào lòng trắng trứng và dùng máy đánh trứng đánh bông cho đến khi bông xốp (không đánh lòng trắng đến mức bông cứng). Sau đó cho lòng trắng trứng đã đánh bông vào hỗn hợp bơ và lòng đỏ trứng, dùng máy đánh ở tốc độ cao, đánh cho đến khi hỗn hợp nhạt màu và quánh (thời gian đánh khoảng 7-10 phút).\r\n\r\n- Rây bột cacao và bột mỳ vào âu bột rồi trộn đều cho đến khi được một hỗn hợp mịn và mượt.\r\n\r\n- Đổ bột vào khay vuông có lót sẵn giấy nến, vỗ nhẹ hoặc gõ nhẹ khuôn xuống bàn cho mặt bột được dàn phẳng và các bọt khí to vỡ bớt . Bật lò nướng đến 180 độ C rồi đặt khay vào chính giữa. Nướng trong khoảng 20-30 phút hoặc đến khi bánh chín vàng mặt.\r\n\r\n- Bánh chín lấy ra khỏi lò và lập tức dùng dao mỏng rạch quanh thành trong của khuôn rồi úp ngược để lấy bánh ra. Bánh rất nhẹ nên khi lấy bạn nhẹ tay thôi nhé! Bóc bỏ lớp giấy lót dính ở đáy bánh. Đánh bông kem tươi với phần bột cacao còn lại.\r\n\r\n- Thông thường các loại bánh cuộn sẽ được cuộn riêng trước để giữ “form” rồi mới trét nhân và cuộn lại với nhân kèm theo. Sau khi lấy bánh ra khỏi khuôn, để hơi nước thoát bớt, sờ vào thấy ấm thì bắt đầu cuộn. Cách cuộn là chuẩn bị một tờ giấy nến có kích thước rộng hơn. Đặt bánh lên giấy và nhẹ nhàng cuộn lại và không quá chặt tay.\r\n\r\n- Sau khi nguội thì mở bánh ra, mép bánh hơi quăn vì đã được tạo nếp. Dùng dao trét một lớp kem lên phần mặt trong của cuộn bánh rồi cuộn lại. Nếu dùng nhiều kem tươi thì bạn nên chừa mép cuối của bánh khoảng 1 – 2 cm để khi cuộn lại kem sẽ được dồn và đẩy dần ra là vừa.\r\n\r\n- Sau khi cuộn bánh, bọc kín lại bằng giấy nến rồi đặt vào ngăn mát tủ lạnh chừng 20 – 30 phút cho bánh định hình.\r\n\r\n- Lấy bánh ra, cắt thành khoanh cỡ 4 – 5 cm. Bạn có thể trang trí bánh bằng một lượng bơ nhỏ trên mặt, thêm miếng hoa quả tươi và hạt bí tách vỏ là có thể thưởng thức rồi.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(82).jpg'),
(83, 'Bánh gấc', 'Vỏ bánh:\r\n- Bột nếp khô : 700g.\r\n- Bột đỗ xanh lọc : 100g.\r\n- Gấc : 1 quả.\r\n- Nước ấm : 100 ml.\r\n- Đường : 200g.\r\n- Lá Mảnh cộng : 50g.\r\n- Rượu trắng : 1 chút.\r\n- Lá chuối : 200g.\r\n \r\nNhân bánh:\r\n- Đỗ xanh đã hấp chín : 300g.\r\n- Đường : 100g.\r\n- Dừa nạo : 50g.\r\n- Nước hoa bưởi : 1 chút.\r\n', '- Bột nếp nhào với nước, rây thật mịn, trộn với bột sắn lọc, để riêng.\r\n \r\n- Gấc bổ đôi, lấy ruột, bỏ hạt, đánh với một chút rượu.\r\n \r\n- Lá Mảnh cộng giã nát, vắt lấy nước.\r\n \r\n- Chia bột làm hai phần. Phần bột nhiều cho đường, rồi vẩy nước ấm vào trộn đều. Sau đó cho gấc vào nhào thật dẻo, mịn cho đến khi bột chuyển sang màu hồng. Để bột nghỉ 20 phút. Phần bột nhỏ còn lại trộn với nước lá mảnh cộng để làm tai bánh.\r\n \r\n- Đỗ xanh sau khi hấp chín thì giã nhỏ, cà mịn rồi trộn với đường để khoảng 20 phút. Sau đó cho vào chảo xào nhỏ lửa. Đến khi sờ thấy đỗ không dính tay thì cho tiếp dừa nạo vào đảo đều. Đổ nước hoa bưởi vào rồi bỏ ra nặn thành từng viên bằng quả mận nhỏ.\r\n \r\n- Lấy phần bột trộn với lá mảnh cộng, nặn thành tai cà chua 5 cánh để riêng.\r\n \r\n- Nhào lại phần bột trộn với gấc rồi chia làm nhiều phần bằng nhau. Dàn mỏng từng viên bột, cho nhân đỗ xanh vào, bao kín, nặn tròn. Tiếp đến lấy que tre khía múi xung quanh bánh. Dùng tay ấn lõm phần gữa bánh rồi gắn tai cà chua lên trên.\r\n \r\n- Đun sôi nước trong nồi hấp, xếp lá chuối xuống đáy, đặt bánh lên trên, hấp khoảng 10 phút là bánh chín.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(83).jpg');
INSERT INTO `eating` (`id`, `name`, `material`, `making`, `tips`, `idType`, `bookmark`, `image`) VALUES
(84, 'Bánh há cảo hoa hồng', '- Thịt nạc băm: 100g\r\n- Giò sống: 30g\r\n- Tôm tươi: 50g\r\n- Bột há cảo: 200g\r\n- Nấm mèo: 3 tai\r\n- Hành tím băm: 1m\r\n- Hành lá, ngò rí, ớt tỉa hoa\r\n- Lá chuối cắt tròn làm đế bánh\r\n- Tiêu, tương ớt, dầu ăn\r\n- Hạt nêm, bột ngọt \r\n', '- Tôm bóc vỏ, cắt hạt lựu. Nấm mèo ngâm mềm, cắt sợi. Hành lá cắt nhỏ.\r\n\r\n- Làm nhân: Trộn đều thịt heo, giò sống với tôm, nấm, 1m mèo, 1m hành tím băm, hành lá xắc nhuyễn, nêm 1/3m tiêu và 1m hạt nêm, chia thành 16 viên.\r\n\r\n- Pha bột: chừa lại 2M bột làm áo, cho lượng bột còn lại vào nồi, nêm 1/2m muối và 1/2m bột ngọt, thêm 1 chén nước ấm vào từ từ, khuấy cho bột tan đều, bắt lên bếp để lửa thật nhỏ, đến khi bột sánh và thấy nặng tay thì thêm 1m dầu ăn vào trộn đều. Đổ bột ra bàn, nhồi mịn rồi chia thành 16 viên.\r\n\r\n- Xoa dầu vào tay, nắn bột dẹt ra, cho nhân vào, bao kín lại. Dùng kẹp hoa hồng đã nhúng dầu kẹp 3 cánh ở giữa trước, sau đó kẹp các cánh lớp ngoài sao cho các cánh xen kẽ xếp thành 3 lớp.\r\n\r\n- Xếp lá chuối vào nồi, sau đó xếp bánh vào hấp khoảng 5 phút thì xả hơi một lần, sau đó hấp tiếp khoảng 20 phút thì bánh chín.\r\n \r\n- Xếp bánh hoa hồng ra dĩa, trang trí với ngò rí và ớt tỉa hoa, dùng nóng với tương ớt.\r\n', '- Khuấy bột trên lửa nhỏ để bột chín 50% sẽ dễ tạo hình. Khi bao nhân nhớ chừa phần bột dư bên trên đủ để tạo cánh hoa.', 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(84).jpg'),
(85, 'Bánh mì trứng đánh thịt nguội', '- Bánh mì sandwich: 8 miếng\r\n- Trứng gà: 4 quả\r\n- Thịt nguội: 2 lát\r\n- Hành tây: 1/4 củ\r\n- Xà lách: 1 cây\r\n- Ngò rí: 2 cây\r\n- Cà chua: 1 trái\r\n- Muối, tiêu, dầu ăn, ớt cắt lát\r\n- Xốt Mayonnaise \r\n', '- Xà lách, ngò rí rửa sạch, để ráo. Hành tây, cà chua, thịt nguội cắt miếng vuông nhỏ.\r\n\r\n- Sandwich nướng vàng. Trứng đánh tan, khuấy đều với ít nước lọc.\r\n\r\n- Xào nhanh hành tây, cà chua và thịt nguội với 1 ít dầu ăn, cho trứng vào vừa chiên vừa khuấy đến khi trứng vừa ráo là được.\r\n \r\n- Phết xốt Mayonnaise  lên 1 mặt bánh sandwich, xếp xà lách, ngò rí, ớt và trứng chiên lên, rắc thêm ít muối, tiêu tùy khẩu vị.\r\n', '- Pha thêm 1 ít nước vào trứng để khi chiên trứng không bị khô, có thể thay nước bằng sữa tươi nếu thích ăn béo.', 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(85).jpg'),
(86, 'Bánh mousse trà xanh', '* Cho phần bánh bông lan\r\n- 50g bột mì số 8 (cake flour)\r\n- 6g trà xanh\r\n- 2 trứng\r\n- 40g đường\r\n- 30g sữa\r\n- 20g bơ\r\n\r\n* Cho phần mousse sô cô la trắng:\r\n- 150ml kem tươi (whippingcream)\r\n- 90g sô cô la trắng\r\n- 2 lòng trắng trứng\r\n- 50ml sữa\r\n- 4g bột gelatin\r\n- 1 muỗng canh nước\r\n\r\n* Cho phần mousse trà xanh:\r\n- 100ml kem tươi\r\n- 8g bột trà xanh\r\n- 2 lòng đỏ\r\n- 20g đường\r\n- 80ml sữa\r\n- 3g gelatin\r\n- 1 muỗng canh nước\r\n- 2 muỗng canh nước nóng để hòa tan bột trà xanh\r\n', ' *Cho phần bánh:  \r\n- Chuẩn bị khuôn tròn đường kính 21cm lót giấy nến ở đáy khuôn.\r\n\r\n- Rây bột mì và bột trà xanh 2-3 lần cho mịn.\r\n\r\n- Cho trứng và đường vào âu chịu nhiệt. Đặt âu lên một nồi nước sôi (đáy âu không chạm nước). Đánh trứng với tốc độ nhỏ rồi tăng dần lên tốc độ cao. Đánh cho đến khi bọt khí nhỏ dần, khi thấy trứng ấm thì nhấc âu ra khỏi nước. Với cách đánh ấm như thế này trứng nhanh nổi và ổn định hơn.\r\n\r\n- Đánh trứng đến khi nổi đặc, mịn thì giảm tốc độ. Khi nhấc que đánh lên thấy trứng mịn chảy xuống thành dòng như dãy ruy-băng là được.\r\n\r\n- Chia hỗn hợp bột thành 3 phần, rây vào trứng. Trộn nhẹ nhàng theo kiểu fold (cạo bột từ đáy âu rồi hất lên và gấp lại) sau mỗi lần thêm bột. Được hỗn hợp (A).\r\n\r\n- Làm nóng bơ và sữa trong lò vi sóng khoảng 30 giây cho bơ tan. \r\n\r\n- Lấy 1/3 hỗn hợp (A) cho vào hỗn hợp bơ sữa trộn đều, được hỗn hợp (B). Từ từ trút phần hỗn hợp (B) ngược trở vào 2/3 hỗn hợp (A) còn lại. Trộn nhẹ theo kiểu fold nhẹ nhàng nhằm tránh vỡ bọt khí.\r\n\r\n- Cho bột vào khuôn đã lót giấy nến. Gõ khuôn vài cái để loại bỏ bọt khí.\r\n\r\n- Chuẩn bị lò nướng ở 170 độ C. Nướng bánh 17-20 phút.\r\n\r\n- Bánh chín để nguội, lấy ra khỏi lò, dùng màng bọc thực phẩm bọc bánh lại, mặt bánh vàng quay xuống dưới.\r\n\r\n- Khi bánh nguội, sử dụng một khuôn tròn không đáy đường kính 18cm (mousse ring) để cắt bánh ra. Đặt bánh trên đĩa với phần mặt bánh ở phía dưới.\r\n\r\n*Chuẩn bị phần mousse sô cô la trắng:\r\n- Ngâm gelatin trong nước. Đánh lòng trắng trứng với tốc độ mạnh cho thật nổi (tạo chóp nhọn). \r\n\r\n- Đánh 250ml kem tươi cho đặc nhưng vẫn mềm mịn (đừng đánh quá mức). Lấy 100g kem đã đánh để riêng ra, cất vào tủ lạnh chuẩn bị cho phần mousse trà xanh. \r\n\r\n- Đun nóng già sữa, cho sô cô la trắng vào, tắt bếp, khuấy đều cho sô cô la tan ra. Cho phần gelatin ngâm mềm vào, khuấy tan bạn được hỗn hợp (C).\r\n\r\n- Trút phần hỗn hợp (C) vào 150ml kem tươi đã đánh bông. Trộn đều để được hỗn hợp (D).\r\n\r\n- Cho phần lòng trắng đã đánh nổi vào (D). Như vậy là hoàn thành mousse sô cô la trắng. Trút phần mousse sô cô la trắng vào khuôn bánh đã chuẩn bị. Dàn đều mặt mousse và cho vào tủ lạnh khoảng 40 phút cho se mặt.\r\n\r\n*Chuẩn bi mousse trà xanh: \r\n- Ngâm gelatin vào nước. Đánh bông lòng đỏ trứng và đường cho đến khi chuyển màu trắng, mịn - gọi là hỗn hợp (E). \r\n\r\n- Hòa trà xanh vào nước nóng. Thêm sữa khuấy tan. Đun nóng trên lửa nhỏ cho tan trà. Cho gelatin đã ngâm mềm vào, khuấy tan được hỗn hợp (F).\r\n\r\n- Trút phần (E) vào (F) và trộn đều được hỗn hợp (G).\r\n\r\n- Trộn phần (G) với 100ml kem tươi đã đánh và giữ lạnh. Như vậy là hoàn tất mousse trà xanh.\r\n\r\n- Trút phần mousse trà xanh nhẹ nhàng lên mặt phần mousse sô cô la trắng. Dàn phẳng mặt bánh. Cho vào tủ lạnh ít nhất 2 giờ cho mousse đông lại.\r\n\r\n- Khi mousse đã đặc, rây bột trà xanh lên mặt và trang trí tùy ý.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(86).jpg'),
(87, 'Bánh nama chocolate', '- 100ml kem tươi (có thể dùng fresh cream hoặc whipping cream)\r\n - 300gr sô cô la thô (loại dùng để làm bánh, có vị ngọt sẵn)\r\n - 40g bơ nhạt (unsalted butter - loại dùng để làm bánh)\r\n - Bột cacao để phủ ngoài\r\n - 1 hộp vuông (loại hộp nhựa hay đựng đồ ăn trong tủ lạnh), lót sẵn 1 lớp giấy nến hoặc wrap (loại nilon bọc ngoài đồ ăn)\r\n', '- Sô cô la thô bào mỏng (nếu dùng loại sô cô la chips thì không cần bào)\r\n \r\n- Nấu sôi whipping cream với lửa lớn, dùng phớ đánh trứng khuấy đều. Nhấc xuống, cho sô cô la vào đành đều cho đến khi tan hết. Sau đó cho bơ vào đánh đều một lần nữa cho đến khi tạo thành một hỗn hợp nhuyễn.\r\n\r\n- Đổ hỗn hợp vào khuôn vuông, dùng 1 lớp nylon phủ đè lên trên mặt để đẩy hết không khí ra ngoài để làm mịn mặt sô cô la. Sau đó cho vào tủ lạnh (ngăn đá thì 2 tiếng, ngăn lạnh thì để qua đêm).\r\n\r\n- Khi sô cô la đã cứng lại thì lấy ra. Đổ một lớp cacao xuống dưới giấy lót, lột lớp wrap rồi úp lên lớp cacao. Sau đó lột nốt lớp nylon phủ bên dưới rồi dùng rây rắc bột cacao còn lại lên làm lớp phủ.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(87).jpg'),
(88, 'Bánh quy phô mai', '- Bột mì - 100g\r\n- Đường bột - 80g\r\n- Bột nở - 2g\r\n- Trứng - 2 quả\r\n- Bơ nhạt - 65g\r\n- Creamcheese - 60g\r\n- Sữa tươi - 15ml\r\n- Tinh chất vani - 1 thìa cafe.\r\n', '- Để bơ ở nhiệt độ phòng cho mềm rồi thêm đường bột và creamcheese vào dùng máy đánh trứng đánh cho bông mịn.\r\n\r\n- Chia trứng thành 3 phần và đổ vào hỗn hợp trên trộn đều khi hết.\r\n\r\n- Trộn lẫn bột mì và bột nở, rây mịn, đổ vào đánh đều cùng hỗn hợp bạn có được ở bước 1.\r\nTừ từ thêm sữa và vani vào trộn đều.\r\n\r\n- Cho hỗn hợp bột vừa trộn vào túi nylon bắt kem, rắc bột khô lên khuôn bánh để chống dính, bóp bánh vào trong khuôn. Nướng bánh trong lò ở 170 độ C khoảng 10 – 13 phút thấy bánh vàng đều thì lấy bánh ra khỏi lò, để trên giá chờ nguội rồi dùng ngay hoặc cất vào lọ kín để dành dùng dần.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(88).jpg'),
(89, 'Bánh rán Đô-rê-mon (Dorayaki)', '- 2 quả trứng gà\r\n- 150g bột làm bánh bông lan \r\n- Nửa thìa nhỏ bột nở\r\n- 1 thìa súp mật ong\r\n- 80g đường cát trắng\r\n- 60ml nước lọc\r\n- 1 ống vani nhỏ\r\n- Nửa thìa nhỏ muối\r\n- 1/2 bát con đậu đỏ\r\n- 60g đường cát trắng\r\n- Nửa thìa nhỏ muối\r\n', '- Đậu đỏ đãi sạch, nhặt bỏ những hạt hỏng, ngâm đậu trong âu nước ấm khoảng 5-6 tiếng.\r\n\r\n- Rửa đậu lại cho thật sạch, cho đậu vào nồi, thêm muối và nước xâm xấp với mặt đâu, đun sôi đến khi hạt đậu nở mềm thì tắt bếp. Bạn có thể ninh đậu nhanh bằng nồi áp suất.\r\n\r\n- Đậu sau khi mềm bạn cho vào máy sinh tố, thêm vào một ít nước hầm đậu để dễ xay và không bị cháy máy, xay đến khi hạt đậu tơi ra.\r\n\r\n- Cho đậu vào chảo, thêm đường, đảo lửa nhỏ đến khi cạn bớt nước và hạt đậu khô lại thì tắt bếp, để nguội cho vào tủ lạnh.\r\n\r\n- Trộn lẫn muối, bột nở, bột làm bánh vào tô lớn, rây bột thật mịn.\r\n\r\n- Cho trứng vào tô lớn, thêm đường dùng dụng cụ đánh trứng cầm tay hay lấy 3 đôi đũa chụm lại, đánh tan đường.\r\n\r\n- Cho từ từ mật ong, dầu ăn vào khuấy theo một chiều để hỗn hợp dầu ăn hòa lẫn với hỗn hợp trứng.\r\n\r\n- Cho hỗn hợp bột ở bước 4 vào hỗn hợp trứng, trộn nhẹ.\r\n\r\n- Đổ thêm nước lọc vào hỗn hợp trứng, trộn đều đến khi đặc, dùng dụng cụ đánh trứng nhích bột lên chảy xuống thành từng dòng liên tục là được. Thêm ống vani vào, trộn đều. Tùy theo độ hút nước của mỗi loại bột mà điều chỉnh lượng nước cho phù hợp.\r\n\r\n- Dùng màng thực phẩm, bọc kín khoảng 30 phút.\r\n\r\n- Bật bếp, cho chảo chống dính lên bếp, chảo nóng thì bạn rưới một ít dầu ăn lên bề mặt chảo, sau đó dùng khăn giấy hay cọ phết, lau sơ phần dầu ăn để chỉ còn bám một lớp mỏng trên bề mặt chảo. Nếu nhiều dầu khi rán bánh sẽ không vàng đều.\r\n\r\n- Để lửa nhỏ, dùng muôi nhỏ, múc bột đổ từ trên cao vào chảo để bánh tròn đều, sau đó rán bánh với lửa vừa.\r\n\r\n- Rán đến khi mặt trên của bánh se lại và xuất hiện các lỗ nhỏ thì lật mặt sau rán vàng, mỗi mặt rán khoảng 3-4 phút.\r\n\r\n- Tiếp tục rán cho hết phần bột, những lần sau bạn không phải phết dầu ăn và khi rán mẻ bánh khác bạn vặn lửa nhỏ trước khi đổ bột.\r\n\r\n- Bánh sau khi rán xếp lên vỉ, phết một lớp đậu đỏ lên bề mặt bánh và dùng một miếng bánh khác, ấn nhẹ lên.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(89).jpg'),
(90, 'Bánh rán nhân mặn', '- 300g bột gạo nếp\r\n- 50g bột gạo tẻ\r\n- 1 củ khoai tây vừa ăn\r\n- Nửa thìa nhỏ muối\r\n- Phần nhân thịt: 300g thịt nạc xay, 1 lọn miến, 1 củ cà rốt nhỏ, vài tai mộc nhĩ, muối, hạt nêm, hạt tiêu, hành khô\r\n- Nước mắm, đường, giấm, ớt quả, đu đu xanh\r\n- Ớt chua ngọt.\r\n', '- Khoai tây rửa sạch, hấp chín, bóc vỏ, tán mịn.\r\n\r\n- Trộn lẫn muối, bột gạo nếp, bột gạo tẻ và khoai tây vào âu lớn, dùng tay bóp nhẹ để khoai tây tan đều.\r\n\r\n- Cho từ từ nước đun sôi khoảng 80 độ C vào âu bột, dùng muôi trộn đều. Tiếp theo dùng tay nhồi đến khi hỗn hợp bột mịn dẻo, chạm nhẹ vào bột mà không dính tay, dùng màng thực phẩm bọc kín khoảng 30 phút.\r\n\r\n- Mộc nhĩ ngâm nở, cắt bỏ chân, rửa sạch, thái nhỏ.\r\n \r\n- Cà rốt cạo vỏ, thái khoanh tròn nhỏ.\r\n\r\n- Miến ngâm vào bát nước, ngâm nở, cắt ngắn.\r\n\r\n- Trộn lẫn thịt xay hoặc băm, miến, mộc nhĩ, hành khô thái nhỏ, cà rốt, hạt tiêu, hai thìa nhỏ muối, nửa thìa nhỏ hạt nêm, ướp trong vòng 30 phút.\r\n\r\n- Dùng thìa chia hỗn hợp thịt thành từng viên nhỏ.\r\n\r\n- Dàn miếng bột mỏng, đặt nhân vào giữa và gói lại.\r\n\r\n- Lưu ý không để khoảng trống có không khí lọt vào. Làm hết phần bột và nhân.\r\n\r\n- Thả bánh vào nồi nhỏ cho bánh ngập dầu. Dùng đũa trở đều, liên tục từng chiếc.\r\n\r\n- Bánh vàng đều, vớt ra rổ nhôm để ráo dầu.\r\n\r\n- Đu đu xanh gọt vỏ, thái lát mỏng vừa ăn. Trộn vào bát đu đu xanh một ít muối, giấm, đường, ướp trong vòng 15 phút, sau đó dùng tay vắt ráo.\r\n \r\n- Ớt quả cắt nhỏ, pha hai thìa canh nước, hai thìa canh đường và một thìa canh nước mắm, một ít giấm, nêm vừa ăn. Cho đu đu xanh vào bát nước chấm.\r\n\r\n- Cắt bánh thành miếng vừa ăn, dùng kèm với nước mắm đã pha và tương ớt chua ngọt.\r\n', NULL, 12, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(90).jpg'),
(91, 'Chè bưởi', '- Đỗ xanh đã bỏ vỏ – 200 gr.\r\n- Bưởi ngon (chỉ  lấy phần vỏ bưởi) – 1 quả. \r\n- Đường – tùy thích.\r\n- Muối – 3 thìa cà phê.\r\n- Bột năng – 100 gr.\r\n- Hoa bưởi – 1 ít (nếu không có hoa bưởi có thể cho vani).\r\n- Nước cốt dừa – tùy thích.\r\n', '- Bưởi gọt bỏ vỏ xanh bên ngoài, giữ lại phần cùi trắng. Trong khi gọt, không nên gọt sát phần vỏ xanh bên ngoài để cùi bớt đánh. Phần cùi trắng các bạn xắt hạt lựu cỡ 1,5cm hoặc miếng nhỏ vừa ăn. \r\n\r\n- Ngâm vỏ vừa gọt với nước muối lần 1 trong ít nhất 2 tiếng, để qua đêm thì càng tốt. Khi ngâm, bóp khoảng 10 lần cho nước muối thấm vào cùi bưởi, sau đó bóp xả sạch với nước sạch.Làm như vậy vỏ bưởi sẽ không bị đắng.\r\n\r\n- Lần xả nước cuối cùng, không nên bóp quá kiệt nước, cùi bưởi sẽ khô, ăn không ngon. Tiếp theo, ướp cùi bưởi với một ít đường khoảng 1 tiếng cho đường ngấm vào cùi bưởi giúp cùi bưởi trong và ngọt hơn.\r\n\r\n- Sau đó, lăn khô cùi bưởi đã ướp đường với bột năng trong một tô lớn. Lớp áo bột năng sẽ giúp phần cùi bưởi giòn giòn, dai dai rất ngon. Khi nấu, cùi bưởi sẽ không bị nát.\r\n\r\n- Sau khi cùi bưởi đã được lăn bột thì đun nước thật sôi rồi thả cùi bưởi vào, đến khi cùi bưởi nổi lên mặt nước thì lấy thìa khấy nhẹ tay. Khi cùi bưởi chuyển sang màu trắng trong thì vớt ra, đổ vào một ấu nước lạnh (nước đá thì càng tốt, giúp cùi bưởi mau cứng và giòn hơn).\r\n\r\n- Ngâm cùi bưởi trong nước lạnh khoảng 15 phút rồi đổ ra để ráo nước. \r\n\r\n- Ngâm đậu xanh trong 2 giờ cho đậu nở, cho thêm một ít muối vào nước ngâm cho đậm đà. \r\n\r\n- Sau đó đem đậu xanh hấp chín, hoặc dùng lò vi sóng khoảng 10 phút (đổ ít nước lên đậu).\r\n\r\n- Tiếp theo, đun sôi một nồi nước khác, cho đường vào khuấy tan (định lượng tùy khẩu vị). Sau đó, các bạn cho đỗ xanh vào đun khoảng 10 phút.\r\n\r\n- Sau đó, hòa bột năng với nước rồi đổ từ từ vào nồi đỗ vừa đun, vừa đổ vừa khấy đều cho đến khi thấy nước chè bắt đầu sánh lại (không nên làm đặc quá).\r\n\r\n- Cho tiếp cùi bưởi vào, khuấy đều ở mức lửa nhỏ.\r\n\r\n- Cho tiếp hoa bưởi vào để tạo mùi thơm (có thể thay thế bằng tinh chất vani). Khi chè bắt đầu sôi thì tắt bếp\r\n\r\n- Cuối  cùng, múc chè ra bát, thêm chút nước cốt dừa rồi thưởng thức. Có thể ăn nóng hoặc ăn với đá mát\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(91).jpg'),
(92, 'Chè cốm', '- 100g cốm khô\r\n- 200g đường trắng\r\n- 50g bột năng\r\n- 1 củ gừng\r\n', '- Gừng gọt vỏ, rửa sạch, để ráo. Lấy 1/3 phần thái sợi, phần còn lại đem giã nhuyễn và vắt nước cốt.\r\n- Bột năng hòa tan với chút nước.\r\n\r\n- Đun sôi 800ml, cho 30g cốm và gừng thái sợi vào nấu sôi cho cốm mềm và thoảng mùi thơm của gừng.\r\n\r\n- Vặn bếp lửa nhỏ, rắc đường trắng từ từ vào nồi, nấu tan rồi cho bột năng đã hòa nước vào khuấy đều tay cho đến khi chè sánh lại, màu trong là được.\r\n\r\n- Rắc nốt phần cốm còn lại vào nồi, khuấy nhẹ tay cho đến khi cốm nở và mềm. Cuối cùng cho nước gừng vào, khuấy đều.\r\n\r\n- Múc chè cốm gừng ra bát. Món này có thể dùng nóng hay lạnh tùy theo sở thích.\r\n', '- Bạn có thể rắc thêm dừa non thái sợi vào, chè sẽ có vị bùi và béo rất ngon.\r\n- Cho thêm nước hoa bưởi hoặc va-ni để chè dậy mùi thơm.', 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(92).jpg'),
(93, 'Chè nhãn lồng hạt sen', '- 100g hạt sen tươi\r\n- 500g nhãn lồng\r\n- 100g đường phèn\r\n- 100g đường cát trắng\r\n- 1 ống va-ni\r\n', '- Hạt sen lấy tim, rửa sạch, để ráo, hấp chín.\r\n\r\n- Nhãn rửa sạch, để ráo nước, bóc vỏ, dùng dao tách bỏ phần hạt nhãn nhưng vẫn giữ nguyên hình dạng của quả nhãn, cho hạt sen vào giữ cùi nhãn.\r\n\r\n- Đun 500ml nước sôi, cho đường phèn và đường cát trắng vào nầu tan, tắt bếp, lược bỏ phần cặn có trong nước đường. Đun sôi nước đường, cho cùi nhãn có hạt sen vào nấu sôi thêm 5 phút, tắt bếp, cho va-ni vào.\r\n\r\n- Múc chè ra bát, có thể dùng nóng hoặc lạnh (để trong ngăn mát tủ lạnh) đều ngon.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(93).jpg'),
(94, 'Chè khoai mỡ cốt dừa', '- 300g khoai mỡ (khoai tím)\r\n- 200g dừa nạo\r\n- 50g lá dứa\r\n- 150g đường cát trắng\r\n- 1 ít vừng trắng\r\n', '- Khoai tím gọt vỏ, rửa sạch, để ráo nước, thái khối vuông 1,5cm. Vừng rang vàng.\r\n\r\n- Lá dứa tách từng lá, rửa sạch, bó gọn lại, nấu với 1/2 lít nước để ra hương thơm.\r\n\r\n- Cho một ít lá dứa còn nóng vào dừa nạo. Bạn nhồi mạnh tay, rồi vắt lấy 1 chén nước cốt và 500ml nước gião dừa.\r\n\r\n- Bạn cho nước gião dừa vào nồi, đun sôi, sau đó cho khoai tím vào. Nấu khoảng 10 phút (khoai đã mềm nhưng không bị nát), bạn cho đường và nước cốt dừa vào nấu thêm khoảng một phút nữa để đường tan, tắt bếp.\r\n\r\n- Cho khoai ra bát, rắc vừng lên, dùng nóng.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(94).jpg'),
(95, 'Chè Thái sữa dừa\r\n', '– Bột năng: 300gr\r\n– Lá dứa: 1 bó to\r\n– Đường: 250gr\r\n– Nước cốt dừa: 1 hộp\r\n', '–Lá dứa cho vào máy xay sinh tố xay nhuyễn. Sau đó lọc lấy nước cốt.\r\n\r\n–Hòa tan nước cốt dứa với 200gr bột năng, sau đó bóp đều cho tới khi khối bột mềm mịn.\r\n\r\n- Rải nốt 100gr bột năng lên 1 đĩa to, tiếp theo cho khối bột  lên trên đĩa rồi cán mỏng, sau đó dùng dao sắc cắt thành từng sợi mỏng dài.\r\n\r\n– Bắc nồi lên bếp, thả những sợi bột vào nồi luộc, tới khi nổi trên mặt nước thì vớt ra. Để trong nước đá.\r\n\r\n–Cho nước và đường theo khẩu vị mong muốn vào nồi, đun sôi, để nguội.\r\n\r\n–Múc từng sợi bột ra bát con, đổ nước đường vào, sau đó rưới thêm nước cốt dừa lên, nếu thích ăn lạnh thì cho thêm đá và thưởng thức.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(95).jpg'),
(96, 'Kem Socola', '– 120ml socola 60%\r\n– ¼ chén nước sôi để nguội\r\n– 1/3 tách bột cocoa không đường\r\n– ½ tách đường\r\n– 2 tách sữa\r\n– 2 tách kem tươi whipping cream\r\n– 1 chút muối\r\n– ¼ thìa café hòa tan\r\n– 3 lòng đỏ trứng đánh tan\r\n– 1 thìa café tinh chất vani\r\n', '– Đun chảy socola bằng phương pháp cách thủy.\r\n\r\n– Khi socola đã chảy hết, đổ sang một chiếc xoong vừa, đế dày, để lửa ở mức trung bình thấp, thêm nước, bột cacao và một nửa phần đường vào, đánh mạnh tay đến khi hỗn hợp đều, mịn. Tắt bếp.\r\n\r\n– Thêm sữa, một cốc kem, phần đường còn lại, café hòa tan và một chút muối vào hỗn hợp socola vừa đun chảy, đánh mạnh tay.\r\n\r\n– Khi hỗn hợp đã mịn, bật bếp, để lửa mức trung bình to, khuấy đều cho đến khi hỗn hợp bắt đầu bốc hơi.\r\n\r\n– Đánh lòng đỏ trứng trong một chiếc bát nhỏ, thật từ từ đổ 1 nửa hỗn hợp socola đang đun vào trứng, vừa đổ vừa khuấy đều.\r\n\r\n– Sau đó đổ hỗn hợp trứng vừa trộn vào phần socola đang đun còn lại, khuấy đều.\r\n\r\n– Vặn lửa vừa, đun hỗn hợp đến khi hơi sền sệt và bắt đầu bốc hơi. Tắt bếp, trộn hỗn hợp với phần kem lạnh còn lại. Lọc hỗn hợp qua rây rồi thêm tinh chất vani, khuấy đều.\r\n\r\n– Bỏ hỗn hợp vào ngăn đá tủ lạnh, khoảng vài tiếng (hoặc nên để qua đêm). Sau khi bỏ ra dùng dụng cụ múc kem ra cốc hoặc đĩa, dưới một chút siro hoặc các loại hạt khô lên trên để kem thơm ngon hơn.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(96).jpg'),
(97, 'Kem sữa gạo', '– 10gr bột gạo nếp.\r\n– 470ml nước.\r\n– 80ml sữa tươi.\r\n– 3 tbsp đường.\r\n– ½ tsp muối.\r\n– Vài miếng vỏ cam hoặc chanh.\r\n– Tô, muỗng, nồi, khuôn kem, que gỗ.\r\n', '– Khuấy đều bột gạo nếp với 20ml nước cho đến khi thấy hỗn hợp hoàn toàn mịn mượt, không bị lợn cợn.\r\n\r\n– Cho nốt 450ml nước còn lại vào 1 cái nồi đáy dày, đặt nồi nước lên bếp đun sôi rồi đổ sữa và đường vào khuấy đều cho đường tan hết.\r\n\r\n– Đổ từ từ hỗn hợp bột gạo nếp ở bước 1 vào nồi hỗn hợp sữa đường, vừa đổ vừa khuấy đều.\r\n\r\n– Khi hỗn hợp ở bước 3 sôi lại thì cho muối vào, khuấy đều rồi tắt bếp.\r\n\r\n– Chờ hỗn hợp kem sữa gạo nguội hoàn toàn thì rót vào khuôn kem.\r\n\r\n– Bạn xiên que gỗ qua vỏ cam hoặc chanh rồi mới chèn lên trên nắp khuôn để giữ cho que gỗ thẳng đúng và giúp cho kem cho kem có hương cam, chanh thơm mát, dễ chịu.\r\n\r\n– Đặt khuôn kem vào trong ngăn đá tủ lạnh ít nhất 4 tiếng để kem đông lại hoàn toàn là có thể lấy ra thưởng thức!\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(97).jpg'),
(98, 'Kem Tequila', '– 240 ml whipping cream\r\n– 2 tsp nước cốt chanh\r\n– 1 tsp vỏ chanh bào vụn\r\n– 1 tbsp rượu Tequila\r\n– 1 viên kem \r\n– Bát lớn, nhỏ\r\n– Máy đánh trứng\r\n– Thìa, kẹp múc kem, …\r\n– Ly thủy tinh \r\n', '– Trong một cái bát lớn, bạn cho whipping cream, nước cốt chanh và ½ tsp rượu vào đó. Sử dụng máy đánh trứng đánh bông đến khi kem thơm mùi rượu thì rót ra một cái ly.\r\n\r\n– Bây giờ, hãy đặt 1 viên kem vào cái ly đó.\r\n\r\n– Hãy làm thật từ từ bằng việc rót 1 tsp rượu còn lại vào ly kem.\r\n\r\n– Sau cùng, hãy rắc chanh bào lên trên để trang trí cho ly kem thêm đẹp mắt.\r\n', '– Để thành phẩm được ly kem Tequila đúng vị, cần để lạnh rượu Tequila trước khi tiến hành làm kem.\r\n– Nên chọn viên kem có hương vị nhẹ nhàng tránh cho việc hương vị kem quá nồng sẽ át đi vị rượu Tequila. Nên sử dụng kem vani hoặc kem sữa dừa.', 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(98).jpg'),
(99, 'Kem trà xanh', '– 2 cup half & half (có thể thay thế bằng 1 cup sữa tươi + 1 cup heavy cream)\r\n– 3 tbsp matcha ( bột trà xanh)\r\n– ½ cup đường\r\n– 1 nhúm muối\r\n– Máy làm kem\r\n– Xoong lớn\r\n', '– Đánh half and half ( hoặc 1 cup sữa tươi + 1 cup heavy cream), bột matcha, đường và muối trong một chiếc xoong lớn. Đun sôi hỗn hợp. Khuấy đều tay đến khi hỗn hợp có bắt đầu có bọt thì tắt bếp.\r\n\r\n– Đợi hỗn hợp nguội rồi bỏ hỗn hợp vào tủ lạnh cho đến khi hỗn hợp lạnh hoàn toàn, có nhiệt độ khoảng 4 độ C.\r\n\r\n– Đổ hỗn hợp vào máy làm kem và vận hành máy theo hướng dẫn của nhà sản xuất.\r\n\r\n– Bỏ kem vào tủ lạnh ít nhất 3 giờ để kem được đông lạnh hoàn toàn.\r\n\r\n– Khi dùng, dùng thìa múc kem, xúc kem thành những viên kem cho vào ly và thưởng thức.\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(99).jpg'),
(100, 'Kem xoài chanh dây', '– 3 quả xoài chín.\r\n– 2 hộp sữa chua.\r\n– 20ml sữa đặc.\r\n– 100gr đường.\r\n– 160ml nước.\r\n– 2 quả chanh dây.\r\n– Nồi, dao, rây, máy xay sính tố, khuôn kem.\r\n', '– Hòa tan đường và nước lại với nhau trong 1 cái nồi nhỏ, sau đó đặt lên bếp đun cho đến khi thấy hỗn hợp nước đường sôi lăn tăn và sánh lại thì tắt bếp. Để nước đường nguội bớt rồi đặt vào trong ngăn mát tủ lạnh.\r\n\r\n– Xoài gọt vỏ, bỏ hạt, cắt lấy phần thịt xoài.\r\n\r\n– Chanh dây cắt đôi, lọc phần ruột chanh dây qua rây để thu được nước cốt, loại bỏ hạt.\r\n\r\n– Cho xoài, nước đường lạnh, sữa chua, sữa đặc và nước cốt chanh dây vào mát xay sinh tố, xay cho hỗn hợp thật nhuyễn mịn.\r\n\r\n– Cho hỗn hợp kem đã xay vào khuôn và để vào trong ngăn đá tủ lạnh khoảng 3 – 4 tiếng cho kem đông lại hoàn toàn là có thể lấy ra thưởng thức!\r\n', NULL, 13, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(100).jpg'),
(101, 'Canh bắp cải gói thịt', '- Thịt xay: 150g\r\n- Giò sống: 50g\r\n- Nước hầm xương: 1 lít\r\n- Bắp cải trắng: 1 cái\r\n- Nấm mèo: 2 tai\r\n- Nấm hương: 5 tai\r\n- Cà rốt: 1 củ\r\n- Hành lá, ngò rí\r\n- Muối, tiêu, hành phi, nước mắm, hạt nêm\r\n', '- Bắp cải tách lấy lá, chần qua trong nước sôi có nêm 1 ít muối. Hành lá rửa sạch, đầu hành băm nhuyễn, lá hành chần sơ qua nước sôi.\r\n\r\n- Nấm mèo ngâm nở, cắt sợi, ướp với 1 ít tiêu. Nấm hương ngâm nở, cắt sợi. Cà rốt gọt vỏ, 50g tỉa bông, cắt lát, 30g cắt sợi. Ngò rí rửa sạch, cắt nhỏ, để riêng cọng và lá.\r\n \r\n- Trộn nhân: cho thịt xay, 1M cà rốt cắt sợi, 1M nấm mèo, 1M nấm hương, 1m hành phi, 1m đầu hành và cọng ngò rí băm vào tô, nêm 1m hạt nêm, 1/3m muối và 1/3m tiêu, trộn đều, cuối cùng cho giò sống vào, dùng muỗng tán đều.\r\n\r\n- Dùng lá bắp cải gói nhân lại, đặt hoa cà rốt lên trên, dùng cọng hành cột lại.\r\n\r\n- Đun sôi nước dùng, cho gói bắp cải vào nấu, nêm 1M hạt nêm , 1/2M nước mắm và 1/2m tiêu, nấu đến khi gói bắp cải chín, tắt bếp.\r\n \r\n- Múc canh ra tô, rắc hành ngò lên trên, dùng nóng với cơm.\r\n', '- Chọn bắp cải Đà Lạt loại lớn vì gân lá nhỏ, dễ gói hơn.\r\n- Nếu không có thời gian nấu nước dùng, nên sử dụng 1,5M hạt nêm  cho 1 lít nước.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(101).jpg'),
(102, 'Canh bí ngòi nấu nấm', '- Sườn non: 200g\r\n- Bí ngòi: 1 trái\r\n- Nấm đông cô: 5 tai\r\n- Cà rốt: 1/2 củ\r\n- Đậu hũ non: 1 miếng\r\n- Hành lá, ngò rí\r\n- Tiêu\r\n- Hạt nêm\r\n', '- Sườn non rửa sạch, chặt miếng vừa ăn, để ráo.\r\n\r\n- Bí ngòi cắt miếng vừa ăn. Cà rốt tỉa hoa, cắt lát mỏng. Nấm đông cô cắt 4. Đậu hũ non cắt khối vuông. Hành ngò cắt nhỏ.\r\n\r\n- Nấu sườn non, cà rốt, nấm đông cô với 1,5 lít nước khoảng 10 phút, sau đó cho bí ngòi vào nấu, nêm 2M Hạt nêm, cuối cùng cho đậu hũ. Khi sôi rắc hành ngò, tiêu rồi tắt bếp.\r\n \r\n- Múc canh ra tô, dùng nóng với cơm.\r\n', '- Chọn bí ngòi còn tươi, da căng láng, xanh mượt, nặng tay thì bí sẽ chắc và ngọt.\r\n- Cho bí ngòi vào sau cùng để giữ được chất dinh dưỡng và bí không bị mềm.\r\n- Tùy theo ý thích có thể thêm bông cải, bắp non, canh vẫn rất ngon.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(102).jpg'),
(103, 'Canh chua bắp cải tím', '- Bắp cải tím: 200g\r\n- Tép: 150g\r\n- Thơm: 1/4 trái\r\n- Cà chua: 1 trái\r\n- Nước cốt me: 1/2 chén\r\n- Rau om, ngò gai, ớt hiểm\r\n- Hành tím\r\n- Tỏi phi có dầu: 2M\r\n- Muối, đường, nước mắm, dầu ăn, bột ngọt\r\n', '- Tép rửa sạch, để ráo.\r\n\r\n- Bắp cải tách lá, cắt quả trám khoảng 2cm. Cà chua cắt múi cau. Thơm cắt lát. Hành tím cắt lát mỏng. Ngò gai, rau om cắt khúc ngắn. Ớt cắt lát.\r\n \r\n- Phi thơm hành tím với 2M dầu ăn, xào thơm tép, cho tiếp thơm và cà chua vào đảo đều. Cho vào 1,2 lít nước đun sôi, nêm 1/2 chén nước cốt me, 3.5M đường, 1M muối, 1.5M bột ngọt  và 1M nước mắm, sau đó cho bắp cải vào nấu vừa chín, thêm rau om, ngò gai, ớt và tỏi phi vào, tắt lửa.\r\n \r\n- Cho canh ra tô, dùng nóng, kèm nước mắm và ớt cắt lát.\r\n', '- Phi thơm hành tím để khử mùi tanh của tép.\r\n- Cho bắp cải vào sau khi nêm gia vị để giữ độ giòn của bắp cải.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(103).jpg'),
(104, 'Canh hến nấu bầu', '- Thịt hến: 150g\r\n- Bầu: 300g\r\n- Cà rốt: 50g\r\n- Hành lá: 2 cây\r\n- Thì là: 2 cây\r\n- Hành tím băm: 1M\r\n- Muối, tiêu, nước mắm, dầu ăn\r\n- Bột ngọt \r\n', '- Thịt hến ướp với 1/2m muối, 1/2m tiêu và 1/2m bột ngọt, để thấm. Phi thơm hành tím băm, cho hến vào xào sơ.\r\n\r\n- Bầu gọt vỏ, băm sợi. Cà rốt bào mỏng, cắt sợi. Hành lá, thì là rửa sạch, cắt nhỏ.\r\n \r\n- Nước luộc hến lấy phần nước trong, đun sôi , cho cà rốt vào, đợi sôi lại cho bầu vào, bầu chín cho hến vào, nêm 1m muối và 1m bột ngọt, thêm hành lá, thì là và tiêu, tắt bếp.\r\n \r\n- Múc canh bầu nấu hến ra tô, dùng nóng với cơm.\r\n \r\n', '- Chọn bầu sao có da màu xanh đậm lẫn những đốm trắng, chọn trái thon sẽ ít ruột hơn. \r\n- Không nấu hến quá lâu để hến không bị dai và giữ được vị ngọt.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(104).jpg'),
(105, 'Canh mồng tơi bắp nụ', '- Đọt mồng tơi : 300g\r\n- Tôm sú bóc vỏ, chừa đuôi : 150g\r\n- Bắp nụ nhỏ : 100g\r\n- Giò sống : 50g\r\n- Hành tím băm\r\n- Muối, tiêu, nước mắm\r\n- Hạt nêm, bột ngọt \r\n', '- Mồng tơi rửa sạch, cắt khúc 4cm. Bắp nụ cắt bỏ cuống.\r\n\r\n- Lấy 100g tôm đập dập, trộn chung với giò sống, 1m hành tím băm, 1/3 m tiêu, 1/4m bột ngọt, vo viên rồi xiên vào trái bắp non. Tôm còn lại chẻ lưng ướp một ít bột ngọt, lộn quả đào.\r\n \r\n\r\n- Đun sôi 1.2 lít nước, cho bắp vào nấu sôi 3 phút, cho rau mồng tơi vào, nêm 1m muối, 2/3m bột ngọt, 1m hạt nêm , cho tôm vào nấu chín, thêm 1/2M nước mắm.\r\n \r\n- Cho canh ra tô, rắc tiêu lên trên, dùng nóng.\r\n', '- Chọn bắp nụ tươi, trái nhỏ. Chọn mồng tơi đọt non, tược vươn ra, lá nhỏ, màu xanh sáng.\r\n- Cho bắp vào nấu trước để bắp chín và tiết ra nước ngọt.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(105).jpg'),
(106, 'Canh nấm rong biển', '- Tôm sú: 150g\r\n- Giò sống: 100g\r\n- Nấm kim châm: 100g\r\n- Nấm hương: 100g\r\n- Nấm rơm: 50\r\n- Cà rốt: 50g\r\n- Rong biển: 1 lá\r\n- Tần ô: 4 cây\r\n- Nước dùng gà: 1 lít\r\n- Hành lá, ngò rí, bột bắp, ớt, muối, tiêu\r\n- Nước tương, hạt nêm, bột ngọt\r\n', '- Tôm chẻ lưng, rút bỏ chỉ lưng, chần qua nước sôi sau đó bóc vỏ, lấy 3 con cắt hạt lựu.\r\n\r\n- Nấm hương, nấm rơm ngâm nước gạo. Nấm hương cắt chân, rửa sạch, ép nhẹ cho ráo, rắc ít hạt nêm, tiêu và bột bắp vào nấm. Quết giò sống lên nấm hương, cho 1 miếng tôm vào giữa rồi thoa 1 ít bột bắp lên mặt nấm. Nấm kim châm cắt bỏ phần chân, tách ra, cắt đôi.\r\n\r\n- Rong biển cắt sợi. Hành lá, ngò rí cắt nhỏ. Cà rốt tỉa hoa, cắt lát mỏng. Rau tần ô cắt khúc ngắn. Ớt cắt lát.\r\n \r\n- Đun sôi nước dùng, cho cà rốt, nấm hương, nấm rơm, nấm kim châm và phần tôm còn lại vào, nêm 1m muối và 1/2m bột ngọt, tắt bếp. Cho rong biển và rau tần ô, hành lá, ngò rí vào, rắc thêm tiêu.\r\n \r\n- Múc canh ra tô, ăn kèm nước tương  và ớt cắt lát, dùng nóng.\r\n', '- Rắc bột bắp 2 lần để giữ được nước ngọt của nấm và giò.\r\n- Cho rau và rong biển vào sau cùng để giữ được màu xanh tươi và mùi thơm.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(106).jpg'),
(107, 'Canh nghêu nấu nấm kim châm', '- Nghêu : 500g\r\n- Đậu hũ non : 1 miếng\r\n- Nấm kim châm : 200g\r\n- Nấm rơm búp : 100g\r\n- Tía tô : 1 nắm\r\n- Ớt hiểm : 2 trái\r\n- Gừng : 1 củ nhỏ\r\n- Sa tế\r\n- Bột ngọt, hạt nêm\r\n', '- Nghêu luộc với gừng đập dập, nghêu há vỏ vớt ra lấy thịt để riêng, nước luộc nghêu gạn bỏ cát và đong thêm cho đủ 1.2 lít nước.\r\n\r\n- Nấm kim châm cắt khúc 5cm. Nấm rơm chần sơ. Đậu hũ cắt miếng vuông cạnh 1cm. Tía tô cắt sợi.\r\n \r\n- Nấu sôi nước luộc nghêu, cho nấm rơm, đậu hũ và ớt hiểm đập dập vào, nêm 2M hạt nêm, 1m bột ngọt, 1m sa tế, cho tiếp nghêu, nấm kim châm vào cuối cùng thêm lá tía tô, tắt lửa.\r\n \r\n- Cho canh nghêu ra tô, dùng nóng với cơm.\r\n', '- Nấm kim châm rất mau chín nên khi nấu canh bỏ nấm vào đảo đều rồi nhắc xuống nhanh để giữ độ giòn.\r\n- Chọn nấm rơm búp, đều cỡ để nấm chín đều, giòn và ngon hơn.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(107).jpg'),
(108, 'Canh sường non táo đỏ', '- Sườn non : 300g\r\n- Táo đỏ (táo tàu) : 50g\r\n- Cà rốt : 1 củ\r\n- Bắp Mỹ : 1/3 trái\r\n- Hành ngò\r\n- Muối, đường\r\n- Hạt nêm,  bột ngọt\r\n', '- Sườn non rửa sạch, chặt khúc vừa ăn, ướp 1/3m muối, 1/2m bột ngọt.\r\n\r\n- Bắp Mỹ cắt khúc. Cà rốt tỉa hoa, cắt lát. Hành ngò cắt nhuyễn. Táo đỏ ngâm nở.\r\n \r\n- Cho sườn vào hầm với 1.2 lít nước, nước sôi cho táo đỏ, bắp và cà rốt vào nêm 1m đường, 1m muối, 1 m hạt nêm, 1m bột ngọt  nấu đến khi các loại rau củ chín, tắt lửa, cho hành ngò vào.\r\n \r\n- Cho canh sườn ra tô, ăn kèm với cơm.\r\n', '- Ngâm táo đỏ trước để táo sạch và nước dùng không bị sẫm màu.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(108).jpg'),
(109, 'Canh thanh long', '- Tôm tươi bóc vỏ: 150g\r\n- Thanh long: 1/2 trái (150g)\r\n- Khoai tây: 1/2 củ\r\n- Cà rốt: 50g\r\n- Hành lá, ngò rí\r\n- Nước mắm, tiêu, muối, đường\r\n- Bột ngọt\r\n', '- Ngò rí, hành lá cắt nhỏ.\r\n\r\n- Cà rốt tỉa hoa cắt mỏng. Khoai tây cắt khối. Thanh long cắt khối, ướp 1,5m đuờng.\r\n\r\n- Tôm tươi đập dập, băm nhuyễn. Ướp 1/2m Bột ngọt, 1/3m muối, 1/4m tiêu, 1m hành lá cắt nhỏ. Vo tôm thành viên tròn.\r\n \r\n- Đặt nồi nước lên bếp, nấu sôi, cho tôm viên vào. Nấu lửa nhỏ, vớt bọt thường xuyên.\r\n\r\n- Cho khoai tây và cà rốt vào nấu chín. Cuối cùng cho thanh long vào, nấu sôi khoảng 2 phút. Nêm thêm 1m Bột ngọt, 1m muối. Tắt lửa. Thêm hành lá, ngò rí.\r\n \r\n- Múc canh ra tô, rắc thêm tiêu, dùng nóng.\r\n', '- Nấu nước dùng ta nên vớt bọt thường xuyên để nước trong.\r\n- Ướp thanh long với đường để cân bằng vị chua trong thanh long.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(109).jpg'),
(110, 'Canh tôm rong biển', '- Tôm sú tươi (lột vỏ): 100g\r\n- Đậu hũ non: 1 miếng\r\n- Rong biển khô (Wakame): 2M\r\n- Cà rốt: 20g\r\n- Cải ngồng: 100g\r\n- Gừng, tiêu\r\n- Hạt nêm\r\n- Gia vị nêm sẵn Canh rau củ\r\n \r\n', '- Tôm bóc vỏ, bỏ chỉ lưng, chừa đuôi, rửa sạch. Ướp 1/2m hạt nêm.\r\n\r\n- Đậu hũ cắt miếng vuông cạnh 1cm. Rong biển rửa sạch, ngâm nở, vớt để ráo.\r\n\r\n- Cải ngồng cắt khúc 4cm. Cà rốt cắt sợi. Gừng cắt sợi.\r\n \r\n- Đun sôi 1 lít nước, cho gói Gia vị nêm sẵn Canh rau củ vào và nấu tan gia vị, thêm gừng cắt sợi. Tiếp tục cho tôm, cà rốt, cải ngồng và đậu hũ vào nấu sôi, cho rong biển vào đảo nhanh. Tắt lửa.\r\n \r\n- Múc canh ra tô, rắc thêm tiêu.\r\n', '- Rong biển có mùi đặc trưng của biển, có thể kết hợp với gừng giúp món ăn thêm ngon.\r\n- Cho rong biển vào sau cùng để giữ độ giòn, tránh bị mềm nhũn.', 4, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(110).jpg'),
(111, 'Cháo bắp bò hạt sen', '- 500g thịt bò bắp, có thể dùng bắp chân trước của thịt bò\r\n- 150g hạt sen\r\n- 1/3 bát con gạo tẻ\r\n- 1/4 bát con đỗ canh đã cà vỏ\r\n- Muối, hạt nêm, rau răm, hành hoa, rau mùi và tiêu.\r\n', '- Bắp bò rửa sạch, để vào ngăn đá tủ lạnh khoảng 30 - 45 phút cho thịt hơi se cứng lại, sau đó lấy ra thái lát vừa ăn.\r\n\r\n- Sau đó đổ nước lạnh ngập mặt thịt, thêm hai thìa nhỏ muối, đun sôi, ninh thịt bò đến khi mềm. Nếu cạn nước bạn châm vào một ít nước lọc.\r\n\r\n- Đỗ xanh đãi vài lần với nước cho sạch, ngâm đỗ khoảng 30 phút đến 1 tiếng trong nước ấm.\r\n\r\n- Hạt sen khô rửa sạch, nếu dùng sen tươi bạn nhớ bỏ hết tâm sen cho sạch, đun nồi nước sôi, thả hạt sen vào đun khoảng từ 15 - 20 phút thì cho đỗ xanh vào đun cùng đến khi đỗ xanh và hạt sen mềm.\r\n\r\n- Gạo đãi sạch, để lên rổ cho ráo nước.\r\n\r\n- Đun nóng chảo, đổ gạo vào rang đến khi hạt gạo se lại. Tiếp theo đổ gạo vào nồi nhỏ, đổ nước lạnh xâm xấp với mặt gạo, đun sôi đến khi hạt gạo nở bung ra, bạn đổ phần hạt gạo đã nở bung ra rổ, đổ bỏ phần nước lạnh đun cùng với gạo.\r\n\r\n- Sau đó bạn đổ phần thịt bò bắp và nước dùng đã ninh thịt bò, thêm đỗ xanh, hạt sen vào nồi lớn, thêm gạo (lấy luôn phần nước đã đun đỗ xanh và hạt sen), đun sôi, nêm nếm gia vị lại tùy theo sở thích của bạn, đun lửa nhỏ đến khi hạt gạo mềm.\r\n\r\n- Rau răm, hành hoa, rau mùi rửa sạch, thái nhỏ.\r\n\r\n- Cuối cùng, tắt bếp, thêm rau răm, hành hoa, rau mùi thái nhỏ vào nồi cháo, tắt bếp, múc ra bát, rắc một ít hạt tiêu lên bề mặt, dùng nóng.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(111).jpg'),
(112, 'Cháo cá hồi', '- Cá hồi 220 g\r\n- 10 g đậu xanh\r\n- 10 g hạt sen khô (hoặc tươi)\r\n- 70 g gạo ngon, 10 g gạo nếp\r\n- 1,5 l nước hầm xương gà \r\n- 2 củ hành tím và 1 khúc tỏi tây\r\n- 15 g muối, 13 g bột nêm\r\n- 12 g đường phèn (dùng đường phèn để món ăn ngọt thanh, không nên dùng đường cát)\r\n- Nước mắm, dầu mè\r\n- Lá chanh, ngò rí, gừng, hành lá\r\n', '- Hạt sen khô ngâm nước cho mềm.\r\n\r\n- Gạo vo sạch, để ráo nước. Sau đó rang sơ phần gạo nếp.\r\n\r\n- Tỏi tây rửa sạch, nướng sơ.\r\n\r\n- Cho gạo và gạo nếp cùng đậu xanh, hạt sen cùng 1,5 l nước hầm xương gà vào một chiếc nồi đất. Sau đó bắc nồi đất lên bếp đun nhỏ lửa cho cháo nhừ, lưu ý cần hớt hết bọt cho nồi cháo trong.\r\n\r\n- Hành tím và tỏi tây thái nhỏ, cho vào nồi cho cháo thơm ngon.\r\n\r\n- Cá hồi rửa sạch cho vào nồi, nêm 1 muỗng cà phê nước mắm ngon, 1 muỗng cà phê dầu mè, 1/2 muỗng cà phê hạt nêm, 1 lát gừng để trên mặt, 1 củ hành tím thái nhỏ, 3 lá chanh, 2 gốc ngò rí rồi đem hấp 15 phút.\r\n\r\n- Khi cá chín để nguội rồi gỡ thịt. Cá hấp theo kiểu này rất thơm, ngon mà không bị tanh. Các bạn cũng có thể dùng cách này hấp cá để ăn cùng cơm hay cuốn cùng các loại rau.\r\n\r\n- Khi cháo gần nhừ nêm theo khẩu vị, kiểm tra độ lỏng, đặc cho vừa ý. Ai thích cháo lỏng hơn nhớ cho thêm nước.\r\n', '- Trong quá trình nấu cháo, thường xuyên dùng vá quấy theo chiều kim đồng hồ, cháo rất mau nhừ.', 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(112).jpg'),
(113, 'Cháo cá', '- 1 con cá (cá chép hoặc cá quả) bạn cũng có thể dùng 2 khúc cá cũng được\r\n- 1 nhánh gừng\r\n- ½ bát gạo\r\n- ½ bát đậu Hà Lan\r\n- Hành xanh, nước mắm\r\n', '- Cá rửa sạch, tưới chút dấm rửa qua cho cá khỏi tanh.\r\n\r\n- Cho cá vào nồi nước, thêm nhánh gừng đập dập, luộc chín. Vớt cá ra gỡ lấy thịt, gỡ cẩn thận để tránh xương.\r\n\r\n- Nước luộc cá lọc qua 1 cái rây để loại bỏ những cái vảy và xương sót lại.\r\n\r\n- Vo gạo rồi cho vào nồi nước luộc cá nấu. Ninh cháo nhỏ lửa đến khi cháo chín nhừ.\r\n\r\n- Thịt cá có thể xào qua với chút hành tím phi thơm, nêm nước mắm, hạt tiêu cho đậm đà. Nếu không thích bạn có thể bỏ qua bước này mà cho thịt cá vào nồi luôn sau khi cháo chín.\r\n\r\n- Cho đậu Hà Lan vào tiếp.\r\n\r\n- Nêm nếm chút hạt nêm cho cháo vừa ăn, chỉ nên nhàn nhạt để còn giữ được vị ngọt của cá.\r\n\r\n- Đun đến khi đậu Hà Lan chín thì tắt bếp.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(113).jpg'),
(114, 'Cháo gà gạo nếp', '- Gà - 1 con 1kg\r\n- Cơm gạo nếp - 2 bát ăn\r\n- Hành lá, xắt khúc ngắn - vài cọng\r\n- Cà rốt, thái hạt lựu - 1/2 củ\r\n- Muối, hạt tiêu vừa ăn\r\n- Gia vị luộc gà\r\n- Gừng tươi - 1 củ nhỏ\r\n- Tỏi - 2 tép\r\n- Rượu sake - 1/3 chén\r\n- Tỏi tây (lấy phần gốc trắng - 2 cây\r\n- Táo tàu - 6 trái.\r\n', '- Gà làm sạch để ráo nước. Gạo nếp ngâm nước lạnh khoảng 1 giờ.\r\n\r\n- Đổ nước vào trong nồi lớn, đặt con gà vào, cho gia vị luộc gà: củ gừng, tép tỏi, tỏi tây, táo tàu, rượu sake vào đun sôi. Sau khi nước sôi thì giảm lửa nhỏ đun khoảng 40 phút cho gà chín mềm.\r\n\r\n- Vớt gà ra để nguội và xé sợi nhỏ. Phần nước còn lại nấu sôi rồi cho gạo nếp đã ngâm vào đun khoảng 20 phút cho cháo nhừ.\r\n\r\n- Tiếp theo cho gà đã xé nhỏ vào nồi và nêm gia vị vừa miệng.\r\n\r\n- Cuối cùng cho cà rốt và hành lá vào nấu đến khi cà rốt chín mềm thì tắt bếp.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(114).jpg'),
(115, 'Cháo gà hạt sen', '- 200g thịt gà\r\n- 100g hạt sen\r\n- 100g gạo tẻ thơm\r\n- 30g gạo nếp \r\n-30g đậu xanh và một củ cà rốt nhỏ \r\n- Hành lá, tía tô, hạt tiêu và nước mắm ngon, bột nêm, dầu ăn, hành tím\r\n', '- Ngâm hạt sen và đậu xanh đã bỏ vỏ trong 2 tiếng cho nở.\r\n\r\n- Gạo tẻ và gạo nếp trộn chung lại, vo sạch rồi ngâm khoảng 20 phút. Nếu bạn nấu cháo cho bé thì có thể giã cho gạo dập bớt. Gạo nếp nấu cùng trong cháo gà hạt sen sẽ khiếp món cháo thơm hơn rất nhiều.\r\n\r\n- Thịt gà luộc chín, để nguội, xé nhỏ, giữ lại nồi nước luộc gà.\r\n\r\n- Bỏ gạo cùng đậu xanh và hạt sen vào nồi nước luộc gà. Đợi cháo sôi, bạn vặn nhỏ lửa, cho gia vị rồi nấu chừng 40 phút cho cháo chín nhuyễn.\r\n\r\n- Phi hành tím cho vàng, thơm rồi trút vào nồi cháo đã nhuyễn, nêm lại cho vừa ăn rồi tắt bếp.\r\n\r\n- Múc cháo gà hạt sen ra tô, bỏ thịt gà rồi đến hành lá, hạt tiêu lên trên là có thể thưởng thức.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(115).jpg'),
(116, 'Cháo hột vịt Bắc thảo nấu tôm', '- Xương heo (nấu lấy nước dùng)\r\n- Tôm lột vỏ lấy chỉ đen, bỏ đầu hấp chín\r\n- Hột vịt bắc thảo lột vỏ cắt nhỏ tùy ý thích\r\n- Ít hành, ngò\r\n- Ít gừng cắt sợi\r\n- Vài củ hành tím nướng sơ lột vỏ rửa sạch\r\n- Gạo + pha ít nếp\r\n- Gia vị: hạt nêm, muối, đường, tiêu xay\r\n', '- Vo gạo + ít nếp cho sạch bỏ vào nồi nước dùng, nêm ít muối + vài củ hành tím nướng sơ.\r\n\r\n- Ninh cho hạt gạo và nếp nở nhừ như ý thích (vớt củ hành tím ra bỏ) sau đó ta cho tôm + hột vịt bắc thảo + ít hạt nêm, đường vào nêm nếm lại cho vừa ăn và để nồi cháo sôi lại khoảng 5 phút thì tắt lửa (để lâu tôm chín quá ăn không ngon).\r\n\r\n- Múc cháo ra tô, cho hành lá + ngò + gừng lên trên và rắc tiêu là có thể dùng. \r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(116).jpg'),
(117, 'Cháo nấm bào ngư', '- Thịt heo: 300 gr\r\n-Nấm bào ngư: 500 gr\r\n- Gạo tẻ: ½ bát\r\n-Hành tím: 1 củ\r\n- Hành lá, rau mùi\r\n- Muối, đường, tiêu, nước mắm, dầu ăn.\r\n', '- Thịt mua về rửa sạch, dùng dao thái miếng nhỏ vừa ăn hoặc băm nhỏ rồi cho ra bát. Cho một chút muối, hạt tiêu vào thịt, đảo đều lên để ướp cho thịt ngấm gia vị.\r\n\r\n-  Đem nấm bào ngư ngâm với muối, rửa sạch với nước rồi dùng dao cắt bỏ chân. Đem nấm ướp với hành tím băm nhỏ cùng các gia vị cho vừa ăn.\r\n\r\n-  Hành lá, rau mùi nhặt sạch, rửa với nước rồi dùng dao thái nhỏ.\r\n\r\n- Cho gạo vào nồi ninh cháo, khi gạo gần chín thì cho thịt heo vào rồi tiếp tục cho nấm vào nồi ninh. Nêm nếm lại gia vị cho nồi cháo vừa miệng và ninh đến khi cháo chín.\r\n\r\n-  Sau khi cháo đã chín, tắt bếp và cho thêm hành lá, rau mùi, tiêu vào nồi cháo là hoàn thành xong món ăn.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(117).jpg'),
(118, 'Cháo rau ngót đậu hũ', '- Gạo tẻ 30g\r\n- Đậu hũ miếng nhỏ\r\n- Rau ngót 30g.\r\n', '- Gạo tẻ nấu chín thành cháo trắng đặc, mịn. \r\n\r\n- Đậu hũ nghiền nát, \r\n\r\n- Rau ngót làm sạch, xay nhuyễn hoặc xắt nhỏ. \r\n\r\n- Khi nồi cháo đang sôi, cho rau ngót vào nấu chín, nêm gia vị vừa đủ, tiếp tục trút hết đậu vào nồi cháo, đảo đều.\r\n\r\n- Nêm gia vị lần cuối và tắt bếp.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(118).jpg'),
(119, 'Cháo thịt bò nấu cà rốt phô mai', '- Thịt bò 30g (thịt bò thăn)\r\n - Cà rốt 30g\r\n- Phô mai 1-2 miếng nhỏ.\r\n', '- Thịt bò sơ chế, băm nhỏ hoặc xay nhuyễn. \r\n\r\n- Cà rốt cắt hạt lựu.\r\n\r\n- Hành trắng đập dập, phi thơm, trút thịt bò xào sơ qua, nêm gia vị, tắt bếp. \r\n\r\n- Gạo trắng nấu thành cháo trắng đặc, sau đó cho thêm cà rốt vào nấu nhừ khoảng 5-7 phút. \r\n\r\n- Khi cháo trắng cà rốt sánh, mịn, mẹ cho thịt bò vào, đảo đều, nêm gia vị, tắt bếp. \r\n\r\n- Múc cháo ra bát, cho phô mai vào đánh tan.\r\n\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(119).jpg'),
(120, 'Cháo thịt đậu xanh', '- Gạo nếp - 100g\r\n- Gạo tẻ - 30g\r\n- Đậu xanh đã cà vỏ - 50g\r\n- Xương hom hoặc xương ống - 200g\r\n- Thịt nạc heo xay - 100g\r\n- Hành lá, tía tô\r\n- Nước mắm, gia vị, hành khô, dầu ăn, hạt tiêu.\r\n', '- Gạo nếp và gạo tẻ vo sạch rồi đổ ra rá cho thật ráo nước.\r\n\r\n- Đậu xanh cũng đãi sạch rồi để ráo nước.\r\n\r\n- Cho xương vào nồi luộc đến khi sôi rồi chắt bỏ nước.\r\n\r\n- Rửa lại xương thật sạch rồi cho vào nồi xào với chút gia vị.\r\n\r\n- Đổ nước vào nồi xương, lượng nước bạn ước chừng gấp khoảng 4 lần lượng gạo.\r\n\r\n- Khi nước nóng già thì cho gạo và đậu xanh vào, đun sôi rồi hớt bớt phần bọt đen. Không động đũa cứ thế ninh đến khi cháo nhừ nhuyễn.\r\n\r\n- Thịt băm ướp với nước mắm, gia vị. Phi thơm hành khô. Sau đó cho thịt vào xào săn.\r\n\r\n- Khi thịt chín thì rắc chút hạt tiêu vào đảo đều rồi tắt bếp.\r\n\r\n- Hành lá, tía tô thái nhỏ. Cháo chín múc ra bát.\r\n\r\n- Sau đó cho thịt, hành và tía tô lên trên.\r\n\r\n- Cháo dùng nóng, có thể ăn kèm quẩy nếu thích.\r\n', NULL, 2, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(120).jpg'),
(121, 'Canh nấm hạt sen', '- Hạt sen : 50g\r\n- Nấm đông cô tươi : 50g\r\n- Nấm linh chi : 50g\r\n- Cà rốt : 1/2 cây\r\n- Đậu hũ non : 100g\r\n- Ngò rí\r\n- Tiêu\r\n- Hạt nêm từ Nấm Hương và Hạt Sen\r\n', '- Cà rốt tỉa hoa, cắt lát. Nấm các loại sơ chế sạch, để ráo. Ngò rí cắt nhỏ.\r\n\r\n- Đậu hũ non cắt miếng vừa ăn.\r\n \r\n- Đun sôi 1 lít nước, cho hạt sen và cà rốt vào nấu khoảng 10 phút, tiếp tục cho các loại nấm vào nấu chín, nêm 3m hạt nêm từ nấm hương và hạt sen, cho đậu hũ vào tắt bếp, rắc thêm tiêu và ngò rí.\r\n \r\n- Múc canh ra tô, dùng nóng với cơm.\r\n', '- Chọn hạt sen tươi để tăng hương vị cho món ăn và tiết kiệm thời gian nấu.\r\n- Cho đậu hũ vào giai đoạn sau cùng để đậu hũ không bị nát.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(121).jpg'),
(122, 'Chả hấp chay', '- Củ năng: 70g\r\n- Cà rốt: 50g\r\n- Đậu hũ trắng: 2 miếng\r\n- Nấm hương: 5 cái\r\n- Nấm mèo: 3 tai\r\n- Bún tàu: 1 lọn\r\n- Chao trắng: 2 viên\r\n- Hành boaro: 1 cây\r\n- Rau thơm, khế, ớt sừng, chuối chát\r\n- Đường, tiêu, bột năng, màu điều, dầu ăn\r\n- Bột ngọt, nước tương \r\n', '- Đậu hũ luộc sơ, vắt ráo, tán nhuyễn, quết cho dai. Bún tàu ngâm mềm, cắt khúc ngắn.\r\n\r\n- Nấm mèo, nấm hương ngâm nở, rửa sạch, cắt sợi. Cà rốt cắt sợi. Hành boaro băm nhỏ, phi thơm. Ướp nấm và cà rốt với 1/2m tiêu và hành boaro phi.\r\n\r\n- Củ năng băm nhỏ. Chao tán nhuyễn.\r\n \r\n- Trộn đậu hũ với bún tàu, nấm, cà rốt, hành boaro, củ năng và chao, nêm 1m đường, 1m bột ngọt  và 1m bột bắp. Lót 1 lớp dầu ăn vào chén rồi ém chả vào, hấp chín.\r\n\r\n- Trộn 1 ít bột năng với nước và màu điều, phết lên mặt chả.\r\n \r\n- Lấy chả hấp ra dĩa, dùng với cơm, rau thơm, khế và chuối chat, chấm với nước tương.\r\n', '- Nên luộc sơ đậu hũ và vắt ráo trước khi quết để đậu có độ dẻo cao.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(122).jpg'),
(123, 'Cuốn diếp chay', '- Chả lụa chay : 0g\r\n- Tôm chay: 10 con\r\n- Cải bẹ xanh : 200g\r\n- Bún tươi : 100g\r\n- Mì căn : 100g\r\n-Nước dừa tươi : 1 trái\r\n- Cái bỗng rượu : 1M\r\n- Mật ong : 1M\r\n- Bông hẹ : 20 cọng\r\n- Dưa leo, rau sống, ớt sừng băm, hành poa rô, đồ chua\r\n- Bột ngũ vị hương, đậu phộng rang, thính, dầu ăn, tiêu, đường\r\n- Hạt nêm, nước tương\r\n', '- Tôm chần sơ, cắt đôi. Chả lụa, dưa leo cắt que. Bông hẹ chần sơ. Hành poaro cắt nhỏ.\r\n\r\n- Mì căn cắt dày 7mm, ướp 1/2M nước tương , 1/2m tiêu, 1m hạt nêm  từ nấm hương và hạt sen, 1m đường, 1 chút ngũ vị hương.\r\n \r\n- Bắc chảo dầu phi poa rô cho thơm, cho mì căn vào chiên sơ, cho tiếp phần nước ướp mì vào, thêm 1/2 chén nước dừa, rim cho mì căn thấm gia vị, đem ra cắt sợi cỡ đầu đũa.\r\n\r\n- Bỗng rượu trộn đều với mật ong, sên nhẹ cho ráo.\r\n\r\n- Pha nước chấm: 4M nước tương , 4M nước dừa,1m đường, nấu sôi, để nguội, nêm thêm 1m ớt băm, 1M đồ chua.\r\n\r\n- Cuốn: trải lá cải bẹ xanh, đặt dưa leo, rau, bún, mì căn, chả lụa, đậu phộng rang, cái bỗng rượu, thính, cuốn lại, đặt bên ngoài thêm 1 con tôm, cột lại bằng bông hẹ. Cắt 2 đầu cho gọn lại.\r\n \r\n- Dùng cuốn diếp với nước chấm.\r\n', '- Cách chọn mì căn ngon: mì căn trắng đục, không vàng, không nhớt, ấn tay vào có độ đàn hồi\r\n- Dùng bỗng rượu giúp món ăn có hương vị đặc trưng của cuốn diếp.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(123).jpg'),
(124, 'Đậu hũ nhồi hạt sen', '- Đậu hũ chiên: 2 miếng\r\n- Nấm đông cô: 6 tai\r\n- Hạt sen tươi: 50g\r\n- Hành boaro, ngò rí, cà chua\r\n- Tỏi băm, màu dầu điều\r\n- Tiêu, đường\r\n- Bột ngọt\r\n- Hạt nêm từ Nấm Hương và Hạt Sen\r\n', '- Đậu hũ cắt đôi dùng muỗng lấy phần ruột trắng ra, phần ruột trắng dùng vải mùng vắt cho ráo nước.\r\n\r\n- Nấm đông cô ngâm nở cắt sợi nhuyễn, nước ngâm nấm giữ lại. Hạt sen luộc chín, một nửa đập dập, một nửa giữ nguyên hạt. Cà chua băm nhỏ. Boaro cắt mỏng, phi vàng một nửa,\r\n\r\n- Trộn đều hỗn hợp đậu hũ trắng, nấm đông cô, hạt sen đập dập, tiêu, hành boaro phi, 1m hạt nêm, 1/2M dầu điều, nhồi vào đậu hũ.\r\n \r\n- Áp chảo phần mặt nhồi nhân của đậu hũ để định hình.\r\n\r\n- Phi thơm phần hành boaro còn lại, cho cà chua vào xào, nêm 1.5M hạt nêm, 1/2M màu điều, 2m đường, 1/2m bột ngọt. Sau đó cho khoảng ½ chén nước ngâm nấm. Cho đậu hũ và phần hạt sen nguyên hạt và rim lửa nhỏ cho thấm gia vị.\r\n \r\n- Cho đậu hũ ra đĩa, rắc tiêu và ngò rí lên trên.\r\n', NULL, 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(124).jpg'),
(125, 'Hủ tíu chay', '- Hủ tíu tươi: 400g\r\n- Củ sắn: 200g\r\n- Nấm rơm: 100g\r\n- Chả chay: 100g\r\n- Cải thảo: 4 lá\r\n- Cà rốt: 1 củ\r\n- Hành boaro: 1/2 cây\r\n- Cần tàu: 2 cọng\r\n- Giá, hẹ: 1 ít\r\n- Muối, tiêu, đường, dầu ăn\r\n- Nước tương, bột ngọt \r\n\r\n', '- Củ sắn cắt sợi. Cải thảo cắt riêng thân và lá rồi cắt xéo bản 2cm. Cà rốt tỉa hoa, cắt lát mỏng. Nấm rơm cắt đầu, luộc sơ, cắt chữ thập trên đỉnh đầu nấm.\r\n\r\n- Hành boaro cắt lát. Cần tàu cắt khúc ngắn 1cm. Chả chay cắt lát dày 0,3cm. Giá rửa sạch, để ráo. Hẹ cắt khúc ngắn 3cm.\r\n \r\n- Cho củ sắn vào nồi với 3 lít nước, nấu khoảng 30 phút cho ra nước ngọt, vớt bỏ xác củ sắn. Phi vàng hành boaro, trút ra chén, cho nấm rơm vào xào, nêm 1 ít muối, tiêu và bột ngọt. Cho nấm rơm và cà rốt vào nồi nước dùng nấu gần mềm, thêm thân cải thảo vào nấu 2 phút rồi cho lá cải thảo vào nấu chín, nêm 1/2M bột ngọt, 1/2M đường, 1/4m muối và 1M nước tương , nếm vị vừa ăn.\r\n \r\n- Trụng hủ tíu qua nước nóng, vẩy ráo nước, cho vào tô. Xếp chả chay lên mặt, chan nước dùng và rau vào xấp mặt, thêm 1M dầu, hành boaro phi, cần tàu và tiêu, chan thêm nước tương, ớt cắt lát và vắt thêm chanh tùy khẩu vị, dùng nóng.\r\n ', '- Chỉ trụng hủ tiếu qua nước nóng để hủ tiếu mềm mà không bị bở. Nêm nước tương  nước dùng sẽ không bị chua.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(125).jpg'),
(126, 'Khổ qua nhồi nấm kho nước tương', '- Khổ qua: 2 trái to\r\n- Thịt heo xay: 80g\r\n- Giò sống: 50g\r\n- Cà rốt cắt sợi: 1/4 củ\r\n- Nấm hương ngâm nở, cắt nhỏ: 5 tai\r\n- Ớt sừng: 1 trái\r\n- Hành lá: 1 cọng\r\n- Hành tím băm, dầu ăn, đường, tiêu\r\n- Hạt nêm, bột ngọt, nước tương\r\n', '- Trộn đều giò sống, thịt heo, nấm hương và cà rốt, nêm 1/2m bột ngọt, 1/2m đường, 1/2m hạt nêm, 1m hành tím băm, ít tiêu để thấm.\r\n\r\n- Hành lá cắt nhỏ, ớt sừng cắt xéo 3cm. Khổ qua cắt khúc 2cm, bỏ ruột, nhồi nhân vào khổ qua.\r\n\r\n- Đun nóng dầu ăn, chiên rám mặt khổ qua, cho 1/2 chén nước nóng vào kho nhỏ lửa, nêm 1m bột ngọt, 1m hạt nêm , 1M đường, 5M nước tương, thêm phần ớt sừng, kho đến khi nước sánh lại, nếm vị vừa ăn, tắt lửa. Rắc hành lá lên trên mặt.\r\n \r\n- Dùng nóng với cơm.\r\n', '-Nấm hương sẽ giúp cho món ăn có vị thơm ngon đặc biệt\r\n-Chiên khổ qua trước khi kho giúp khổ qua thấm gia vị và ngon hơn.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(126).jpg');
INSERT INTO `eating` (`id`, `name`, `material`, `making`, `tips`, `idType`, `bookmark`, `image`) VALUES
(127, 'Lẩu chay', '- Su hào, cà rốt, củ cải trắng - mỗi thứ 1 củ\r\n- Củ cải mặn - 100 gr\r\n- Đậu hũ non - 1 khuôn (khoảng 250 gr)\r\n- Đậu hũ bịch (hình dạng giống đậu hũ trứng) - 1 bịch\r\n- Nấm hương; nấm linh chi; nấm đùi gà - mỗi loại 150 gr\r\n- Bông cải xanh - 100 gr (1/2 cái)\r\n- Cải ngọt, cải thìa, cải thảo, mì căn, bắp non, tần ô - mỗi thứ 50gr\r\n- Muối, đường, boa rô.\r\n', '- Nấu nước dùng: Củ cải mặn xả qua nước lạnh, cắt khúc cỡ ngón tay. Củ cải trắng, su hào, cà rốt gọt vỏ, rửa sạch, cắt khúc. Tất cả cho vào nồi nước lớn, đun nhỏ lửa trong vòng 30 phút để lấy chất ngọt. Sau đó lọc lấy nước, nêm thêm muối, đường cho vừa ăn.\r\n\r\n- Boa rô rửa sạch, cắt khúc, rồi cho lên chảo nóng phi thơm với dầu, trút vào nước lẩu.\r\n\r\n- Nấm các loại ngâm với nước muối trong 10 phút cho hết độc và bụi cát, rửa sạch lại với nước lạnh nhiều lần, cắt khúc.\r\n\r\n- Các loại rau nhặt sạch, rửa nhiều lần với nước có thuốc tím pha loãng. Sau đó cắt khúc vừa ăn. Bày ra đĩa cùng mì căn, đậu hũ, nấm.\r\n\r\n- Đợi nước lẩu sôi rồi nhúng các loại nguyên liệu trên. Dùng nóng.\r\n \r\n', NULL, 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(127).jpg'),
(128, 'Rau củ chiên tẩm nước tương', '- Bí đỏ: 200g\r\n- Đậu cô ve: 200g\r\n- Tôm khô: 10g\r\n- Củ sen: 1 củ\r\n- Cà tím: 1 trái\r\n- Ớt hiểm: 2 trái\r\n- Hành lá: 2 cây\r\n- Đường, dầu ăn\r\n- Giấm gạo lên men\r\n- Hạt nêm, nước tương\r\n', '- Củ sen gọt vỏ, cắt lát, ngâm vào nước có ít giấm gạo  để không bị đen.\r\n\r\n- Bí đỏ gọt sơ vỏ, cắt thành miếng mỏng vừa ăn. Đậu cô ve tước chỉ sơ, cắt đôi.\r\n\r\n- Cà tím cắt miếng mỏng, ngâm trong nước muối loãng. Ớt, hành lá cắt nhỏ.\r\n \r\n- Nấu hỗn hợp nước tương: Cho vào nồi 300 ml nước và 10g tôm khô, nấu 10 phút với lửa nhỏ, nêm 1/2m hạt nêm, 1M đường và 3M nước tương.\r\n\r\n- Đun nóng dầu, cho các loại rau củ vào chiên vừa chín tới, vớt ra để ráo dầu rồi cho vào hỗn hợp nước tương.\r\n \r\n- Xếp rau củ ra dĩa cùng với hỗn hợp nước tương, rắc ớt và hành là lên trên.\r\n', '- Sau khi chiên rau củ xong, cho ngay vào hỗn hợp nước tương khi rau củ còn nóng, rau củ sẽ thấm nước tương ngon hơn.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(128).jpg'),
(129, 'Sườn chay nấu đậu', '- Sườn chay: 100g\r\n- Đậu bi: 50g\r\n- Đậu trắng: 100g\r\n- Cà rốt: 50g\r\n- Dừa tươi: 1 trái\r\n- Tàu hũ ky: 1 miếng\r\n- Ngò rí: 2 cây\r\n- Bánh mì: 4 ổ\r\n- Hành boaro: 1 cây\r\n- Ớt sừng: 3 lát\r\n- Muối, tiêu, dầu ăn, dầu điều, thuốc muối, bột năng, xốt cà hộp\r\n- Nước tương, bột ngọt \r\n', '- Đậu trắng và đậu bi luộc chín trong nước có ít thuốc muối, vớt ra rửa sạch, để ráo.Đậu trắng lột bỏ vỏ, cà rốt tỉa hoa, xắt lát. Hành boaro 1/2 băm nhỏ, 1/2 cắt khúc xéo 1cm, phi thơm hành boaro băm. Dừa tươi chặt lấy nước. Hòa tan 1M bột năng với 2M nước. Ngò rí rửa sạch, để ráo.\r\n\r\n- Sườn chay ngâm mềm, ướp với 1M hành boaro phi, 1,5m muối, 1,5m nước tương  và 1,5m bột ngọt, để thấm 10 phút.\r\n \r\n- Phi thơm hành boaro cắt khúc, cho sườn vào xào săn, nêm 2M xốt cà vào đảo đều. Thêm cà rốt, đậu trắng, đậu bi và nước dừa vào nấu chín, thêm 1m dầu điều và nước bột năng vào khuấy cho nước hơi sánh lại, nêm lại vị vừa ăn, tắt bếp.\r\n \r\n- Múc sườn chay nấu đậu ra dĩa sâu lòng, rắc thêm tiêu và ngò rí, dùng kèm bánh mì, nước tương và ớt cắt lát, bỏ thêm tàu hũ ky.\r\n', '- Nên luộc đậu trong nước có ít thuốc muối để đậu mau mềm và giữ được màu xanh. Sau khi luộc xong nên rửa lại thật kĩ. Có thể dùng đậu trắng hộp thay cho đậu tươi thì không cần luộc.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(129).jpg'),
(130, 'Thịt quay chay', '- Chả lụa chay: 300g\r\n- Vỏ bánh mì: 1 cái\r\n- Nước cốt dừa: 1 chén\r\n- Bột gạo: 2M\r\n- 1 khuôn inox hình chữ nhật\r\n- Muối, đường, bột năng\r\n- Dầu điều\r\n- Nước tương, bột ngọt \r\n \r\n', '- Vỏ bánh mì ép phẳng. Chả chay cắt miếng rộng bằng cỡ khuôn, dày 0,8-1cm.\r\n\r\n- Làm mỡ heo: cho vào nồi 50g bột năng, 2M bột gạo, 1 chén nước cốt dừa, 1/2 chén nước, 1/3m đường, 1/3m muối và 1/3m bột ngọt, cho lên bếp khuấy đến khi đặc lại nhưng chưa chín hẳn.\r\n\r\n- Tạo hình: dùng khuôn hình chữ nhật, phết dầu dưới đáy và xung quanh thành khuôn, cho vỏ bánh mì vào rồi phết hỗn hợp mỡ heo lên, xếp chả chay lên trên, làm 2 lớp như vậy, ép cho các phần dính chặt vào nhau.\r\n \r\n- Xếp chả vào nồi hấp 15 phút, để nguội, lấy ra khỏi khuôn, phết ít dầu điều lên da heo rồi đem chiên sơ, để nguội, cắt miếng nhỏ.\r\n \r\n- Xếp thịt quay chay ra dĩa, khi ăn chấm kèm nước tương.\r\n \r\n ', '- Chọn bánh mì lớn và xốp để làm da heo chay.\r\n- Khuấy bột không quá chín để làm mỡ heo dễ dàng hơn.', 5, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(130).jpg'),
(131, 'Cá chiên hoa cúc', '- 300g phi lê cá lóc\r\n- Trứng gà ta: 2 quả\r\n- 100g ớt Đà Lạt xanh, đỏ\r\n- 30g hành tây\r\n- 100g bột chiên giòn\r\n- Hành tím băm, tỏi băm\r\n- Tiêu, đường, dầu điều\r\n- Dầu ăn, bột năng\r\n- Xốt cà hộp\r\n- Hạt nêm\r\n- Giấm gạo lên men\r\n', '- Hành tây, ớt đà lạt cắt sợi dày 0,7cm. Trứng gà đánh tan. Hành tím, tỏi vắt lấy nước cốt.\r\n\r\n- Nghiêng dao khứa phần phi lê cá lóc để tạo khứa ca rô, cắt thành 3 miếng vuông , ướp với 1.5m Hạt nêm, 1/2m tiêu, 1m nước hành lá và 1m nước tỏi.\r\n \r\n- Cho bột chiên giòn vào túi ni lông, nhúng cá qua trứng rồi cho vào túi bột, xốc cho bột thấm đều quanh cá, lấy ra, giữ cho rơi rớt bột, chiên vàng.\r\n\r\n- Phi thơm hành tỏi băm, cho ớt đà lạt và hành tây vào xào, nêm 1/3m Hạt nêm, trút ra dĩa.\r\n\r\n- Làm xốt chua ngọt: cho vào tô 1/2 chén nước dừa, 1M Giấm gạo lên men, 1M đường, 1m Hạt nêm, 1 ít bột năng, dầu điều màu và 1M xốt cà hộp, khuấy đều. Phi thơm hành tỏi, cho xốt vào nấu đến khi xốt sánh lại, thêm ớt đà lạt và hành tây vào, đợi sôi lại thì tắt bếp.\r\n \r\n- Xếp cá chiên ra dĩa, rưới xốt chua ngọt lên trên, dùng nóng.\r\n', '- Chọn cá thịt dày, có da thì tạo hình hoa cúc sẽ đẹp hơn.\r\n- Hơi nghiêng dao cắt hình ca rô trên khứa cá để tạo thành hình bông cúc.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(131).jpg'),
(132, 'Cá thu chiên nghệ', '- Cá thu: 2 khoanh (150g/ khoanh)\r\n- Xoài keo: 1 trái\r\n- Nước cốt nghệ tươi: 2M\r\n- Hành tím bào mỏng: 5 củ\r\n- Hành tím cắt nhỏ (không băm): 1M\r\n- Lá húng lủi\r\n- Ớt sừng: 2 trái\r\n- Ngò rí: 2 cọng\r\n- Gạo rang giã nhỏ: 1M\r\n- Dầu ăn, tiêu, đường, nước mắm, ớt bột\r\n- Giấm gạo lên men\r\n- Bột chiên gà giòn\r\n', '- Ướp cá thu với nước nghệ, 1/2m tiêu, tẩm sơ qua Bột chiên gà giòn.\r\n\r\n- Xoài cắt sợi bản 2mm, ngâm xoài và hành tím bào với nước đá 10 phút, vớt ra để ráo.\r\n\r\n- Ớt sừng 1 trái băm nhỏ, 1 trái cắt sợi. Cọng ngò rí cắt nhỏ.\r\n\r\n- Pha nước mắm: Hòa đều 4M đường, 2M nước mắm, 2M giấm gạo lên men, 1m ớt bột, 2M cọng ngò rí, 1M hành tím cắt nhỏ, gạo rang và 1M ớt băm.\r\n \r\n- Làm gỏi: Ướp xoài, hành tím bào, ớt sợi với 1m đường, để 5 phút cho thấm. Thêm lá húng lủi, trộn đều.\r\n- Đun nóng dầu ăn, tẩm cá qua Bột chiên gà giòn, chiên áp chảo cá đã tẩm với lửa vừa đến khi chín vàng, vớt ra để ráo dầu.\r\n \r\n- Xếp cá 1 bên dĩa, gỏi 1 bên dĩa, trang trí ít húng lủi và ớt sừng cắt sợi, đặt chén nước mắm pha kế bên.\r\n', '- Ướp cá với nghệ để tăng thêm hương vị cho món ăn.\r\n- Tẩm cá qua bột 2 lần để cá thấm gia vị và tạo độ giòn trên bề mặt cá.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(132).jpg'),
(133, 'Chả cá thu chiên cốm', '- Chả cá thu nạo - 500 gr\r\n- Cốm dẹp - 100 gr\r\n- Mỡ gáy - 100 gr\r\n- Trứng nguyên - 1 quả\r\n- Lòng trắng trứng - 1\r\n', '- Thái mỡ hình hạt lựu, ướp với một chút đường.\r\n\r\n- Quết cá thật dẻo rồi nêm muối, hạt nêm, tiêu, nước mắm. Tiếp tục quết. Cho 1 muỗng canh lòng trắng vào, quết cho đến khi chả cá dẻo trở lại. Trộn mỡ gáy vào.\r\n\r\n- Đánh tan một quả trứng. Vo chả thành viên tròn rồi lăn qua trứng, sau đó lăn qua cốm. Đem chiên ngập dầu. Để lửa vừa, khi nào chả cá nổi lên là được. Chấm tương ớt.\r\n', NULL, 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(133).jpg'),
(134, 'Cua lột chiên giòn', '- Cua lột (khoảng 600g): 4 con\r\n- Xà lách xoong: 100g\r\n- Trứng gà: 1 quả\r\n- Hành tỏi băm, ớt, ngò rí\r\n- Bột gạo, bột mì, men bột mì\r\n- Tương ớt, bơ tươi, muối\r\n- Tiêu sọ, dầu ăn\r\n- Hạt nêm, xốt Mayonnaise \r\n', '- Cua làm sạch, cắt đôi, ướp với 1M hành tỏi băm, 1/2m tiêu và 2m Hạt nêm, để thấm. Bơ tươi đun chảy. Xà lách xoong lặt rửa sạch, để ráo.\r\n\r\n- Pha men hoạt hóa: trộn đều 1m men bột mì và 2M nước ấm, sau đó để khoảng 10 phút.\r\n\r\n- Pha bột: trộn đều 50g bột gạo với 200g bột mì, 200ml nước ấm, lòng đỏ trứng gà, 1/2m Hạt nêm và men hoạt hóa, để 10 phút đến khi thấy bột sệt lại là được.\r\n \r\n- Đun sôi dầu, nhúng cua vào bột khô, áo một lớp thật mỏng sau đó chiên chín vàng, dội bơ chảy lên cua, để ráo dầu.\r\n\r\n- Pha xốt chấm: trộn đều 2M xốt Mayonnaise với 1/2m tương ớt và 1/2m tiêu sọ đập dập.\r\n \r\n- Xếp cua chiên giòn ra dĩa, trang trí thêm ngò và ớt, chấm kèm hỗn hợp xốt chấm đã pha.\r\n', '- Chọn cua lột còn sống, thịt cua sẽ ngọt và thơm hơn.\r\n- Dùng men khô hòa với ít nước ấm để tạo thành men hoạt hóa.\r\n- Nếu không có men bánh mì ta cũng có thể thay thế bằng bột chiên giòn.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(134).jpg'),
(135, 'Đậu mơ kẹp phô mai chiên', '- Đậu phụ mơ (Bắc): 5 miếng\r\n- Phomai cứng: 100g\r\n- Húng lủi, húng quế, tía tô, lá chanh (Bắc): mỗi loại 10g\r\n- Ớt băm: 1m\r\n- Dầu ăn, tương ớt\r\n- Bột chiên gà giòn\r\n- Xốt Mayonnaise\r\n- Trang trí: Xà lách, ớt xoăn, húng lũi\r\n \r\n', '- Đậu phụ cắt làm 2, rạch một đường ở giữa. Phô mai cắt miếng 2x3cm.\r\n\r\n- Lá chanh, tía tô, lá húng quế cắt sợi. Húng lủi lặt riêng từ lá.\r\n\r\n- Tạo hình, gấp đôi một sợi rau thơm với một miếng phômai, cho vào giữa miếng đậu (mỗi miếng là một loại rau thơm khác nhau).\r\n \r\n- Lăn miếng đậu qua Bột chiên gà giòn, cho áo đều xung quanh.\r\n\r\n- Đun nóng dầu, cho miếng đậu kẹp phomai chiên cho lớp vỏ ngoài vừa vàng giòn. Vớt ra để ráo dầu.\r\n\r\n- Pha xốt: trộn đều 2M xốt Mayonnaise, 1M tương ớt và 1m ớt băm\r\n \r\n- Ăn nóng, chấm với xốt mayonnaise trộn.\r\n \r\n', '- Chọn loại đậu phụ chưa chiên, có độ cứng vừa phải, không quá mềm để không bị bể.\r\n- Không chiên quá lâu, để phomai có độ mềm vừa phải thì sẽ ngon hơn.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(135).jpg'),
(136, 'Ếch chiên bột', '- Ếch: 300g\r\n- Dưa leo: 1 trái\r\n- Xà lách, rau thơm, tắc, tỏi ớt băm, cà chua bi\r\n- Dầu ăn, nước mắm, đường\r\n- Bột chiên giòn Hải Sản\r\n', '- Dưa leo cắt lát, cà chua bi cắt đôi.\r\n\r\n- Pha nước mắm: 2M đường, 2M nước tắc, 2M nước mắm, 1/2M tỏi băm, 1/2M ớt băm.\r\n\r\n- Ếch làm sạch, chặt miếng vừa ăn.\r\n\r\n- Trộn 120g Bột chiên giòn Hải Sản với 110ml nước.\r\n \r\n- Đun nóng dầu ăn, nhúng ếch vào bột sau đó đem chiên lửa vừa đến khi ếch chín đều, vàng giòn, vớt ra để ráo.\r\n \r\n- Ăn kèm rau sống, chấm nước mắm.\r\n', '- Chiên lửa vừa, không quá lớn để ếch chín đều, khi sắp chín thì tăng lửa để ếch được giòn và ngon hơn.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(136).jpg'),
(137, 'Lươn chiên giòn xốt tiêu xanh', '- Thịt lươn: 300g\r\n- Tiêu xanh: 30g\r\n- Nước dừa tươi: 1/2 chén\r\n- Hành tỏi băm, sả băm\r\n- Dầu ăn, dầu hào, tương ớt, bột bắp, đường\r\n- Giấm gạo lên men\r\n- Bột Chiên Gà Giòn\r\n- Hạt nêm.\r\n', '- Lươn làm sạch, rửa bằng giấm và nước ấm. Lóc lấy phần nạc, bỏ phần xương sống, cắt khúc, ướp vào 1/3M tiêu xay, 1M hành tỏi băm.\r\n\r\n- Tiêu xanh tách lấy hạt, đập dập hạt tiêu xanh.\r\n\r\n- Thịt lươn xốc đều với Bột Chiên Gà Giòn, chiên ngập dầu phần lươn, vớt ra để ráo dầu.\r\n\r\n- Làm xốt: Cho vào 1/2 chén nước dừa xiêm, 1/2M hạt nêm, 1M đường, 1/2M dầu hào, 1/2M tương ớt, 1/2M bột bắp. Phi thơm 1m sả băm, 1M hành tỏi băm và tiêu xanh, cho phần nước dừa tươi trộn vào nấu cho hỗn hợp sôi và có độ sệt.\r\n\r\n- Cho xốt vào dĩa, xếp lươn lên trên và rưới xốt.\r\n', NULL, 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(137).jpg'),
(138, 'Mực chiên ngũ sấc', '- Mực ống: 2 con\r\n- Bột gạo: 150g\r\n- Mè đen: 1M\r\n- Lá cẩm: 100g\r\n- Lá dứa: 30g\r\n- Bột nghệ, ớt bột\r\n- Tiêu, tương ớt, dầu ăn\r\n- Hạt nêm, xốt Mayonnaise\r\n', '- Mực làm sạch, cắt khoanh dày 0,5cm, ướp với 1 ít Hạt nêm  và tiêu, để thấm. Mè đen giã nhuyễn. Lá dứa cắt nhỏ, thêm 50g nước vào xay nhuyễn, vắt lấy 4M nước. Lá cẩm cắt nhỏ, xay nhuyễn với 2/3 chén nước, lọc lấy 4M nước lá cẩm.\r\n\r\n- Pha bột tẩm màu đen: trộn đều 1M Xốt mayonnaisevới 4M nước lọc, 3M bột gạo và 1M mè đen.\r\n\r\n- Pha bột tẩm màu vàng: trộn 1M Xốt mayonnaisevới 4M nước lọc, 3M bột gạo, 1/3m bột nghệ và 1/2m ớt bột.\r\n\r\n- Pha bột tẩm màu xanh: trộn 1M Xốt mayonnaisevới 4M nước lá dứa và 3M bột gạo.\r\n\r\n- Pha bột tẩm màu tím: trộn 1M Xốt mayonnaisevới 4M nước lá cẩm và 3M bột gạo.\r\n\r\n- Pha bột tẩm màu trắng: trộn 1M Xốt mayonnaisevới 4M nước lọc và 3M bột gạo.\r\n \r\n- Lăn mực qua bột gạo, chia thành 5 phần, nhúng mỗi phần qua 1 loại bột tẩm rồi chiên chín vàng, vớt ra để ráo dầu.\r\n\r\n- Xếp mực chiên ngũ sắc ra dĩa, khi ăn chấm với Xốt mayonnaisetrộn ớt băm hoặc ớt bột.\r\n', '- Mực lau thật khô trước khi chiên để không bị bắn dầu.\r\n- Trộn bột tẩm với Xốt Mayonnaise Aji-mayo sau khi chiên sẽ giữ được màu sắc tươi tự nhiên.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(138).jpg'),
(139, 'Tôm chiên khoai tây', '- Tôm sú: 6 con (khoảng 100g)\r\n- Khoai tây: 1 củ lớn\r\n- Tiêu trắng: muối, mỗi thứ 1 muỗng café.\r\n- Bột xù: 30g\r\n- Chanh: 1 trái\r\n- Dầu ăn Brazil: 50ml\r\n- Gạo: 300g\r\n- Thịt heo: 30g\r\n- Thịt bò: 30g\r\n- Hành tây: 1/2 củ\r\n- Ớt xanh và ớt đỏ Đà \r\n- Lạt: mỗi thứ 1/2 trái\r\n- Dầu olive, tỏi, ngò tây.\r\n', '- Tôm sú bóc vỏ lấy đầu và ướp muối, tiêu rồi tẩm bột xù đã pha nước sền sệt.\r\n\r\n- Khoai tây bào sợi mỏng và cuộn với tôm đã tẩm qua bột chiên xù. Cho tôm đã bọc khoai tây vào chảo dầu nóng chiên giòn.\r\n\r\n- Chiên khoảng 2 phút thấy khoai tây vàng đều thì vớt ra ngay để ráo dầu.\r\n', NULL, 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(139).jpg'),
(140, 'Xôi gà chiên', '- Thịt gà băm : 150g\r\n- Xôi đậu xanh : 400g\r\n- Bánh tráng vàng : 12 cái\r\n- Hành lá cắt nhỏ : 1/2 chén\r\n- Hành tím cắt lát : 1/2 chén\r\n-Tỏi băm\r\n- Mè trắng rang\r\n- Nước bột năng pha loãng\r\n- Tiêu trắng xay, đường, bột ngũ vị hương, dầu ăn, dầu hào\r\n- Nước tương, bột ngọt, hạt nêm , xốt mayonnaise\r\n', '- Ướp gà với 1M tỏi băm, 1/4m bột ngũ vị hương, 1M dầu hào, 1m đường, 1m nước tương, 1/2m tiêu và 1m hạt nêm.\r\n \r\n- Phi thơm hành tím, cho gà vào xào nhanh tay, tắt lửa, cho hành lá, 1M mè trắng, 2m dầu mè, 1M nước bột năng pha loãng vào đảo đều.\r\n\r\n- Thoa ướt bánh tráng, cho 1 lớp xôi, 1 lớp nhân thịt gà, thêm 1 lớp xôi, gói dẹp thành hình chữ nhật.\r\n\r\n- Đun nóng dầu, cho viên xôi vào chiên chín vàng.\r\n\r\n- Cho viên xôi ra dĩa, chấm kèm xốt mayonnaise.\r\n', '- Xào gà nhanh tay để gà vẫn giữ được độ ngọt sau khi xào.\r\n- Dùng bánh tráng bọc bên ngoài viên xôi chiên, để khi ăn giòn và thơm hơn.', 6, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(140).jpg'),
(141, 'Cơm cá hồi cà ri', '- 4 bát cơm trắng\r\n- 400g cá hồi\r\n- 200g đậu cô-ve\r\n- 2 thìa cà phê tỏi xay\r\n- 1 thìa cà phê hành tím xay\r\n- 1/2 thìa cà phê tớ xay\r\n- 1 thìa cà phê sả xay\r\n- 2 thìa xà phê hạt nêm\r\n- 1 thìa cà phê đường\r\n- 2 nhánh hành lá\r\n- 1/2 thìa súp bơ\r\n- Dầu ăn\r\n', '-Cá hồi chia thành 4 phần, rán vàng giòn. Đâu cô ve cắt khúc, xào chín với bơ, nêm ít muối\r\n\r\n-Đun nóng dầu ăn trong chảo, cho tỏi, ớt ,sả, hành tím vào phi thơm, thêm bột cà ri vào đảo đều. Đổ tiếp sữa tươi vào nấu đến khi sốt hơi sánh lại, nêm hạt nêm, đường cho vừa ăn\r\n', '- Bạn nên nấu cơm hơi ráo khi dùng kèm với các món sốt như cà –ri vì nếu cơm quá mền khi rưới sốt vào sẽ dễ bị nhão, ăn rất ngán.', 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(141).jpg'),
(142, 'Cơm chiên bí đỏ', '- 3 chén cơm trắng\r\n- 100g thịt xông khói chay\r\n- 100g bí đỏ\r\n- 100g đậu Hà Lan\r\n- 1 trái bắp luộc chín\r\n- 1 thìa súp nho khô\r\n- 1 cây hành boa-rô\r\n- 1 thìa súp bơ nước\r\n- 2 thìa súp dầu mè\r\n- 1 thìa cà phê bột cà ri\r\n- ½ thìa cà phê muối\r\n- ½ thìa cà phê đường\r\n- 2 thìa cà phê Hạt nêm Knorr từ Nấm và Rong biển\r\n- Nước tương ăn kèm\r\n', '- Thịt xông khói cắt hạt lựu\r\n\r\n- Bí đỏ hấp chín, tán nhuyễn. Đậu Hà Lan luộc chín. Bắp tách hạt. Nho khô ngâm mềm. Boa-rô bỏ lá, cắt hạt lựu\r\n\r\n- Đun nóng bơ, cho boa-rô vào phi thơm, cho lần lượt bắp, đậu Hà Lan, thịt xông khói vào xào, nêm ít muối, đường, Hạt nêm Knorr từ Nấm và Rong biển, trút ra tô, để riêng\r\n\r\n- Làm nóng dầu mè, phi thơm boa-rô, cho bột cà ri vào xào nhanh tay với bí đỏ, cho cơm vào trộn đều. Chiên cho cơm săn lại, cho hỗn hợp đã xào vào, thêm nho khô, trộn đều, nêm 2 thìa cà phê hạt nêm\r\n\r\n- Dọn dùng kèm nước tương.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(142).jpg'),
(143, 'Cơm chiên củ quả', '- Gạo tẻ 200g\r\n- Gấc 1 trái\r\n- Ớt chuông xanh, đỏ, vàng 1/2 trái mỗi loại\r\n- Thịt ức gà 200g\r\n- Gia vị: hạt nêm, tiêu, hành ngò, tỏi băm, rượu trắng, dầu.\r\n', '- Gấc tách lấy cơm, bỏ hạt, thêm chút rượu trắng, đánh tan. Vỏ gấc rửa sạch, lót giấy nhôm ở trong.\r\n\r\n- Thịt ức gà luộc chín, vớt để nguội, cắt hạt lựu 1cm.\r\n\r\n- Nước luộc gà nấu với gạo, 1/2 cơm gấc, cho nước vừa phải để cơm ráo, để nguội hoặc để trong tủ lạnh cho hạt cơm tơi ra.\r\n\r\n- Ớt chuông cắt hạt lựu 1cm. Trộn với 1/2 muỗng cà phê hạt nêm.\r\n\r\n- Phi dầu với tỏi băm, cho thịt gà vào xào vừa chín, thêm ớt chuông xào chín, trút ra. Phi dầu với tỏi, cho cơm gấc còn lại xào nhanh, cho cơm vào xào chung, nêm một muỗng càphê hạt nêm, xào cho hạt cơm thật săn lại, cho trở lại thịt gà vào, trộn đều.\r\n\r\n- Tắt lửa, thêm hành ngò, tiêu.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(143).jpg'),
(144, 'Cơm chiên cua', '- Cua ngon - 1 kg (chọn con vừa khoảng 3 – 4 con/kg)\r\n- Gạo - ½ kg\r\n- Ớt chuông - 1 quả\r\n- Đậu đũa - 50gr\r\n- Hành lá; tỏi tươi; gia vị vừa đủ.\r\n', '- Món này quan trọng nhất là khâu sơ chế ban đầu, đó là luộc cua. Thông thường, khi luộc cua không cần thêm nước vì trong quá trình nấu cua tự tiết nước ra. Nhưng với món cơm chiên này cần luộc cua đúng cách. Cua tháo bỏ dây và rửa sạch dưới vòi nước mạnh. Đun sôi khoảng 1 lít nước, thả cua vào đậy kín nắp.\r\n\r\n- Thăm chừng khoảng vài phút thấy nước sôi trở lại thì tắt bếp vì cua rất nhanh chín. Dùng nước luộc cua nấu cơm.\r\n\r\n- Cua để nguội, gỡ lấy thịt. Ướp với một ít bột nêm và hành lá băm nhuyễn.\r\n\r\n- Ớt chuông; đậu đũa rửa sạch, cắt khúc cỡ hạt lựu.\r\n\r\n- Khi cơm đã chín, bắt đầu khâu chiên cơm.\r\n\r\n- Phi thơm hành tỏi băm cùng với ít dầu ăn, cho đậu và ớt vào chiên trên lửa lớn cho chín tới. Cho thịt cua vào xào nhanh tay cũng với lửa lớn. Trút cơm vào chiên. Thêm một ít muối, bột nêm, tiêu cho vừa ăn, nhắc xuống.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(144).jpg'),
(145, 'Cơm gà xì dầu', '- Đùi hoặc má đùi gà\r\n- 3 cánh hoa hồi rang thơm\r\n - 60ml xì dầu\r\n- 1 nhánh gừng\r\n- Vài tép tỏi\r\n- 1 cục đường phèn nhỏ\r\n - 1 muỗng canh rượu Mai Quế Lộ\r\n - 200ml nước lạnh\r\n- 1 muỗng canh dầu ăn\r\n', '- Gà rửa sạch để ráo. Gừng, tỏi thái lát mỏng.\r\n\r\n- Hoa hồi rang sơ cho thơm rồi cho vào nồi nhỏ hỗn hợp gồm xì dầu, nước lạnh, hoa hồi, đường phèn, tỏi, gừng, bắt lên bếp nấu đến khi đường tan, hỗn hợp sôi nhẹ thì tắt bếp để nguội.\r\n\r\n- Cho gà vào tô hoặc hộp to, rưới hỗn hôp xì dầu vừa nấu đã để nguội vào, đậy nắp lại, cất vào tủ lạnh vài tiếng hoặc qua đêm cho hỗn hợp thấm đều vào gà\r\n\r\n- Sau khi đã ướp gà qua đêm bạn lấy ra, cho tất cả vào nồi nấu với lửa nhỏ, thỉnh thoảng vớt bọt nếu có.\r\n\r\n- Trở gà cho miếng gà được thấm đều gia vị, nấu đến khi hỗn hợp xì dầu sệt lại thì tắt bếp.\r\n\r\n- Lấy gà ra cắt nhỏ hoặc để nguyên miếng tuỳ thích, xếp lên đĩa cơm dùng chung với dưa cà chua tuỳ thích.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(145).jpg'),
(146, 'Cơm hấp Atiso', '- 1 bông hoa Atiso\r\n- 100g tôm sú tươi\r\n- 100g thịt nạc vai xay nhuyễn\r\n- 100g bột chiên xù\r\n- 30g đậu trắng\r\n- 3 lòng đỏ trứng gà\r\n- Hành tím khô, hạt nêm, tiêu vừa đủ\r\n', '- Hoa atiso rửa dưới vòi nước chảy cho sạch bụi bám ở các lớp cánh.\r\n\r\n- Dùng thìa xúc bỏ hết lớp nhụy và một số cánh nhỏ màu tím đỏ ở trong cùng của hoa.\r\n\r\n- Tách bớt một số cánh hoa bánh tẻ để riêng (dùng làm chả hoa atiso đấy).\r\n\r\n- Đậu ván trắng hầm mềm với một chút muối và nước. Trộn hạt đậu đã mềm với cơm trắng rồi nhồi vào bông hoa atiso (nếu không thích có đậu thì chỉ cần cơm không cũng được).\r\n\r\n- Đặt bông hoa vào nồi hấp cách thủy khoảng 15 phút là được.\r\n\r\n- Lòng đỏ trứng gà đánh tan cùng chút hạt nêm rồi tráng vàng, thái chỉ, rắc lên cơm để làm nhụy hoa.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(146).jpg'),
(147, 'Cơm nắm trái tim', '- 250g gà rán\r\n- 150g khoai tây rán (hoặc cá tẩm bột)\r\n- 1 quả ớt chuông đỏ\r\n- 1 quả ớt chuông xanh\r\n- 5g vừng\r\n- 1 tô cơm trắng\r\n- 1 củ cải trắng nhỏ (luộc chín hoặc dầm chua ngọt)\r\n- Khuôn trái tim (tùy ý)\r\n', '- Xé gà thành miếng nhỏ, cắt nhỏ khoai tây và củ cải.\r\n\r\n- Tiếp theo là thái ớt chuông thành dạng hạt lựu. Các bạn nên xào hoặc chần qua ớt để dễ ăn hơn.\r\n\r\n- Cho ớt và vừng vào tô cơm trắng.\r\n\r\n- Trộn đều cơm và ớt với nhau\r\n\r\n- Chia cơm thành từng miếng nhỏ, nặn hình trái tim rồi đặt miếng gà, củ cải và khoai tây vào giữa.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(147).jpg'),
(148, 'Cơm tấm sườn', '- Sườn non: 600g\r\n- Cơm tấm: 1kg\r\n- Mỡ thịt: 200g\r\n- Hành lá: 4 cọng\r\n- Nước tỏi: 2M\r\n- Tỏi ớt băm: 1M\r\n- Nước cốt chanh: 1M\r\n-  Ăn kèm: dưa leo, cà chua, đồ chua\r\n- Muối, tiêu, đường, dầu điều, ngũ vị hương, nước mắm\r\n- Hạt nêm ,bột ngọt, nước tương, xốt Mayonnaise\r\n', '- Đầu hành băm nhỏ, phần lá cắt nhỏ, cho vào chén.\r\n\r\n- Sườn non chặt khúc 5cm, ướp 1M đường, 2m hạt nêm, 1/4m muối, 1/3m ngũ vị hương, 1/4m tiêu, 1M nước tỏi, đầu hành băm nhỏ, 1m dầu điều, 2M xốt Mayonnaise và 1M nước tương để thấm.\r\n\r\n- Làm mỡ hành: Mỡ thịt cắt khối 1cm. Thắng mỡ với ít nước đến khi tóp mỡ vàng ngà. Tắt lửa cho 1/2m tỏi băm vào phi thơm, chan mỡ đang nóng vào chén hành lá, nêm ít hạt nêm, trộn đều cho hành chín.\r\n \r\n- Nướng sườn trên lửa than hoặc trong lò nướng 180 độ đến khi chín vàng.\r\n\r\n- Pha nước mắm: Hòa tan 2,5M đường, 2M nước mắm, 1/2m muối, 1/2m bột ngọt, 1M nước cốt chanh, 1M tỏi ớt băm và 1/2 chén nước lọc.\r\n \r\n- Cho cơm tấm sườn ra dĩa, rưới mỡ hành và nước mắm lên trên, dùng kèm dưa leo, cà chua và đồ chua.\r\n', '- Khi nướng thịt se mặt, dùng cọ phết gia vị lên sườn giúp sườn thơm và bóng hơn.\r\n-  Ướp thịt với xốt mayonnaise giúp thịt sau khi nướng có mùi vị đậm đà và không bị khô.', 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(148).jpg'),
(149, 'Cơm tôm bí đỏ', '- Bí đỏ: 70g\r\n- Tôm sú: 200g\r\n- Đậu cô ve Nhật: 300g\r\n- Cơm nguội: 2 chén\r\n- Hành lá: 2 cây\r\n- Ngò rí: 2 cây\r\n- Ớt hiểm: 3 lát\r\n- Tiêu, tỏi băm, dầu ăn\r\n- Nước tương, hạt nêm\r\n', '- Bí đỏ gọt vỏ, bào nhuyễn. Tôm bóc hết vỏ, lấy chỉ đen, cắt hạt lựu. Đậu cô ve tước bỏ xơ, rửa sạch, cắt hạt lựu. Hành lá cắt nhỏ. Ngò rí rửa sạch, để ráo.\r\n \r\n- Phi thơm 1M tỏi băm, xào chín tôm và đậu cô ve, cho cơm vào, nêm 1,5m Hạt nêm, chiên đến khi hạt cơm săn lại, cho bí đỏ vào đảo đều, bí chín thì rắc thêm hành lá, trộn đều, tắt bếp.\r\n \r\n- Cho cơm chiên ra dĩa, rắc ngò rí lên trên, dùng nóng kèm Nước tương  và ớt cắt lát.\r\n', '- Bào bí đỏ thật nhuyễn giúp bí mau chín.\r\n- Khi nấu cơm để chiên thì nấu cơm phải khô hoặc dùng cơm nguội.\r\n- Sử dụng Hạt nêm  trộn đều khi chiên giúp cơm thấm đều gia vị.\r\n- Chiên cơm bằng lửa lớn và nhanh tay để bí không làm cơm bị nhão.', 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(149).jpg'),
(150, 'Đậu phụ cuộn cơm', '- Cơm trắng - 1 tô\r\n- Đậu phụ rỗng ruột - 2 miếng\r\n- Giấm sushi - 2 muỗng canh\r\n- Vừng rang trắng - 1 muỗng canh\r\n- Gia vị: nước tương, đường, mirin.\r\n', '- Cho đậu phụ vào nồi nước sôi, trụng sơ cho đậu ra bớt dầu.\r\n\r\n- Cắt đôi miếng đậu phụ ra làm hai thành hình vuông, dùng tay tách ruột bên trong cho đậu không dính vào nhau. Bạn nhớ tách nhẹ tay tránh làm rách miếng da đậu phụ.\r\n\r\n- Bắc nồi nhỏ lên bếp, cho vào nồi 1 muỗng canh nước tương, 1 muỗng canh đường, 1 muỗng canh mirin và 1 muỗng canh nước nấu cho tan đường rồi cho đậu phụ đã cắt vào nấu cho đến khi nước cạn, đậu phụ thấm đều gia vị\r\n\r\n- Cho cơm vào tô, nếu cơm nguội thì bạn nên hâm lại chút cho ấm, thêm mè và giấm sushi vào cơm, trộn đều.\r\n\r\n- Múc cơm vào màng bọc thực phẩm, nắn cơm thành miếng nhỏ, dài bằng cỡ ngón tay cái và to gấp đôi sao cho vừa đủ nhét vào miếng đậu phụ.\r\n\r\n- Cho viên cơm đã nắn vào miếng đậu phụ, dùng tay ấn cơm xuống hai góc miếng đậu sao cho vuông vức, gập phần dư của miếng đậu lại.\r\n\r\n- Làm tiếp tục cho đến khi hết đậu rồi xếp ra đĩa, dùng ngay hay để nguội đều được.\r\n', NULL, 3, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(150).jpg'),
(151, 'Bánh Rô-ti cuốn thịt bò', '- Bánh rô ti: 1 gói\r\n- Thịt bò xay: 150g\r\n- Bắp cải: 50g\r\n- Cà rốt: 30g\r\n- Cần tây: 30g\r\n-  Ớt sợi, ớt băm, olive đen, tỏi băm\r\n- Gia vị: tiêu, đường\r\n- Hạt nêm\r\n- Xốt Mayonnaise\r\n', '- Thịt bò ướp với 1M tỏi băm, 1m hạt nêm, 1/2m tiêu, 1 ít muối và 1 ít đường, để thấm.\r\n\r\n- Bắp cải, cà rốt, cần tây, hành tây cắt sợi nhỏ. Olive cắt khoanh.\r\n \r\n- Phi thơm tỏi, xào nhanh thịt bò vừa chín, trút ra. Xào tiếp bắp cải, cà rốt, hành tây và cần tây, nêm 1/4m hạt nêm, tắt bếp.\r\n\r\n- Trộn thịt bò với rau củ xào, 1M ớt sợi, 2M mayonnaise,  1M olive và 1/2m ớt băm.\r\n\r\n- Bánh rô ti áp chảo vàng đều 2 mặt. Trải bánh rô ti ra, cho nhân và ngò rí lên, cuốn chặt lại.\r\n \r\n- Xếp bánh ra dĩa, dùng nóng.\r\n', '- Dùng chảo không dính để áp chảo bánh sẽ không cần dầu ăn.\r\n- Không dùng dầu khi xào rau, rau sẽ xém cạnh và thơm hơn.', 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(151).jpg'),
(152, 'Bò bía', '- 30 cái bánh tráng\r\n- 2 cây lạp xưởng\r\n- 2 quả trứng vịt\r\n- 1 củ sắn\r\n- 50g tôm khô\r\n- 2 cây xà lách\r\n- 100g đậu phộng rang\r\n- Cà rốt, củ cải trắng cắt sợi\r\n- Hành phi, ớt\r\n- Gia vị: muối, tiêu, đường\r\n- Bơ đậu phộng\r\n- Giấm gạo, hạt nêm, xốt tương \r\n', '- Lạp xưởng luộc chín, để nguội, cắt lát mỏng.\r\n\r\n- Trứng vịt đánh tan, dùng chảo không dính tráng mỏng, để nguội, cắt sợi nhỏ.\r\n\r\n- Củ sắn gọt vỏ, cắt sợi. Phi thơm hành băm, cho củ sắn vào xào, nêm 1/4m muối và 1/2m tiêu, xào chín, trút ra dĩa.\r\n\r\n- Tôm khô ngâm nước nóng cho mềm, để ráo, giã dập, cho vào chảo xào với 2m dầu ăn cho săn.\r\n\r\n- Xà lách rửa sạch, bỏ cọng cứng, để ráo.\r\n \r\n- Thấm nước cho bánh tráng mềm, đặt một lá xà lách lên giữa bánh tráng, cho củ sắn, trứng tráng lên rồi cuốn lại 1 lớp, thêm 3 miếng lạp xưởng dọc theo chiều dài của cuốn rồi gấp hai đầu bánh dư vào, cuốn tiếp nửa vòng, rắc ít tôm khô dọc theo chiều dài cuốn bò bía, sau cùng mới cuốn hết phần bánh tráng, khi cuốn giữ cho cuốn bò bía chắc tay.\r\n\r\n- Làm xốt tương chấm: phi 1m tỏi với 1M dầu ăn, cho 2M xốt tương , 1M bơ đậu phộng, 2/3 chén nước, 1m đường, 1m hạt nêm  và 1m giấm. Nấu đến khi xốt có độ sệt vừa phải, vị ngọt đậm đà từ xốt tương, béo từ bơ đâu phộng. Khi ăn thêm ớt băm, cà rốt, củ cải ngâm giấm đường và đậu phộng rang giã nhỏ.\r\n \r\n- Xếp bò bía ra dĩa, khi ăn chấm với xốt tương pha.\r\n', '- Muốn thấm ướt bánh tráng có thể dùng 1 cái khăn sạch, thấm nước, vắt ráo rồi lau lên bánh, hoặc xếp xen kẽ bánh với lá chuối cũng làm bánh dẻo hơn.', 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(152).jpg'),
(153, 'Bò cuốn rau củ', '- 400g bò thăn\r\n- 200g cà rốt\r\n- 100g đậu cô-ve\r\n- 2 thìa dầu hào\r\n- 2 củ tỏi khô\r\n- 50ml nước tương\r\n- 50g đường\r\n- 1 thìa rượu trắng\r\n- Bột gia vị\r\n- Bột nêm, tiêu, dầu ăn.\r\n', '- Thịt bò thái lát to bản dày khoảng 0,3cm, dùng chày dần mềm rồi đem ướp với bột gia vị, bột nêm, tiêu, dầu hào và tỏi băm nhỏ, để khoảng 10 phút cho thấm.\r\n\r\n- Cà-rốt thái que, đậu cô-ve thái khúc vừa ăn rồi đem chần chín sơ.\r\n\r\n- Đun nước tương với đường, rượu trắng cho hơi cạn và sánh lại.\r\n\r\n- Trải thịt bò lên mặt phẳng, dùng cọ phết lớp nước tương cô đặc lên, đặt cà-rốt, đậu cô-ve vào rồi cuốn chặt tay, Sau đó cùng sợi chỉ buộc chặt lại giữ mép\r\n\r\n- Đun nóng dầu, cho thịt bò vào rán chín với lửa nhỏ\r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(153).jpg'),
(154, 'Cải thảo cuộn bò', '- 600g cải thảo\r\n- 200g phi-lê thịt bò\r\n- 50g củ năng\r\n- 2 tai mộc nhĩ\r\n- 2 thìa cà-phê bột năng\r\n- 1 thìa súp dầu ăn\r\n- 1 thìa cà-phê hạt nêm\r\n- 2 thìa súp tương ớt\r\n- 2 nhánh hàng lá.\r\n', '- Cải thảo tách lá, rửa sạch. Đun sôi nước, thả cải thảo vào trụng sơ. Vớt ra, để ráo, dùng dao gọt mỏng bớt phần cuống cải.\r\n\r\n- Củ năng thái nhỏ.\r\n\r\n- Mộc nhĩ thái nhỏ.\r\n\r\n- Nhân: Xay nhuyễn thịt bò, trộn đều với hạt nêm, tiêu, củ năng, mộc nhĩ và bột năng để tạo độ dính.\r\n\r\n- Làm hành mỡ: Đun sôi dầu ăn, cho hành lá thái nhỏ vào.\r\n\r\n- Trải lá cải thảo trên mặt phẳng, múc ít nhận vào phần đầu lá, cuộn lại, đặt vào đĩa chịu nhiệt, hấp cách thủy 15 phút. Cải thảo chín, gắp ra đĩa, quết mỡ hành lên. Dùng kèm với tương ớt.\r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(154).jpg'),
(155, 'Chả tôm cuốn đậu bắp', '- Tôm tươi, bóc vỏ rút chỉ đen - 200g\r\n- Giò sống - 100g\r\n- Đậu bắp, thì là, ớt, hành khô\r\n- Hạt nêm, ớt bột, tiêu.\r\n', '- Tôm băm nhỏ. Hành, thì là băm nhỏ.\r\n\r\n- Cho tôm, giò sống, hành, thì là vào bát. Thêm vào 2 thìa cà phê hạt nêm, ½ thìa cà phê ớt bột (nếu muốn ăn cay thì bạn thêm vào 1 quả ớt băm nhỏ), ½ thìa cà phê tiêu.\r\n\r\n- Trộn đều nguyên liệu trong bát, để gia vị thấm chừng 30 phút.\r\n\r\n- Bôi ít dầu ăn vào tay để chống dính, xúc một ít nguyên liệu nặn viên tròn xung quanh đầu đậu bắp.\r\n\r\n- Nhúng miếng đậu bắp quấn tôm qua 1 lớp bột mì khô cho đỡ dính.\r\n\r\n- Nắn lại viên chả cho bọc đều quả đậu bắp.\r\n\r\n- Đun sôi nhiều dầu ăn trong chảo.\r\n\r\n- Thả các viên chả và chiên ngập dầu.\r\n\r\n- Đến khi chả tôm cuốn đậu bắp vàng đều thì lấy ra đĩa có lót giấy thấm dầu để vài phút rồi dùng nóng.\r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(155).jpg'),
(156, 'Gà cuốn lá nếp', '- 500g đùi gà\r\n- 50g hành khô\r\n- 20g lá nếp (lá dứa)\r\n- 1/2 quả ớt chuông\r\n- 2 thìa cà-phê bột gia vị\r\n- 1 thìa cà-phê hạt nêm\r\n- 3 thìa cà-phê đường\r\n- 1/4 thìa cà-phê tiêu\r\n- 1 thìa súp nước hành\r\n- 1 thìa súp giấm\r\n- Dầu ăn\r\n', '- Gà rửa sạch, để thật ráo, lóc bỏ xương, đem tahí lát mỏng to bản.\r\n\r\n- Ướp gà với bột gia vị, hạt nêm, nước hành, tiêu, 2 thìa cà-phê đường, 2 thìa súp dầu ăn, để khoảng 20 phút cho thấm.\r\n\r\n- Lá nếp rửa sạch, để ráo, đặt miếng gà, cuộn tròn thật chặt tay rồi dùng tăm ghim mép lá lại.\r\n\r\n- Than hoa đốt hồng, cho gà cuốn lên vỉ hoặc xiên vào que đem nướng chín vàng. Khi nướng bạn nhớ quết dầu ăn để lá nếp không bị khô và cháy.\r\n\r\n- Gà chín vàng, để ráo dầu, xếp ra đĩa, rắc ớt chuông thái sợi đã ngâm với giấm pha 1 thìa cà-phê đường. Khi dùng, bỏ lá nếp.\r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(156).jpg'),
(157, 'Gà cuộn trứng muối', '- 500g đùi gà\r\n- 1 quả ớt Đà Lạt đỏ\r\n- 500g quả đậu xanh\r\n- 3 quả trứng vịt muối\r\n- Bột gia vị, tiêu, hạt nêm, rau mùi.\r\n', '- Gà sơ chế sạch, lọc bỏ xương đem ướp với bột gia vị, hạt nêm và tiêu để khoảng 15 phút cho thấm.\r\n\r\n- Trứng muối luộc chín, lấy lòng đỏ dầm thật nhuyễn.\r\n\r\n- Quả đậu xanh rửa sạch ngâm nước khoảng 15 phút. Ớt Đà Lạt bỏ ruột, thái lát dài.\r\n\r\n- Trải gà ra đĩa, cho đậu xanh, trứng muối, ớt đỏ vào giữa cuộn cho chặt tay, ghim mép thịt bằng tăm hoặc buộc bằng sợi chỉ. Phần thịt gà còn dư thái miếng vuông vừa ăn.\r\n\r\n- Cho gà cuộn vào hấp khoảng 15 phút. Khi gà chín cho ra đĩa, để nguội thái khoanh tròn, xếp ra đĩa. Trang trí với rau mùi. Dùng kèm cơm. \r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(157).jpg'),
(158, 'Gỏi cuốn tôm chiên', '- Tôm sú: 500g\r\n- Rau mầm: 100g\r\n- Tía tô: 2 cây\r\n- Húng quế: 2 cây\r\n- Rau diếp cá: 5 cây\r\n- Xà lách: 1 cây\r\n- Bánh ướt không nhân: 200g\r\n- Bánh tráng: 1 gói\r\n- Đường, nước mắm, tỏi băm\r\n- Ớt băm, dầu ăn\r\n- Giấm gạo \r\n- Bột tẩm khô chiên giòn \r\n', '- Tôm bóc vỏ, chừa đuôi, khứa nhẹ vài đường dưới bụng tôm. Rau mầm, tía tô, húng quế, diếp cá, xà lách lặt rửa sạch, để ráo. Bánh ướt cắt miếng khoảng 2*6cm.\r\n \r\n- Cho tôm vào tô, rắc ½ gói bột tẩm khô chiên giòn vào, trộn đều rồi cho vào chảo dầu nóng chiên vàng, vớt ra để ráo dầu.\r\n\r\n- Cuốn: thấm mềm bánh tráng bằng nước lọc, xếp xà lách, rau tía tô, húng quế, rau mầm, diếp cá và bánh ướt vào, gấp phần trên và 1 bên mép bánh vào, đặt 1 con tôm lên, phần đuôi hướng ra ngoài bánh, gấp mép còn lại vào rồi cuộn tròn.\r\n\r\n- Pha nước mắm chua ngọt: trộn đều 1,5M giấm gạo với 2M đường, 2M nước mắm, ½ chén nước, 1m ớt băm và 1m tỏi băm.\r\n \r\n- Xếp gỏi cuốn tôm chiên ra dĩa, khi ăn chấm với nước mắm chua ngọt.\r\n', '- Chiên tôm với bột tẩm khô chiên giòn tôm sẽ có vị vừa ăn, vàng đẹp và giòn, giữ được vị ngọt của tôm. \r\n- Khi sơ chế khứa vài đường dưới bụng tôm đề chiên xong tôm không bị cong.', 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(158).jpg'),
(159, 'Hàu cuốn thịt xông khói', '- 8 con hàu\r\n- 4 lát thịt xông khối\r\n- 1 thìa cà-phê tabasco\r\n- 1 thìa súp sốt Worcestershire\r\n- Que xiên.\r\n', '- Hàu cạy vỏ, gỡ lấy thịt đem rửa sạch nhớt, để ráo. Vỏ hàu chà hết bùn đất, rửa lại bằng nước muối, lấy khăn lông sạch thấm khô.\r\n\r\n- Thịt xông khói thái làm đôi.\r\n\r\n- Trải miếng thịt xông khói trên mặt phẳng, đặt hàu lên rồi cuộn lại và dùng que xiên ghim chặt.\r\n\r\n- Bật lò nướng ở nhiệt độ 180 độ C, cho que xiên hàu và thịt vào nướng đến khi thịt xông khối chuyển sang màu nâu nhạt là được.\r\n\r\n- Đặt que xiên lên vỏ hàu, rưới sốt Worcestershire và Tabasco lên.\r\n\r\n- Món này dùng nóng, dọn kèm bát tương ớt.\r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(159).jpg'),
(160, 'Thịt cuốn cải bẹ xanh', '- 300g thịt cua.\r\n- 400g thịt đùi.\r\n- 1.5kg cải bẹ xanh.\r\n- 400g bún nhỏ sợi.\r\n- 1 củ cà rốt.\r\n- 2 miếng đậu phụ trắng, hẹ lá, rau thơm, dầu ăn.\r\n- Sốt: 1/2 bát tương hạt xay nhuyễn, 1/4 bát nước cốt dừa, 1 thìa súp đường, 1/2 thìa cà phê hạt nêm, 1 thìa cà phê tỏi băm nhỏ, 1 thìa súp sốt cà, vừng rang.\r\n', '- Làm sốt: Phi thơm tỏi với dầu ăn, cho nguyên liệu làm sốt vào (trừ vừng), nêm vừa ăn, trút ra bát, khi dùng sẽ rắc vừng.\r\n\r\n- Thịt đùi luộc chín, để nguội, thái sợi dài 4cm. Thịt cua ướp chút dầu vừng, hạt nêm, để vài phút. Phi thơm tỏi, xào thịt đùi và thịt cua, trút ra bát.\r\n\r\n- Cải bẹ xanh chỉ lấy phần lá, rửa sạch, để ráo. Đậu phụ thái sợi dài 4cm, rán vàng. Cà rốt thái như đậu phụ, luộc chín.\r\n\r\n- Trải lá cải ra đĩa, cho bún, vài lá rau thơm, thịt đùi, thịt cua, cà rốt, đậu phụ vào cuộn tròn chặt tay, dùng lá hẹ đã trụng nước sôi cột lại, bày ra đĩa.\r\n', NULL, 9, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(160).jpg'),
(161, 'Bê thui hấp gừng', '- 300g thịt bê thui\r\n- 100g hành lá\r\n- 10g gừng\r\n- Nước mắm gừng: 100ml nước mắm, 1 thìa gừng băm nhỏ, 1 thìa cà-phê tỏi ớt băm, 4 thìa cà-phê đường, 2 thìa cà-phê nước cốt chanh.\r\n', '- Thịt bê thui thái miếng vầu ăn. Gừng gọt vỏ, thái lát.\r\n\r\n- Cho thịt bê thui, gừng và hành lá vào xửng hấp khoảng 5 phút.\r\n\r\n- Hòa tan nước mắm với các nguyên liệu để pha mắm gừng.\r\n\r\n- Dọn thịt bê thui ra đĩa, dùng với nước mắm gừng.\r\n', NULL, 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(161).jpg'),
(162, 'Cá lóc hấp bầu', '- Cá lóc phi lê: 300g\r\n- Thịt xay: 100g\r\n- Bầu: ½ trái nhỏ\r\n- Ớt sừng: 1 trái\r\n- Cần tàu: 1 nhánh\r\n- Hành lá: 1 cây\r\n- Gừng: 2 lát\r\n- Bún tàu: 1 lọn\r\n- Muối, tiêu, đường\r\n- Dầu ăn, bột năng\r\n- Hạt nêm, nước tương\r\n', '- Cá lóc cắt miếng hình chữ nhật 7*4 cm, ướp với 1m hạt nêm  và 1/2m tiêu. Thịt xay ướp với 1M đầu hành băm, 1m hạt nêm, 1 ít tiêu, 1/4m muối và 1m bột năng.\r\n\r\n- Bầu chẻ dọc làm đôi, bỏ ruột, cắt ngang 4cm. Ớt sừng, cần tàu, hành lá và gừng cắt hạt lựu. Hòa tan 1,5M bột năng với ½ chén nước. Bún tàu ngâm mềm.\r\n \r\n- Cho thịt vào miếng bầu, xếp cá lên trên, rải bún tàu dưới dĩa, cho vào nồi hấp 15 phút đến khi bầu và cá đều chín.\r\n\r\n- Làm xốt: xào thêm gừng, cần tàu, hành lá và ớt, cho vào 1 chén nước, nêm 3M nước tương, 2M đường, 1m muối và 1/2M hạt nêm, đun sôi, thêm bột năng pha loãng vào cho xốt hơi sệt lại.\r\n \r\n- Lấy dĩa cá ra khỏi nồi hấp, rưới xốt lên trên, trang trí với ngò rí, dùng nóng với cơm trắng, chấm kèm nước tương.\r\n', '- Trộn bột năng vào thịt để thịt có độ kết dính tốt. Đợi nước sôi mới cho cá vào hấp để cá không bị ra nước ngọt.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(162).jpg'),
(163, 'Cua hấp tắc', '- 1kg cua\r\n- 5 quả tắc (quả quất)\r\n- 1 thìa cà-phê tỏi băm\r\n- 1 thìa cà-phê đường\r\n- Muối\r\n- Rau mùi.\r\n', '- Cua rửa sạch, bẻ ra lấy phần gạch đem bỏ. Úp mai cua lại, cho vào đĩa.\r\n\r\n- Lấy 3 quả tắc thái lát, xếp lên đĩa. Hai quả tắc còn lại vắt lấy nước, trộn với muối, đường, tỏi băm rồi rưới lên bề mặt cua. Sau đó đem hấp khoảng 20 phút là được.\r\n\r\n- Dùng nóng. Trang trí với rau mùi và tắc thái lát.\r\n', NULL, 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(163).jpg'),
(164, 'Đậu mơ hấp lá sen', '- 6 bìa đậu mơ\r\n- 100g hạt sen tươi\r\n- Lá sen, bột bắp\r\n- Gia vị: Dầu ăn, dầu hào chay, hạt nêm.\r\n', '- Hạt sen bỏ tim, rửa sạch, luộc mềm, tán nhuyễn, giữ lại nước luộc.\r\n\r\n- Hạt sen bỏ tim, rửa sạch, luộc mềm, tán nhuyễn, giữ lại nước luộc.\r\n\r\n- Cho nước luộc lên bếp nấu sôi trở lại, cho hạt sen đã tán nhuyễn vào nấu cùng. Nêm 2 thìa cà phê hạt nêm và một chút dầu hào, sau đó dùng nước bột bắp để làm sánh xốt hạt sen.\r\n\r\n- Lá sen, rửa sạch, để ráo. Lót lá sen vào đĩa sâu lòng, xếp đậu mơ lên.\r\n\r\n- Rưới 1 ít nước hấp.\r\n', NULL, 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(164).jpg'),
(165, 'Gà hấp bí ngô', '- 1 quả bí ngô nhỏ\r\n- 1 cái đùi gà\r\n- 100g đậu hạt\r\n- Tỏi, gừng, hành lá\r\n- Gia vị, nước tương, dầu mè.\r\n', '- Đầu tiên, cắt bí ngô theo hình răng cưa và nạo bỏ ruột.\r\n\r\n- Luộc qua gà và đậu. Sau đó, mình xé nhỏ thịt gà ra.\r\n\r\n- Ướp gà và đậu với gia vị, nước tương và dầu mè.\r\n\r\n- Cho hỗn hợp vào trong bí ngô, thêm tỏi và gừng lên trên mặt quả bí.\r\n\r\n- Cuối cùng, hấp chín bí ngô, Rắc chút hành lá lên trên trước khi ăn.\r\n', NULL, 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(165).jpg'),
(166, 'Nấm Đông Cô hấp tôm', '- Tôm thẻ: 300g\r\n- Cá thác lác: 50g\r\n- Mỡ gáy: 20g\r\n- Lòng trắng trứng: 1/2 cái\r\n- Nước dùng: 1 chén\r\n- Nấm đông cô khô: 10 tai\r\n- Cà rốt: 1/4 củ\r\n- Hành lá: 5 cọng\r\n- Bột năng pha loãng\r\n- Gừng sợi, ớt sợi ngâm xoăn\r\n- Tiêu, đường, dầu ăn\r\n- Nước tương, hạt nêm\r\n', '- Tôm lột vỏ, đập dập, quết nhuyễn. Cá quết mịn với 1 phần đầu hành băm. Mỡ luộc chín, băm nhuyễn. Trộn cá, tôm, nêm 1/3m tiêu, 1m Hạt nêm, 1 ít lòng trắng trứng, cho mỡ vào trộn chung.\r\n\r\n- Hành lá cắt khúc. Nấm đông cô ngâm nở, cắt bỏ chân nấm.\r\n \r\n- Vo viên tôm bằng trái tắc, lăn qua bột năng khô, nhồi vào nấm, hấp khoảng 5 phút là chín.\r\n\r\n- Làm xốt chấm: phi thơm đầu hành lá và gừng sợi, thêm 1 chén nước dùng, 1M Nước tương,  1m Hạt nêm, 1/2m đường, pha bột năng vào tạo độ sánh. Cho cà rốt, hành lá và ớt sợi vào đảo đều. Tắt bếp.\r\n \r\n- Múc  nấm đông cô hấp tôm ra dĩa, trang trí với ớt và ngò rí, rắc tiêu, dùng nóng\r\n', '- Luộc nấm đông cô với nước có gừng, Hạt nêm  và hành lá để khử mùi nấm và giúp nấm thơm hơn.\r\n- Lòng trắng trứng sẽ giúp cho nhân tôm sau khi hấp có độ xốp và không bị khô.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(166).jpg'),
(167, 'Nghêu hấp nước tương', '- Nghêu: 1kg\r\n- Rượu vang trắng: 50ml\r\n- Bơ lạt: 40g\r\n- Ngò rí: 5 cây\r\n- Hành lá: 5 cây\r\n- Ớt sừng: 1 trái\r\n- Chanh: 1 trái\r\n- Đường, dầu ăn, bơ lạt, tỏi băm\r\n- Nước tương \r\n', '- Nghêu ngâm nước muối (hoặc nước vo gạo) khoảng 15 phút cho nhả hết cát, rửa sạch vỏ. Ngò rí cắt nhỏ, để riêng cọng và lá. Hành lá cắt nhỏ. Tỏi, ớt băm nhỏ.\r\n \r\n- Phi thơm tỏi và cọng ngò, cho nghêu vào đảo đều, thêm rượu vang trắng, bơ lạt, đậy nắp hấp đến khi nghêu mở vỏ ra, 1,5M Nước tương, 1m đường, cho hành lá, ngò và ớt bằm vào, đảo đều rồi tắt bếp.\r\n\r\n- Pha nước tương chấm: trộn đều 2M nước tương  với 2M nước, 2m đường và 2m nước cốt chanh, cho thêm ớt bằm và tỏi bằm.\r\n \r\n- Cho nghêu hấp ra dĩa, rắc thêm ngò rí và vắt thêm chanh, khi ăn chấm với nước tương pha.\r\n', '- Chọn con nghêu còn khép miệng, đều con, chắc và nặng tay. Chỉ hấp nghêu vừa chín (vừa hé miệng), thịt nghêu sẽ ngọt mà không bị teo nhỏ lại.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(167).jpg'),
(168, 'Ốc nhồi hấp sả', '- Ốc nhồi: 500g\r\n- Thịt giò sống: 120g\r\n- Sả: 3 cây\r\n- Nấm mèo: 3 tai\r\n- Hành lá, hành tỏi băm, sả băm, gừng băm\r\n- Lá chanh cắt nhuyễn, chanh, ớt băm\r\n- Hạt nêm\r\n', '- Ốc nhồi luộc sơ, gỡ lấy thịt, rửa sạch nhớt trên phần miệng ốc, bỏ bớt phần đen ở đuôi. Thịt ốc băm nhỏ, vỏ ốc để riêng.\r\n\r\n- Lá chanh cắt sợi. Nấm mèo ngâm nở, cắt nhuyễn. Hành lá cắt nhỏ. Sả cây cắt thành từng bẹ. \r\n\r\n- Trộn đều thịt ốc,120g giò sống, nấm mèo,1m hành lá, 1m tỏi băm, 1m hành băm, 1m sả băm, 1m gừng băm, 1M lá chanh, 1 ít ớt băm, 1/2m hạt nêm, 1/2m tiêu.\r\n\r\n- Cho cọng sả vào vỏ ốc, lần lượt nhồi thịt vào từng con ốc, đem hấp chín.\r\n\r\n- Pha nước mắm: trộn đều 1M nước mắm, 1M đường, 1/2M nước cốt chanh, 1m gừng băm, 1M lá chanh cắt sợi và 1m ớt băm.\r\n \r\n- Xếp ốc ra dĩa, khi ăn chấm nước mắm và dùng kèm lá tía tô.\r\n \r\n', '- Luộc sơ ốc giúp thịt ốc giòn và mất mùi tanh.\r\n- Chọn lá sả bản to để giữ được nước ngọt trong nhân khi hấp.', 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(168).jpg'),
(169, 'Sườn non hấp xốt tương', '- Sườn non: 300g\r\n- Đậu hũ non dạng tròn: 200g\r\n- Gừng: 1 củ\r\n- Cà rốt: ½ củ\r\n- Ớt sừng: 1 trái\r\n- Rau cần tàu: 1 ít\r\n- Tỏi băm, ớt băm\r\n- Bột năng, dầu mè, tiêu\r\n- Nước tương\r\n- Xốt tương.\r\n', '- Sườn chặt miếng dài khoảng 5cm, dùng dao nhọn xăm đều miếng thịt để thịt mềm và mau thấm gia vị. Ướp sườn với 1M tỏi băm, 1/2M tỏi băm, 1M nước tương , 1M đường, 1M bột năng, 1M dầu mè và 3M xốt tương, trộn đều, để thấm 15 phút.\r\n\r\n- Cà rốt tỉa bông, cắt lát mỏng. Cần tàu cắt khúc. Ớt và gừng cắt sợi. Đậu hũ cắt miếng dày 1cm.\r\n\r\n- Phi thơm tỏi băm với dầu điều, cho sườn vào xào thơm, thêm ½ chén nước vào, xào đến khi sườn săn thì tắt bếp.\r\n\r\n- Xếp đậu hũ non xung quanh dĩa, trút sườn vào giữa, rải gừng và ớt cắt sợi lên trên, cho vào nồi hấp 20 phút.\r\n\r\n- Lấy sườn đã hấp chín ra, cho rau cần tàu lên trên, dùng nóng.\r\n', NULL, 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(169).jpg'),
(170, 'Tôm hấp sả, lá chanh', '- 12 con tôm sú\r\n- 12 lá chanh\r\n- 3 củ sả\r\n- 1 thía súp rượu trắng\r\n- 1 nhánh gừng tươi\r\n- 2 thìa súp muối\r\n- 1 thìa cafe tiêu\r\n- 1 thìa súp nước cốt chanh\r\n- 1 thìa cafe bột ớt\r\n', '- Tôm sơ chế sạch, dùng dao rạch một đường ở sống lưng, rút bỏ chỉ đen.\r\n\r\n- Lá chanh rửa sạch, Sả rửa sạch, thái khúc, đập giập.\r\n\r\n- Ướp tôm với sả, lá chanh, rượu trắng và gừng.\r\n\r\n- Làm muối tiêu chanh ớt: Rang khô muối, đem giã nguyễn rồi trội đều với tiêu, ớt bột và nước cốt chanh.\r\n\r\n- Chuẩn bị nồi hấp, đun sôi nước, đặt tôm vào xửng, hấp khoảng 8-10 phút là chín.\r\n\r\n- Lấy tôm ra, dùng nóng và kèm với muối tiêu chanh ớt.\r\n', NULL, 7, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(170).jpg'),
(171, 'Đậu hũ dồn tôm kho chao', '- Tôm tươi bóc vỏ: 70g\r\n- Giò sống: 50g\r\n- Đậu hũ chiên: 2 miếng\r\n- Hành lá: 2 cọng\r\n- Ớt hiểm : 1 trái\r\n- Tiêu, đường, dầu ăn, chao trắng\r\n- Bột ngọt, hạt nêm, nước tương\r\n \r\n', '- Tôm tươi đập dập băm nhuyễn trộn đều với giò sống, ướp 1/2m hạt nêm, 1/3m bột ngọt, ít tiêu và hành lá cắt khúc, để thấm. Ớt hiểm đập dập.\r\n\r\n- Cắt đôi miếng đậu hũ, dùng dao rạch nhẹ lấy bớt phần ruột, sau đó nhồi nhân vào bên trong, đem chiên sơ, để ráo.\r\n\r\n- Pha xốt kho: 1M đường, 3 viên chao trắng, 1/2 chén nước tương , 1/2 chén nước trắng.\r\n \r\n- Kho đậu hũ dồn tôm với nước xốt, thêm ớt trái, kho đến khi nước sệt lại, nếm vị vừa ăn, tắt lửa, cho hành lá cắt khúc vào.\r\n \r\n- Cho đậu hũ ra dĩa, dùng với cơm trắng.\r\n', '- Để trộn chao được đều ta nên tán nhỏ chao trước khi pha nước xốt.\r\n- Tôm đập dập trước khi băm để tạo độ dai cho phần nhân.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(171).jpg'),
(172, 'Ếch kho tiêu xanh', '- Thịt ếch : 600g\r\n- Tiêu xanh : 50g\r\n- Tỏi : 8 tép\r\n- Húng quế : 50g\r\n- Ớt sừng : 1 trái\r\n- Hành tím băm : 1M\r\n- Dùng kèm: bánh mì, cơm hoặc cháo\r\n- Dầu ăn, dầu điều, muối, đường, nước mắm, ngũ vị hương\r\n- Bột ngọt, xốt Mayonnaise\r\n\r\n \r\n', '- Thịt ếch rửa sạch để ráo, chặt làm tư, ướp 1M hành tím băm, 1m bột ngọt, 1/4m ngũ vị hương, 3m nước mắm, 1/2m muối.\r\n\r\n- Ớt sừng cắt khúc xéo 2cm. Tiêu xanh đập giập.\r\n \r\n- Cho vào nồi đất 1M đường,1/2M dầu ăn nấu lửa nhỏ khi thấy hỗn hợp có màu cánh gián, cho lần lượt tỏi, tiêu xanh, ếch vào xào săn. Thêm 1m dầu điều, 1/2 chén nước nóng kho nhỏ lửa đến khi ếch chín thấm vị, xốt sánh lại thì tắt lửa cho 1M xốt Mayonnaise vào đảo đều, dùng kéo cắt đôi rau húng quế vào nồi kho.\r\n \r\n- Ăn kèm với bánh mì, cơm hoặc cháo.\r\n', '- Cho thêm dầu điều để nước kho có màu đẹp.\r\n- Dùng kéo cắt lá quế ở giai đoạn cuối cùng giúp lá quế giữ được hương vị nhiều hơn là cắt trước.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(172).jpg'),
(173, 'Gà kho me', '- Đùi tỏi gà : 500g\r\n- Me vắt : 100g\r\n- Gừng cắt sợi : 2M\r\n- Sả cắt lát : 1/2 chén\r\n- Mè trắng rang giã dập : 4M\r\n- Ớt hiểm, ngò rí, hành lá\r\n- Dầu điều, ngũ vị hương, đường, muối\r\n- Nước tương, bột ngọt\r\n', '- Me ngâm với nước nóng lọc lấy 2/3 chén nước me. Ớt hiểm đập dập. Hành ngò cắt khúc.\r\n\r\n- Đùi gà chặt miếng vừa ăn, ướp 1/3m bột ngũ vị hương, 2/3 chén nước me, 4M đường, 1/2m muối, 1/2 chén nước tương, 1m bột ngọt, 1M gừng và 2M sả.  Để thấm 10 phút.\r\n \r\n- Phi thơm sả và gừng với 1M dầu điều, cho thịt gà vào xào săn, thêm ớt hiểm, 2M mè rang kho lửa vừa không đậy nắp. Kho đến khi nước hơi sánh lại, tắt lửa, cho hành lá, ngò rí và rắc mè lên trên.\r\n \r\n- Cho món gà kho ra dĩa, ăn kèm với cơm và rau sống.\r\n', '- Kết hợp chút bột ngũ vị hương với nước me sẽ làm nổi bật mùi vị đặc trưng của me.\r\n- Xào thịt gà thật săn giúp gà thấm gia vị hơn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(173).jpg'),
(174, 'Lươn kho sả, ớt', '- Lươn: 300g\r\n- Nước dừa tươi: 1 chén\r\n- Ớt hiểm, ớt bột, sả băm\r\n- Đường, tiêu, nước màu\r\n- Nước mắm, dầu điều, dầu ăn\r\n- Hạt nêm\r\n', '- Lươn làm sạch, cắt khúc 2cm, ướp 1m Hạt nêm, 1m đường, 1m nước màu, 1/3m muối, 2M nước mắm, 1M dầu điều, 1/2m tiêu, để thấm.\r\n \r\n- Phi thơm 2M sả băm, thêm 1/2m ớt bột, cho lươn vào xóc đều cho săn thịt, tắt bếp.\r\n\r\n- Cho lươn vào niêu đất, ban đầu kho lửa to, thêm 3 trái ớt hiểm đập dập, bớt lửa rồi cho một chén nước dừa tươi vào, kho riu riu không đậy nắp để lươn thấm gia vị. Kho đến khi lươn chín thì tắt bếp.\r\n \r\n- Dùng nóng với cơm trắng và rau dưa.\r\n', '- Dùng tro bếp hoặc giấm để sơ chế sạch nhớt lươn.\r\n- Phi thơm sả, ớt rồi xóc đều với lươn sẽ giúp lươn thơm và săn thịt hơn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(174).jpg'),
(175, 'Ốc bươu kho gừng', '- Thịt ốc bươu: 300g\r\n- Củ gừng nhỏ gọt vỏ: 50g\r\n- Hành tím băm: 1M\r\n- Tỏi băm: 1M\r\n- Ớt hiểm: 3 trái\r\n- Dầu điều, dầu ăn, muối, đường, nước mắm,\r\n- Bột ngọt\r\n- Giấm gạo lên men \r\n', '- Ốc bươu bỏ phần ốc bùn phía dưới, xát thịt ốc với ít muối và ít giấm rửa sạch nhớt, để ráo. Ướp với 1m bột ngọt, 1M nước mắm, 1m giấm gạo lên men, 2M hành tỏi băm, gừng sợi và ớt hiểm.\r\n\r\n- Gừng thái chỉ. Ớt hiểm đập dập.\r\n\r\n- Dùng tộ đất cho 1M dầu, 1M đường thắng caramel, thêm 1M dầu điều, cho ốc bươu vào xào săn, kho nhỏ lửa đến khi ốc vừa chín, nước kho cạn sệt.\r\n', '- Thắng caramen và cho ốc vào ngay để ốc không ra nước.\r\n- Kho thịt ốc thật nhanh để ốc không bị dai và khô.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(175).jpg');
INSERT INTO `eating` (`id`, `name`, `material`, `making`, `tips`, `idType`, `bookmark`, `image`) VALUES
(176, 'Thịt bò kho quế', '- 600g thịt bò bắp\r\n- 5g quế chi\r\n- 3 cây sả\r\n- 1 chén nước dừa tươi\r\n- 2M hành tím băm\r\n- Muối, tiêu, đường, ớt bột, dầu điều\r\n- Hạt nêm, xốt tương đậu nành \r\n', '- Thịt bò làm sạch, chần sơ, cắt khối vuông 3cm. Sả cắt khúc, đập dập.\r\n\r\n- Ướp thịt với 2M hành tím băm, 1m đường, 1/2m muối, 2m hạt nêm, 1m ớt bột, 2M dầu điều, 1 miếng quế và sả, để thấm 30 phút.\r\n \r\n- Cho thịt bò vào nồi xào săn rồi cho nước dừa và 1 chén nước lọc vào, đậy nắp, kho lửa nhỏ cho thịt chín mềm, nước sánh lại thì cho 1M xốt tương  vào, đảo đều.\r\n\r\n- Múc thịt bò kho ra dĩa, rắc thêm tiêu, dùng nóng.\r\n', '- Chọn thịt bò có lẫn gân và ít mỡ thì khi kho sẽ ngon và thơm hơn.Có thể kho bằng nồi áp suất thì thời gian kho sẽ ngắn hơn, khi đó chỉ cho ít nước dừa và không thêm nước lọc.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(176).jpg'),
(177, 'Thịt kho khổ qua tây', '- Thịt ba chỉ : 200g\r\n- Khổ qua tây : 2 trái\r\n- Tôm khô : 50g\r\n- Hành tím : 5 củ\r\n- Ớt hiểm cắt lát : 1 trái\r\n- Tỏi băm, hành lá\r\n- Nước mắm, đường, tiêu, dầu ăn\r\n- Bột ngọt, hạt nêm \r\n', '- Thịt ba chỉ cắt con chì. Khổ qua tây cạo vỏ, rửa sach, cắt đôi bỏ ruột, cắt khúc 2cm.\r\n\r\n- Tôm khô ngâm nước ấm cho mềm. Đầu hành lá đập dập, phần lá cắt khúc 2cm. Ớt hiểm cắt lát.\r\n \r\n- Pha hỗn hợp gồm: 2M nước mắm, 2M đường, 2m hạt nêm, 1/3m bột ngọt, 1/2m tiêu và 1/2 chén nước.\r\n\r\n- Cho dầu ăn vào nồi, cho thịt ba chỉ vào xào săn, thêm tỏi băm, hành tím, ớt hiểm, đầu hành lá, tôm khô vào xào thơm, cho khổ qua tây và hỗn hợp đã pha vào kho chung đến khi hỗn hợp sệt lại, cho hành lá vào, tắt lửa.\r\n \r\n- Cho thịt kho ra dĩa, rắc tiêu lên trên, ăn kèm với cơm trắng.\r\n', '- Xào cho thịt ba chỉ săn để loại bớt lượng mỡ trong thịt và tạo độ béo cho món ăn.\r\n- Cạo bỏ phần phấn trắng bên ngoài trái khổ qua tây, giữ lại phần vỏ để món ăn giòn hơn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(177).jpg'),
(178, 'Tôm sú kho lá quế', '- Tôm sú (bỏ đầu, vỏ chừa đuôi): 250g\r\n- Lá quế: 30g\r\n- Hành tím lột vỏ: 10 củ\r\n- Tỏi lột vỏ: 10 tép\r\n- Ớt hiểm: 3 trái\r\n- Đậu phộng rang: 1M\r\n- Dầu ăn, ầu điều, nước mắm, đường, tiêu, bột ngọt\r\n', '- Tôm sú xẻ dọc sống lưng lấy chỉ đen. Cắt bỏ phần đuôi nhọn.\r\n\r\n- Lá quế cắt sợi. Hành tím, tỏi và ớt hiểm đập dập.\r\n\r\n- Ướp tôm với 1m bột ngọt, 2M nước mắm, 1/2m tiêu, trộn đều. Thêm hành tỏi, ớt hiểm đập dập, 1/2 phần lá quế thái sợi vào trộn đều.\r\n \r\n- Cho 2M dầu điều, 1M đường vào tộ đất, thắng caramel. Cho tôm vào xào săn, thêm 2M nước và kho đến khi thịt tôm săn lại, thấm đều gia vị, nước kho sệt lại. Rắc 1/2 phần lá quế cắt sợi còn lại. Tắt lửa.\r\n \r\n- Rắc đậu phộng rang lên mặt.Dùng nóng với cơm trắng, rau sống.\r\n', '- Xẻ dọc sâu lưng tôm để tôm thấm gia vị và khi kho tôm nở bung trông đẹp mắt hơn.\r\n- Ướp trước 1/2 lá quế thái sợi sẽ giúp thịt tôm ngấm mùi lá quế hơn, tạo nên hương vị đặc trưng cho món ăn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(178).jpg'),
(179, 'Vịt kho củ cải mặn', '- Thịt vịt: 400g\r\n- Củ cải muối: 150g\r\n- Cà rốt: 50g\r\n- Gừng: 30g\r\n- Ớt hiểm: 2 trái\r\n- Hành lá, ngò rí, tỏi băm\r\n- Đường, dầu điều\r\n- Bột ngọt, hạt nêm\r\n', '- Gừng gọt vỏ 1/2 băm nhuyễn vắt lấy nước cốt, 1/2 cắt sợi. Vịt rửa sạch, cắt miếng vừa ăn ướp với 1M hạt nêm, 1m bột ngọt, 1,5m đường và 2M nước gừng.\r\n\r\n- Củ cải muối cắt miếng vừa ăn, ngâm khoảng 15 phút, xả lại nhiều lần với nước lạnh, vắt ráo. Cà rốt cắt miếng. Hành ngò cắt nhỏ.\r\n \r\n- Phi thơm gừng và 1M tỏi băm với 2M dầu điều, xào săn thịt vịt, thêm nước cho ngập mặt vịt, cho tiếp cà rốt, củ cải muối, và 2 trái ớt hiểm vào, kho đậy nắp đến khi vịt chín, nước còn sanh sánh, tắt lửa thêm hành ngò.\r\n \r\n- Dùng nóng với cơm trắng.\r\n \r\n', '- Ướp vịt với gừng và phi thơm gừng trước khi xào vịt giúp khử mùi hôi của vịt.\r\n- Ngâm củ cải muối với ít muối và xả nhiều lần với nước giúp bớt vị mặn và củ cải được giòn hơn.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(179).jpg'),
(180, 'Vịt kho sấu', '- Thịt vịt: 1kg\r\n- Măng luộc: 250g\r\n- Sấu: 100g\r\n- Nước dừa tươi: 2 chén\r\n- Ớt hiểm: 3 trái\r\n- Hành tỏi băm\r\n- Tiêu, muối, đường, dầu điều\r\n- Hạt nêm, nước tương\r\n', '- Vịt cắt chéo da. Áp chảo da vịt cho ra bớt mỡ, chặt miếng 4-5 cm, ướp 1M hành tỏi băm, 1m muối, 1M đường, 1m hạt nêm,  4M nước tương  và ớt hiểm đập đập, để thấm 10 phút.\r\n\r\n- Măng cắt miếng hình chữ nhật 1.5 x 2.5 cm. Sấu cắt bỏ cuống, khứa chữ thập làm bốn.\r\n \r\n- Phi thơm hành tỏi với 2M dầu điều, xào săn vịt và măng cho thấm gia vị. Cho 2 chén nước dừa vào kho khoảng 15 phút cho thịt chín, thêm sấu vào kho tiếp và dầm nhẹ cho sấu ra nước chua. Kho đến khi thịt chín mềm, nước hơi sánh thì tắt lửa.\r\n \r\n- Múc vịt kho ra đĩa, rắc thêm hành lá, tiêu, ngò rí và ớt cắt lát lên trên. Dùng với cơm nóng.\r\n \r\n', '- Chọn vịt tươi mới, nạc nhiều, ít mỡ, thịt chắc.\r\n- Chọn măng mạnh tông, dày thịt, giòn và ngọt hơn măng tre tàu.', 8, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(180).jpg'),
(181, 'Bò nướng sa tế', '- Bò phi lê : 600g\r\n- Sả băm : 20g\r\n- Sả : 2 cây\r\n- Tỏi băm : 2M\r\n- Hành tím băm : 2M\r\n- Ớt băm : 2M\r\n- Cọng ngò rí cắt nhỏ : 2M\r\n- Dầu ăn, nước mắm, sữa đặc, ớt bột nhuyễn\r\n- Nước tương, hạt nêm, bột ngọt \r\n- Trang trí: xà lách dún xanh, cà chua\r\n- Ăn kèm: nước tương và bánh mì\r\n', '- Bò ướp với 1/2M nước tương, 1/2M nước mắm ,1m hạt nêm và 1m bột ngọt, để thấm 30 phút.\r\n\r\n- Sả cây đập dập, xé sợi.\r\n\r\n- Làm sa tế: Làm nóng dầu trong chảo, cho sả, tỏi, hành tím và ớt băm vào xào thơm, tiếp tục thêm 1m ớt bột, cọng ngò rí vào quậy đều rồi để nguội.\r\n\r\n- Áp chảo cuộn bò cho săn, phết 1M sữa đặc có đường rồi ướp hỗn hợp sa tế xung quanh.\r\n \r\n- Xếp sả cây xé sợi lên khay, đặt cuộn bò áp chảo trên sả, nướng trong lò 180ºC trong 15 phút đến khi thịt bò chín, cho ra dĩa, để nguội, cắt lát vừa ăn.\r\n \r\n- Xếp thịt bò ra dĩa, dùng kèm bánh mì và nước tương.\r\n', '- Áp chảo nhanh phần bò để tạo mùi thơm cho món ăn và giữ lại nước ngọt của bò.\r\n- Phết sữa đặc lên thịt bò giúp kết dính các gia vị và tạo mùi thơm.', 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(181).jpg'),
(182, 'Bò nướng xốt cam', '- Thịt bò 300g\r\n- Cà rốt 1 củ\r\n- Mè 10g\r\n- Cam vàng 1 quả\r\n- Giá một chút\r\n- Ớt chuông vàng 1/4 trái\r\n- Gia vị.\r\n', '- Thịt bò xắt mỏng, to bản, càrốt, ớt chuông xắt cọng. Thịt bò ướp bột nêm, chút dầu ăn, để 30 phút cho thấm gia vị. Cuộn càrốt, giá, ớt chuông, rắc chút mè, cuộn lại, nướng với lửa than.\r\n\r\n- Xốt cam: cam mài vỏ, vắt nước.\r\n\r\n- Chảo nóng cho bơ, tỏi, nước cam, chút đường, vỏ cam, nêm gia vị vừa ăn. Nước xốt chấm kèm với thịt bò nướng.\r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(182).jpg'),
(183, 'Cá chẽm nướng lá chuối', '- Cá chẽm philê: 300g\r\n- Cà chua: 2 trái\r\n- Cần tàu: 4 nhánh\r\n- Dầu tỏi phi: 2M\r\n- Hành tây: 1 củ\r\n- Ớt sừng cắt sợi\r\n- Sả đập dập: 4 khúc\r\n- Mật ong: 1M\r\n- Lá chuối: 2 tàu\r\n- Ăn kèm: Rau sống các loại\r\n- Dầu điều, nước mắm, muối, tương ớt, tương xí muội\r\n- Bột ngọt\r\n', '- Cá cắt miếng hình chữ nhật 5x8cm dày 2cm, thấm ráo.\r\n\r\n- Cà chua cắt lát dày 5mm. Cần tàu cắt khúc 5cm. Hành tây cắt lát 2mm.\r\n\r\n- Pha nước ướp cá: 2M dầu tỏi phi, 1M mật ong, 1M dầu điều, 1M nước mắm, 1/3m muối, 2M tương xí muội, 1M tương ớt và 2m bột ngọt. Ướp cá khoảng 10 phút cho thấm.\r\n \r\n- Hơ nhanh lá chuối trên lửa cho mềm. Xé lá chuối bản 12cm. Trải lá chuối mặt bóng phía dưới, xếp theo thứ tự từ dưới lên gồm sả đập dập, hành tây, cà chua, cần tàu và cá trên cùng. Gói lá chuối lại cho kín và đem nướng.\r\n \r\n- Món này dùng kèm với rau sống các loại và cơm trắng.\r\n \r\n', '- Dùng nĩa xăm nhẹ mặt cá khi ướp giúp cá dễ thẩm thấu gia vị hơn.\r\n- Hơ nhanh lá chuối trên lửa giúp lá chuối mềm và dễ gói hơn.', 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(183).jpg'),
(184, 'Cánh gà nướng chao', '- Cánh gà 8 cánh\r\n- Gia vị:\r\n	+ Dầu hào : 1 muỗng\r\n	+ Rượu : 1 muỗng\r\n	+ Nước chao : 1 muỗng\r\n	+ Chao đỏ : 2 miếng\r\n	+ Dầu oliu : 1 muỗng.\r\n', '- Đầu tiên, đem gà rửa sạch, ngâm trong nước khoảng chừng 20 phút, vớt ra để ráo.\r\n\r\n- Tiếp theo  chuẩn bị một bát lớn, cho chao đỏ và nước chao vào ghiền nát, sau đó cho các gia vị dầu hào, dầu oliu và rượu vào trộn đều.\r\n\r\n- Cánh gà sau khi để ráo, ướp gà với hỗn hợp trên, để chừng 30 phút cho cánh gà thấm đều.\r\n\r\n- Tới phần nướng,  chuẩn bị một miếng nhôm đã lót sẵn lên khay nướng, để cánh gà đã ướp lên trên. Chuẩn bị lò nướng ở nhiệt độ 200 độ C, cho khay cánh gà vào nướng khoảng 15 phút. Trong 15 phút này có thể lấy cánh gà ra để quét hỗn hợp chao lên rồi nướng tiếp.\r\n\r\n- Để cánh gà chín đều, lấy gà ra quét lên một lớp chao nữa, lấy gà lên phần giá nướng thêm 5 phút. \r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(184).jpg'),
(185, 'Mực nướng bơ', '- 2 con mực\r\n- 15g bơ\r\n- Một ít muối và đường\r\n', '- Rửa sạch mực, cắt phần xúc tu để riêng, xoa muối vào để lột lớp vỏ bên ngoài.\r\n\r\n- Dùng khăn thấm khô 2 mặt miếng mực.\r\n\r\n- Cắt các đường dài 2-3cm dọc 2 bên miếng mực.\r\n\r\n- Đun cách thủy bơ cho đến khi tan hết.\r\n\r\n- Cho muối và đường vào rồi khuấy đều đến khi tan hết.\r\n\r\n- Phết bơ lên mặt miếng mực .\r\n\r\n- Nướng mực ở 180 độ C trong 20-25 phút. \r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(185).jpg'),
(186, 'Sò điệp nướng ba màu', '- Sò điệp : 10 con\r\n- Khoai tây cắt khối vuông : 20g\r\n- Cà rốt cắt khối vuông : 20g\r\n- Đậu bo : 20g\r\n- Xà lách : 4 lá\r\n- Hành lá\r\n- Bợ\r\n- Xốt mayonnaise \r\n', '- Sò điệp tách một bên vỏ, làm sạch.\r\n\r\n- Khoai tây, cà rốt, đậu bo luộc chín, vớt ra ngâm nước lạnh. Hành lá cắt nhỏ.\r\n \r\n- Phi thơm đầu hành, cho 3 loại rau củ vào xào sơ với bơ, cho hành lá vào, nêm 1/4m muối, 1/3m bột ngọt, đảo đều.\r\n\r\n- Cho rau củ lên con sò điệp, rắc thêm hành lá, cho vào lò nướng hoặc nướng trực tiếp trên than khoảng 5 phút thấy nước trong vỏ sò vừa sôi là được.\r\n \r\n- Cho xốt mayonnaise  ngọt dịu lên sò điệp, dùng nóng.\r\n', '- Khoai tây, cà rốt và đậu cần luộc chín kỹ trước để các nguyên liệu chín đồng đều.\r\n- Sò điệp cần làm sạch cát, bỏ phần viền đen bám rìa con sò.', 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(186).jpg'),
(187, 'Sườn non nướng sữa', '- 400g sườn non\r\n- 100g lạc\r\n- 1 củ tỏi\r\n- 1,5 thìa súp sữa đặc có đường\r\n- 2 thìa cà-phê hạt nêm\r\n- 1 thìa cà-phê nước tương.\r\n- Dầu ăn.\r\n', '- Lạc rang vàng, chờ nguội chà bỏ vỏ. Chia lạc làm 2 phần, 1 phần đem xay nhỏ, phần còn lại đem đập giập.\r\n\r\n- Tỏi bóc vỏ, thái lát rồi băm nhỏ, vắt lấy nước cốt, bỏ xác.\r\n\r\n- Sườn non rửa sạch, để ráo, chặt thành từng miếng dài khoảng 4cm, ướp với nước cốt tỏi trong vòng 5 phút cho thấm. Sau đó, cho sữa đặc, hạt nêm, nước tương và lạc xay nhỏ vào trộn đều. Ướp sườn non khoảng 20 phút cho thấm gia vị.\r\n\r\n- Bật lò nướng ở nhiệt độ 180 độ C trong 10 phút. tiếp đến, rưới 1 muỗng canh dầu ăn vào khay rồi xếp sườn non lên nướng khoảng 8 phút. Sau đó, bật lò nướng lên 250 độ C, nướng thêm 5 phút nữa cho sườn non chín vàng.\r\n\r\n- Xếp sườn nướng sữa ra đĩa rắc lạc đập giập lên trên. Dùng nóng với nước tương.\r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(187).jpg'),
(188, 'Thăn bò nướng gan ngỗng', '- Thăn nội bò Mỹ: 200 gr\r\n- Gan ngỗng: 50 gr\r\n- Cream: 20 ml\r\n- Xốt cà chua: 10 gr\r\n- Măng tây: 30 gr\r\n- Xà lách: một bó nhỏ\r\n- Khoai tây nghiền: 200 gr\r\n- Muối, tiêu, bột mì, rượu brandy, bơ\r\n', '- Thịt bò rửa sạch, nướng trên bếp lò cho vàng 2 mặt. Ướp dầu ô liu và một chút muối, tiêu, sau đó đút lò 250oC trong 8 phút.\r\n\r\n- Gan ngỗng tẩm ít bột mì, muối, tiêu đem rán trên chảo cho vàng 2 mặt. Xịt một ít rượu brandy, đem nướng lại trong lò cho vàng và giòn.\r\n\r\n- Măng tây cắt bỏ phần gốc già. Xào với bơ, nêm gia vị vừa ăn.\r\n\r\n- Nấu xốt cà chua với cream, nêm gia vị vừa ăn.\r\n\r\n- Khoai tây nghiền nêm gia vị vừa ăn đem đút lò.\r\n\r\n- Xếp thăn bò ra đĩa, cho măng tây và gan ngỗng lên trên như hình. Rưới xốt, trang trí với khoai tây nghiền và xà lách.\r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(188).jpg'),
(189, 'Thịt ba chỉ nướng trái cây', '- Thịt ba chỉ không da: 200g\r\n- Nước cốt hành: 1m\r\n- Nước cốt tỏi: 1m\r\n- Nước cốt thơm: 1M\r\n- Thơm: 1/4 trái\r\n- Đậu que: 5 cọng\r\n- Lê: 1 trái\r\n- Tương ớt, mật ong, tiêu, dầu ăn\r\n- Nước tương, hạt nêm, xốt Mayonnaise\r\n \r\n', '- Thịt ba chỉ cắt lát mỏng, ướp 2m tương ớt, 1m mật ong, nước cốt hành tỏi, 1/2m tiêu, 1M nước cốt thơm, 1/2m hạt nêm, 2M xốt Mayonnaise, 2m nước tương, để thấm.\r\n\r\n- Thơm, lê gọt vỏ, cắt miếng vừa ăn. Đậu que cắt khúc ngắn.\r\n\r\n- Lần lượt xiên thịt xen kẽ với thơm, lê và đậu que.\r\n \r\n- Cho ít dầu vào chảo, chờ dầu nóng, cho xiên thịt vào nướng áp chảo đến khi chín vàng.\r\n \r\n- Trình bày món ăn ra dĩa, dùng kèm cà chua bi, rau thơm.\r\n', '- Ướp thịt ba chỉ với nước cốt thơm sẽ làm thịt mềm và ngon hơn. Xốt Mayonnaise sẽ giúp cho thịt được mềm và ngon hơn khi nướng.', 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(189).jpg'),
(190, 'Thịt nướng cuộn rau', '- Thịt ba chỉ - 400g\r\n- Rau diếp - 150g\r\n- Hành hoa (chỉ lấy phần củ và thân to trắng), - 1 nắm nhỏ\r\n- Gừng nhỏ - một mẩu\r\n- Tiêu sọ - chục hạt\r\n- Bột thì là - 1/3 thìa cà phê\r\n- Hoa hồi (nếu thích) - hai bông\r\n- Nước tương - 20ml\r\n- Xốt ướp thịt nướng - 10ml\r\n- Dầu ăn - 10ml.\r\n', '- Rửa  sạch thịt, cuộn tròn thịt lại và gói chặt trong túi nylon, để lên ngăn đá tủ lạnh chừng 1 tiếng thì bỏ ra, bóc bỏ túi và thái lát các khoanh thịt khá dễ dàng vì thịt đã đông se lại. Để thịt rã đông hẳn và gỡ các miếng thịt dài thẳng ra.\r\n\r\n- Gừng và hành thái lát chéo. Ướp thịt cùng với gừng, hành, hoa hồi, nước tương, hạt tiêu và nước sốt thịt nướng trong hộp kín, xóc đều, để ngăn mát tủ lạnh chừng 1 tiếng.\r\n\r\n- Chắt nước ướp ra bát con, 1 tay cầm một đầu miếng thịt, 1 tay kẹp đũa vào hai bên miếng thịt tuốt phần gia vị tẩm ướp rơi xuống đĩa hứng. Dùng giấy ăn hoặc khăn bông thấm khô các miếng thịt\r\n\r\n- Bật bếp, đặt chảo chống dính lên và cho 2 thìa cà phê dầu ăn vào láng đều đáy chảo cho đỡ khô vênh miếng thịt khi nướng áp chảo. Nướng đều hai mặt thịt cho tới khi vàng xém và thịt hơi co lại.\r\n\r\n-  Lót giấy bạc vào khay nướng lò, xếp đều thịt vào khay, dùng bàn chải nhỏ chấm phết hết một nửa bát nước ướp thịt vào khắp các miếng thịt. Cuối cùng rắc bột thì là lên trên và cho khay thịt vào lò nướng ở 160 độ C.\r\n\r\n- Nướng 10 phút thì dùng bàn chải nhỏ chấm phần nước ướp còn lại rỏ đều lên bề mặt các miếng thịt, phần nước ướp còn lại này có thể hòa thêm gần 1 thìa cà phê mật ong cho thịt lên màu đẹp, tiếp tục nướng thịt thêm 10 phút.\r\n\r\n- Trong khi chờ thịt chín trong lò, tranh thủ rửa sạch rau diếp, ngâm rau trong chậu nước thả đá lạnh chừng 5 – 7 phút, nước càng lạnh rau càng giòn. Vớt rau vẩy ráo từng lá một, cuộn tròn lá rau theo chiều dọc, quấn dải thịt nướng quanh điểm giữa cuộn rau. Xiên thịt và rau vào xiên tre, mỗi xiên chừng 4 miếng thịt cuộn rau như thế. Làm nhanh tay để thịt nóng.\r\n', NULL, 10, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(190).jpg'),
(191, 'Cải rổ xào trứng muối', '- Cải rổ cọng lớn: 250g\r\n- Lòng đỏ trứng muối đã luộc chín: 2 cái\r\n- Tỏi: 10 tép\r\n- Dầu hào, dầu mè\r\n- Hành lá: 4 cọng\r\n- Ớt sừng\r\n- Gia vị: tiêu, đường, dầu ăn, bột ngọt\r\n', '- Lòng đỏ trứng muối tán thật nhuyễn.\r\n\r\n- Cải rổ tước bỏ xơ, xắt lát xéo, mỏng. Lấy thêm một ít lá, chần sơ.\r\n\r\n- Tỏi đập dập. Hành lá lấy cọng cắt khúc ngắn để riêng, phần lá để riêng.\r\n\r\n- Ớt sừng bỏ hạt, xắt miếng hình quả trám.\r\n\r\n- Đổ nước vào chảo, cho thêm ít muối để chần sơ cải rổ. Sau đó vớt ra, ngâm trong nước lạnh thêm viên đá.\r\n\r\n- Pha trước phần gia vị: Trộn 1M dầu hào, 1m dầu mè, 1/2m đường,1M nước và 1m bột ngọt.\r\n\r\n- Đặt chảo lên bếp, cho 3M dầu ăn, cho tỏi vào chiên cho thơm. Cho lòng đỏ trứng muối vào đảo nhanh tay cho nổi bông, tiếp tục cho cải rổ vào xào chín với cọng hành lá và ớt miếng và nước xốt. Tắt bếp, rắc thêm tiêu.\r\n', '- Chần sơ cải rổ để cải có màu xanh, giảm bớt mùi hăng, khi xào sẽ mau chín hơn.\r\n- Lòng đỏ trứng muối cần xào trước với dầu cho nổi bông, tạo màu sắc đẹp mắt cho món ăn.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(191).jpg'),
(192, 'Ếch xào bông hẹ sa tế', '- Thịt đùi ếch : 300g\r\n- Bông hẹ : 100g\r\n- Hành tây : 1/2 củ\r\n- Đậu phộng rang : 30g\r\n- Ớt sừng cắt sợi\r\n- Hành tỏi băm\r\n- Ngò gai, rau om\r\n- Muối, tiêu, đường, dầu ăn, sa tế tôm\r\n- Giấm gạo lên men\r\n- Bột ngọt \r\n', '- Ướp đùi ếch với 1M hành tỏi băm, 1/3m tiêu, 1/3m muối, 1/2m đường, 1m bột ngọt, 1M sa tế và 1/2M giấm gạo lên men, để thấm khoảng 10 phút.\r\n\r\n- Hành tây cắt múi cau. Bông hẹ cắt khúc 4cm. Rau om, ngò gai cắt khúc ngắn.\r\n \r\n- Phi thơm hành tỏi băm, cho thịt ếch xào với lửa to, xốc đều, thêm 1/2 chén nước, 1/2M sa tế, ếch vừa chín cho hành tây, bông hành, ớt sợi trộn đều đến khi vừa chín, tắt lửa.\r\n \r\n- Cho ra đĩa, rắc đậu phộng, ngò gai, rau om lên trên. Ăn nóng với cơm, chấm với nước tương  và ớt cắt lát.\r\n', '- Chọn đùi ếch to vừa (100g khoảng 4 cặp), nếu chọn to quá thì phải khứa dọc đùi ếch và ướp lâu hơn.\r\nThêm nước trong khi xào giúp thịt ếch chín đều.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(192).jpg'),
(193, 'Khoai tây xào thịt bò', '- Thịt bò philê mềm: 200g\r\n- Khoai tây vàng: 300g\r\n- Tương cà: 20g\r\n- Rau cần tàu: 50g\r\n-Cà chua: 1 trái\r\n-Tỏi băm\r\n- Muối, nước mắm, đường, tiêu, dầu ăn\r\n- Hạt nêm, bột ngọt, xốt Mayonnaise \r\n', '- Thịt bò philê cắt nhỏ, ướp với 1/2m hạt nêm, 1/2m đường, 1m tỏi băm, 1/4m tiêu và 2M xốt Mayonnaise.\r\n\r\n- Khoai tây cắt làm 6 múi, cho vào tô, rắc vào 1/4m muối, 1/4m bột ngọt, trộn đều, cho thêm 1m nước, bọc vải mùng bên ngoài tô, bỏ vào nồi hấp chín (hoặc cho khoai tây vào lò vi sóng).\r\n\r\n- Cà chua cắt làm 6 múi giống khoai tây. Rau cần tàu cắt khúc khoảng 4cm.\r\n \r\n- Lấy khoai tây khỏi nồi hấp (hoặc lò vi sóng). Cho một ít dầu vào chảo, đun nóng, áp chảo nhanh cho phần khoai tây có màu vàng nâu, cho khoai tây ra dĩa.\r\n\r\n- Cho một ít tỏi băm vào chảo, phi vàng; sau đó cho thịt bò vào, đảo đều; rồi cho tiếp cà chua và khoai tây vào, nêm thêm 3M nước, 1M tương cà, 1M xốt Mayonnaise, 1m nước mắm, và cho phần rau cần tàu vào cuối cùng. Tắt lửa.\r\n \r\n- Xếp khoai tây và cà chua quanh dĩa, cho thịt bò vào giữa, trang trí thêm với vài nhánh ngò, dùng nóng.\r\n \r\n', '- Ướp thịt bò cùng xốt Mayonnaise sẽ giúp thịt bò mềm, thơm và có vị rất ngon.\r\n- Khoai tây được hấp chín trước khi xào sẽ giúp khoai tây thêm mềm và dẻo, món ăn thêm ngon hơn.\r\n', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(193).jpg'),
(194, 'Lòng heo xào dứa', '- Gan heo : 100g\r\n- Tim heo : 100g\r\n- Lòng non : 100g\r\n- Thơm : 1/4 trái\r\n- Ớt Đà Lạt đỏ : 1/2 trái\r\n- Cần tây : 50g\r\n- Hành tây : 1/2 củ\r\n- Hành tỏi băm\r\n- Gừng, hành lá, rau răm, ngò rí\r\n- Đường, muối, tiêu, dầu ăn, rượu trắng\r\n- Giấm gạo lên men \r\n- Hạt nêm \r\n- Nước tương\r\n', '- Đun nước sôi với chút muối, giấm gạo lên men  và gừng, luộc lòng trước, sau đó cho tim gan vào chần sơ. Vớt ra ngâm nước cho nguội.\r\n\r\n- Gan heo, tim heo cắt lát 2mm, lòng non cắt khúc 3cm. Ướp 1M hành tỏi băm, 1/2M hạt nêm, 2M nước tương  và 1m đường, ít tiêu để thấm.\r\n\r\n- Thơm cắt rẻ quạt dầy 3mm. Ớt Đà Lạt cắt hình thoi cạnh 2.5cm. Hành tây cắt múi cau. Cần tây tước bỏ xơ, cắt lát xéo. Hành lá, rau răm cắt khúc.\r\n\r\n- Phi thơm hành tỏi gừng, cho lòng vào xào lửa lớn, thêm 1M rượu trắng, xào đến khi lòng gần chín, cho tiếp thơm, ớt Đà Lạt, cần tây và hành tây vào đảo đều, khi rau củ vừa chín tới, tắt lửa.\r\n \r\n- Trút lòng xào ra đĩa, thêm tiêu, ngò rí. Dọn kèm với cơm trắng, khi ăn chấm nước tương và ớt cắt lát.\r\n', '- Chần lòng non trước với nước sôi có ít gừng và giấm giúp lòng được trắng và giữ được độ giòn.\r\n- Khử lòng bằng rượu trắng giúp lòng thơm hơn.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(194).jpg'),
(195, 'Mực xào đậu Hà Lan', '- Mực sữa: 300g\r\n- Đậu Hà Lan: 200g\r\n- Hành tây: 1 củ\r\n- Ớt sừng cắt quả trám: 1 trái\r\n- Tỏi băm: 1M\r\n- Tương ớt: 1M\r\n- Rau răm: 20g\r\n- Muối, dầu ăn, tiêu\r\n- Hạt nêm\r\n- Nước tương\r\n', '- Mực sữa rửa sạch, để nguyên con, ướp 1/2m hạt nêm, 1M nước tương, 1/3m tiêu, 1m tỏi băm.\r\n\r\n- Đậu Hà Lan tước bỏ xơ, cắt xéo. Hành tây cắt múi cau. Rau răm cắt nhỏ.\r\n\r\n- Chần sơ đậu Hà Lan trong nước sôi có chút muối, vớt ra ngâm đá, để ráo.\r\n \r\n- Phi thơm tỏi, cho mực vào xào lửa lớn, tiếp tục cho hành tây, đậu Hà Lan vào, nêm 1M nước tương, cho 1M tương ớt, ớt sừng và rau răm vào, tắt lửa.\r\n \r\n- Cho ra dĩa. Dùng nóng với cơm.\r\n', '- Ướp nước tương vào mực để khi xào mực thấm gia vị mà không bị khô.\r\n- Chần đậu Hà Lan trước giúp đậu giữ màu xanh đẹp và chín đều.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(195).jpg'),
(196, 'Nấm bào ngư xào xốt me', '- Nấm bào ngư: 200g\r\n- Thịt nạc dăm: 50g\r\n- Bông cải xanh: 100g\r\n- Cà rốt: 50g\r\n- Hành lá: 3 nhánh\r\n- Ớt sừng băm: 1M\r\n- Mè rang: 30g\r\n- Tỏi băm: 1M\r\n- Hành tím băm: 1M\r\n- Dầu ăn, dầu mè, đường, tiêu\r\n- Nước tương\r\n- Hạt nêm\r\n- Xốt mayonnaise\r\n', '- Nấm bào ngư xé đôi, ướp 1m hành, tỏi băm, 1M nước tương  và 1/2m đường.\r\n\r\n- Thịt nạc dăm cắt miếng, ướp 1m hành, tỏi băm, 1/2m hạt nêm, 1/2m tiêu.\r\n\r\n- Bông cải xanh tách nhánh thật nhỏ. Cà rốt cắt lát mỏng, dùng dao răng cưa nhấn chiều ngang 1cm. Hành lá cắt khúc.\r\n\r\n- Pha gia vị xào: 2M xốt mayonnaise, 1M nước tương, 1/3m tiêu, 1m dầu mè, 1m mè rang, 1M ớt sừng băm.\r\n \r\n- Phi thơm hành tỏi, xào săn thịt nạc dăm, cho nấm bào ngư vào xào lửa lớn, thêm một ít nước, tiếp tục cho bông cải, cà rốt vào xào chín. Thêm 1/2 chén nước, cuối cùng tắt lửa, thêm xốt xào và hành lá vào, đảo đều, rắc thêm mè rang còn lại.\r\n\r\n- Múc ra dĩa, dùng nóng với cơm.\r\n', '- Nấm bào ngư xé đôi giúp hương vị nấm hòa quyện với gia vị.\r\n- Tách nhỏ bông cải xanh giúp nguyên liệu nhanh chín, không cần phải chần.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(196).jpg'),
(197, 'Nhộng xào bông hẹ', '- Nhộng: 200g\r\n- Bông hẹ: 150g\r\n- Cà rốt: 100g\r\n- Hành tím băm\r\n- Tiêu, đường, nước mắm\r\n- Bột ngọt, xốt mayonnaise\r\n', '- Nhộng rửa sạch, để ráo nước.\r\n\r\n- Bông hẹ cắt bỏ bớt phần gốc, cắt khúc ngắn khoảng 5cm. Cà rốt cắt sợi\r\n \r\n- Đặt chảo lên bếp, cho nhộng vào rang với củ hành tím cho thơm, nêm 1m đường, 1/2m bột ngọt, 1M nước mắm, 1/3m tiêu, cho cà rốt vào đảo đều đến khi cà rốt chín cho bông hẹ vào, nêm thêm 1M xốt mayonnaise vào đảo đều, tắt bếp.\r\n \r\n- Cho nhộng xào ra dĩa, dùng nóng với cơm.\r\n', '- Dùng xốt mayonnaise ở giai đoạn cuối giúp món xào có độ bóng, đẹp, hấp dẫn.\r\n- Cho nhộng vào rang trước với hành và gia vị giúp nhộng thấm gia vị và thơm, không bị chai phần vỏ.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(197).jpg'),
(198, 'Ốc xào khế chua', '- Thịt ốc bươu : 200g\r\n- Khế chua : 2 trái\r\n- Tía tô : 1 nắm\r\n- Nước nghệ tươi\r\n- Hành lá\r\n- Hành tỏi băm\r\n- Ớt cắt sợi, ớt hiểm băm\r\n- Dầu ăn, muối, đường, nước mắm\r\n- Bột ngọt \r\n- Giấm gạo lên men\r\n', '- Ốc sơ chế sạch, để ráo, ướp với 1M hành tỏi băm, 2M nước nghệ, 1M giấm gạo lên men, 1/2m muối, 1/2m bột ngọt, để thấm 5 phút.\r\n\r\n- Khế bỏ rìa, cắt ngang dầy 3mm. Hành lá cắt khúc 3cm, đầu hành chẻ dọc, tía tô cắt sợi 5mm. Khế xốc với chút đường, ép ráo nước.\r\n \r\n- Phi thơm hành tím băm và đầu hành lá, cho ốc vào xào với lửa lớn, ốc chín trút ra để riêng.\r\n\r\n- Tiếp tục xào khế với phần nước ốc cho cạn bớt nước, cho ốc vào trở lại, nêm 1/2M nước mắm, thêm hành lá, ớt băm và ớt sợi vào trộn đều, tắt lửa.\r\n\r\n- Cho ốc ra dĩa, rắc tía tô lên mặt, dùng nóng với cơm.\r\n', '- Xóc khế với đường giúp giảm vị chua, tăng thêm độ ngọt của khế, sau khi xào sẽ ngon hơn.\r\n- Xào ốc với lửa to, trút ra sau đó mới trộn chung với khế xào giúp ốc không quá chín, sẽ giữ độ giòn.\r\n', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(198).jpg'),
(199, 'Sò điệp xào bông cải xanh', '- Cồi sò điệp: 200g\r\n- Bông cải xanh: 300g\r\n- Cà rốt: 1/2củ\r\n- Nấm đông cô tươi: 100g\r\n- Gừng nhỏ: 1 củ\r\n- Hành tỏi băm, ngò rí\r\n- Bột năng, dầu hào, dầu ăn\r\n- Tiêu, muối, đường\r\n- Hạt nêm\r\n', '- Bông cải rửa sạch, để ráo. Cà rốt tỉa hoa, cắt lát mỏng. Nấm đông cô rửa sạch, cắt đôi. Tất cả chần sơ trong nước sôi có pha ít muối, vớt ra để ráo.\r\n\r\n- Gừng 1/2 củ đập dập, cho vào nước đã chần rau củ, đun sôi để chần sò điệp, phần còn lại xắt sợi.\r\n- Sò điệp ướp 1m Hạt nêm, 1M hành băm, 1/2m tiêu, để thấm, phủ bột năng lên sò điệp.\r\n\r\n- Pha nước xốt: trộn đều 1.5M dầu hào, 1/2m đường, 2M nước và 1m Hạt nêm.\r\n \r\n- Phi thơm hành tỏi băm, cho sò điệp vào xào nhanh tay với lửa to trút ra dĩa, tiếp tục cho các loại rau vào xào, thêm xốt rồi đảo đều thấm gia vị, cho sò điệp vào trở lại, thêm 1M gừng xắt sợi, tắt bếp.\r\n \r\n- Cho sò điệp ra dĩa, rắc tiêu, trang trí thêm ngò rí, dùng nóng.\r\n', '- Sò điệp chần sơ với nước có gừng sẽ khử được mùi tanh.\r\n- Xào nhanh tay với lửa to để sò điệp không bị dai hay chảy nước.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(199).jpg'),
(200, 'Vịt xào măng', '- Thịt ức vịt: 300g\r\n- Măng luộc: 300g\r\n- Hành tây: 1/2 củ\r\n- Cà rốt: 50g\r\n- Nước dừa tươi: 1 chén\r\n- Hành lá, ngò rí, ớt sừng, hành tỏi băm\r\n- Rượu trắng, tiêu xay, đường, dầu ăn\r\n- Hạt nêm, nước tương \r\n', '- Ức vịt rửa sạch với rượu và gừng, cắt lát mỏng, ướp với 1/2 m đường, 2m hành tỏi băm, 1/2 m tiêu, 1/2 m Hạt nêm, 1M Nước tương để thấm.\r\n\r\n- Măng cắt miếng dày 3 ly, ngang 5cm ướp với 1/2 m Hạt nêm.\r\n\r\n- Cà rốt cắt sợi dày 5 ly. Hành tây cắt múi cau. Ớt sừng băm nhỏ. Ngò rí cắt khúc.\r\n \r\n- Phi 2M dầu ăn với 2m hành tỏi băm cho thơm, cho thịt vịt vào xào săn, thêm 1m rượu trắng, cà rốt, măng, nước dừa, nếm lại cho vừa ăn. Cuối cùng cho hành tây, tắt lửa, cho hành lá, ớt và rắc thêm tiêu.\r\n\r\n- Múc ra dĩa, trang trí với ngò rí, dùng nóng.\r\n', '- Để khử mùi hôi của vịt nên rửa vịt với nước có pha gừng và rượu trắng.\r\n- Thêm rượu trắng trong lúc xào để thịt thơm hơn.\r\n- Luộc măng trước khi chế biến để loại bỏ chất độc trong măng.', 11, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(200).jpg'),
(201, 'Bánh Mochi Nhật Bản', '– 150g đậu đỏ\r\n– 100g bột nếp\r\n– 20g đường\r\n– 150ml nước lạnh\r\n– Nước cốt dừa\r\n– 1 ít bột năng\r\n', 'Bước 1:\r\n– Đậu đỏ rửa sạch, cho vào nồi, đổ nước xâm xấp, nấu cho mềm\r\n– Lấy một nửa số đậu đỏ đã nấu mềm, đem xay mịn\r\n– Cho số đậu đỏ xay vào lại nồi, tiếp tục nấu với số đậu đỏ còn lại\r\n– Vặn lửa riu riu, liên tục khuấy đều tay, khi đậu mềm như ý thì cho đường vừa miệng vào đến khi đặt sệt thành mứt\r\n\r\nBước 2:\r\n– Cho nước vào tô lớn, bỏ 20g đường vào khuấy tan\r\n– Dùng rây từ từ lược bột nếp vào tô nước, liên tục lấy thìa khuấy để bột không bị vón\r\n– Nhào đến khi hỗn hợp mềm dẻo, cho nước cốt dừa vào nhào đều\r\n– Đem hỗn hợp hấp cách thủy\r\n\r\nBước 3:\r\n– Rải bột năng lên một mặt phẳng sạch\r\n– Cho cục bột đã hấp chín lên, bắt đầu nhào cho ráo và cắt thành những phần đều nhau\r\n– Tán những phần bột đó thành những hình tròn dẹp trên bàn tay, rồi vo viên đậu đỏ bỏ lên, vo tròn lại\r\n– Làm càng tròn thì càng đẹp\r\n', '– Để có lớp vỏ bánh đẹp,  có thể cho màu lúc nhào bột.\r\n– Lăn vỏ bánh qua lớp dừa nạo sẽ có những viên mochi tuyết tuyệt đẹp.', 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(201).jpg'),
(202, 'Fish and Chips (Anh)', '- Phi lê cá (dùng loại nào cũng được)\r\n - Khoai tây\r\n - Bột mỳ\r\n - Trứng\r\n - Bia\r\n - Muối, hạt tiêu, đường, ớt\r\n', '- Rửa sạch cá, thấm khô rồi thái thành từng miếng vừa.\r\n\r\n- Trộn đều bột mỳ với muối, đường, hạt tiêu, ớt bột.  Nếu không ăn được cay thì bạn không cần dùng đến ớt bột.\r\n\r\n- Pha bột với trứng và một chút bia. Bia sẽ giúp cho cá không bị tanh.\r\n\r\n- Nhúng cá vào bột rồi bỏ vào chảo dầu chiên giòn.\r\n\r\n- Với khoai tây, sau khi chiên xong, cho vào chảo sạch rồi đảo với một ít muối tinh trên lửa vừa.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(202).jpg'),
(203, 'Kim chi Hàn Quốc', '- 2 cây cải thảo to khoảng 3kg \r\n- 1 củ cải trắng to \r\n- 2 củ cà rốt \r\n- 1 củ hành tây cỡ vừa \r\n- 1 nhánh gừng bằng ngón tay cái\r\n- 1 củ tỏi \r\n- 1 nắm to hành lá (hoặc nếu có lá hẹ thì cho thêm vào sẽ ngon hơn)\r\n- 80g-100g bột nếp \r\n- 1-1,2 lít nước lọc\r\n- 120g-150g bột ớt Hàn Quốc \r\n- 80g-100g đường\r\n- 150ml-200ml nước mắm\r\n- Muối hạt to (200-300g)\r\n', '- Cải thảo cắt bỏ phần cuống tròn của cải, chẻ cải ra làm đôi hoặc làm 4 phần, nhúng qua nước để khi rắc muối bám vào sẽ bám hơn. Xát muối vào phần bẹ trắng rải thêm muối vào bên trong của bẹ cả, xát như vậy cho đến khi hết. Cứ 20 – 30 phút lại trở mặt cải. Cứ như vậy đến khi cải mềm dẻo (có thế gập vào mà không bị nứt gãy là được). Khi cải đã mềm dẻo, đem xả sạch với nước 2-3 lần cho đỡ mặn, xếp cải vào rổ để rổ nghiêng cho cải ráo nước (không được vắt cải để nhanh ráo nước vì như vậy sẽ làm nát cải).\r\n\r\n-  Trong lúc đợi cải ngấm muối thì tiến hành làm cà rốt, củ cải, hành tây, hành lá bỏ vỏ và lá úa rửa sạch để ráo nước. Hành tây thái nhỏ đem xay nhuyễn cùng gừng, tỏi ra một hỗn hợp nhuyễn mịn màu vàng nhạt. \r\n\r\n- Cho bột nếp hoà với 1-1,2 lít nước khi đun quấy đều tay để bột không bị cháy). Khi thấy bột sôi lăn tăn thì cho đường vào quấy cho tan, đun sôi lại rồi tắt bếp để bớt nguội (không để nguội hẳn vì khi đó trộn bột ớt lên màu không đẹp).\r\n\r\n- Tiếp đến cho ớt bột Hàn Quốc, ớt bột Việt Nam vào quấy đều trong khoảng 20p cho ớt nên màu đẹp rồi đổ tiếp hỗn hợp hành gừng tỏi xay nhuyễn, thêm nước mắm vào trộn đều.\r\n\r\n- Cà rốt, củ cải bào sợi khoảng 3cm, hành lá thái khúc 3cm đem trộn đều với sốt bên trên. Đeo bao tay rồi phết sốt lên khắp phần bẹp trắng của cây cải, chú ý phết cả vào phần giữa các bẹ với nhau để toàn bộ phần cải đều có sốt.\r\n\r\n- Cuộn tròn kim chi rồi xếp vào hộp thuỷ tinh hoặc hộp nhựa (không nên nén kim chi đầy và chặt vì sau khi lên men, kim chi sẽ tiết ra nước). Bạn để ngoài đến khi kim chi lên men khoảng 2-3 ngày tùy thời tiết (ấn vào thấy sủi bong bóng nhỏ) thì cho vào tủ lạnh ăn dần.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(203).jpg'),
(204, 'Lẩu Thái', '- 200 gram mực tươi\r\n- 200 gram nghiêu\r\n- 200 g tôm\r\n- 20 lá chắp hay còn gọi là lá chanh của Thái Lan. \r\n- 20 gram giềng\r\n- 4 củ sả tươi\r\n- 5 quả ớt\r\n- 20g hành tím\r\n- Ngò rí ( rau mùi ) 20 gram\r\n- 3 quả cà chua\r\n- 5 lít nước dùng hầm từ xương ống\r\n- Tương ớt, Mè Thái, dầu điều\r\n- 1 hộp sữa tươi\r\n- Ngũ vị hương và một số gia vị thông thường khác\r\n', '- Cho dầu ăn vào nồi, bật bếp đun nóng già dầu ăn. Rồi bắt đầu cho cà chua thái lát, giềng đập dập, hành tím băm vào đảo cho dậy mùi thơm rồi cho tiếp xả đập dập, mè rang, tương ớt, ngũ vị hương, dầu điều vào.\r\n\r\n-  Đến bước này với các nguyên liệu tạo màu như cà chua, tương ớt, giếng, dầu điều sẽ làm nồi lẩu có màu khá sặc sỡ. Vừa cho nguyên liệu các bạn vừa để lửa ở mức vừa và đảo đều tay.\r\n\r\n-  Sau khi đã cho hết các nguyên liệu vào và đun được khoảng 3,4 phút. Các bạn đổ nước dùng đã chuẩn bị trước từ nước xương hầm vào và tiếp tục đun đến khi sôi.\r\n\r\n-  Khi nồi lẩu đã sôi các bạn bắt đầu cho muối, knorr, nước mắm sao cho vừa đủ không mặn cũng không nhạt. Cho tiếp rau mùi, lá chanh vào khoảng 30 giây để cho nồi nước lẩu càng được thơm hơn.\r\n\r\n- Tiếp theo vớt phần cái ra ngoài như là gừng, xả, giềng… để chúng ta chỉ sử dụng phần nước thôi. Hoặc bạn có thể chắt phần nước sang 1 nồi khác để chúng ta thực hiện bước tiếp theo.\r\n\r\n-  Đun sôi nồi nước lẩu trở lại rồi cho sữa tươi và vài lá chanh vào là đã có nồi nước lẩu Thái vừa chua, vừa cay, lại thơm.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(204).jpg'),
(205, 'Pate gan Ngỗng kiểu Pháp', '-  0,5 kg gan ngỗng \r\n-  1 củ hành tây cắt hạt lựu \r\n-   muỗng cafe tỏi cắt miếng (khoảng 4 múi tỏi) \r\n-  1/2 muỗng cafe húng tây \r\n-  1/2 muỗng cafe muối \r\n-  1/2 muỗng cafe đường \r\n-  2 lá thơm, 2 muỗng rượu \r\n-  Bơ nhạt \r\n-  2 muỗng cafe tiêu\r\n', '- Gan ngỗng sát muối vào rửa sạch , để ráo nước rồi lọc sạch hết các phần mỡ bám trên gan. Sau khi thái nhỏ gan, cho húng tây, muối, đường vào để ướp 15 phút cho ngấm đều gia vị vào gan.\r\n\r\n- Phi hành lên cho thơm, rồi cho tỏi vào xào tiếp cho tỏi chín, chừng 1 phút, tránh để tỏi khét.\r\n\r\n- Cho gan ngỗng vừa ướp + lá thơm vào đảo sơ qua rồi cho thêm rượu vào xào khoảng 10 phút cho gan thật chín xong tắt bếp.\r\n\r\n- Bạn bỏ lá thơm ra rồi chắt bỏ phần nước đi. Cho gan nấu chín vào máy xay và trong lúc xay thì cắt từng miếng bơ cho xay chung với tiêu.\r\n\r\n- Nêm lại cho vừa ăn, nếu thấy nhạt thì cho thêm gia vị. Vì gan đã chín nên xay rất nhanh và  sẽ thấy hỗn hợp hơi lỏng. Tuy nhiên, khi để pate vào hộp rồi để tủ lạnh cho đông lại, khi ăn sẽ thấy patê đặc hơn và thái miếng ăn với bánh mì thì tuyệt vời. Gan ngỗng thường ăn nóng nhưng khi gan làm patê phải ăn khi lạnh mới cảm nhận được hết vị ngon béo của gan ngỗng\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(205).jpg'),
(206, 'Pizza Ý', '- 500gr bột mỳ \r\n- 1 thìa canh đường (đường vàng sẽ ngon hơn) \r\n- 10- 12,5gr men nở (instant dry yeast) \r\n- 5gr muối, tiêu \r\n- 50ml dầu olive \r\n- 300ml nước \r\n- 6 thìa canh Pizza sauce (sốt cà chua kiểu Ý) \r\n- 150gram thịt bò xay hoặc băm nhỏ \r\n- 1/3 quả ớt chuông xanh – thái hạt lựu \r\n- 1/4 củ hành tây nhỏ – thái hạt lựu \r\n- 60-70gram pho mát Mozzarella bào sợi \r\n- 1 củ hành khô – bóc vỏ, băm nhỏ\r\n-  2-3 tép tỏi (tỏi nước ngoài thì 1 tép to) – bóc vỏ, băm nhỏ \r\n- 2 thìa canh rượu vang đỏ (không bắt buộc) \r\n- Lá basil tươi\r\n', '- Ngâm men nở với nước ấm 10′ rồi đem trộn cùng bột mì, muối, đường và dầu olive . Dùng tay nhào khoảng 15′ đến khi bột mịn, không dính tay thì lấy màng nilon bọc lại, ủ bột 40 phút. Sau đó bột sẽ nở gấp đôi, dùng tay đấm mạnh vào cục bột cho bột xẹp khi hoàn toàn, nhào tiếp bột lần nữa cho mịn nhuyễn rồi chia bột thành các cục nhỏ hơn.\r\n\r\n- Lấy bột đã ủ ra,dùng cây lăn bột lăn thành hình tròn mỏng để làm đế bánh pizza.\r\n\r\n-  Bật lò 250 độ C trước khi nướng bánh từ 30-40 phút. Đặt pizza stone hoặc đế khuông pizza vào lò. \r\n\r\n- Bắc chảo lên bếp, để lửa to, đun nóng 1 thìa canh dầu Olive. Đợi dầu nóng thì phi thơm hành tỏi. \r\n\r\n- Cho thịt bò vào xào. Khi thịt chín khoảng 80% thì cho 3 thìa canh Pizza sauce & rượu vang (nếu có) vào chảo. Hạ lửa vừa, xào đến khi thịt chín và lượng nươc trong chảo cạn bớt. Nêm lại muối, tiêu cho vừa ăn. Trút ra bát, để sang một bên. Ớt chuông và hành tây các bạn thái hạt lựu trộn với 1 thìa canh dầu Olive \r\n\r\n-  Đem phần đế bánh ra khây, cho phần sốt thịt bò với ớt chuông, hành tây và basil xếp xen kẽ sao cho nhân có màu sắc xanh đỏ hài hòa. \r\n\r\n-  Nướng Pizza ở nhiệt độ 250 độ C trong khoảng 12-15 phút, đến khi bánh chín vàng, pho mát hơi sôi lăn tăn là được. \r\n\r\n- Dùng nóng. Pizza ăn không hết có thể bảo quản trong hộp kín, để ngăn mát tủ lạnh trong khoảng 1 ngày. Khi ăn thì nướng lại ở 190-200độ C trong khoảng 5 phút, Pizza sẽ giòn trở lại.\r\n\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(206).jpg'),
(207, 'Salad Nga', '- Thịt nguội: 450g\r\n- Trứng: 5 quả\r\n- Khoai tây: 2 củ\r\n- Dưa chuột ngâm chua: 5 quả\r\n- Cà rốt: 3 củ\r\n- Lá thì là\r\n- Hành tây: 1 củ\r\n- Mayonnaise\r\n- Ngô ngọt đóng hộp hoặc đậu hà lan\r\n', '- Lá thì là cắt bỏ rễ, lá úa rồi rửa sạch.\r\n\r\n- Cà rốt, khoai tây gọt bỏ vỏ rửa sạch.\r\n\r\n- Trứng rửa qua nước, cho vào 1 nồi khác rồi luộc chín khoảng 13p. \r\n\r\n- Cho nước vào nồi đun sôi rồi thả khoai tây cũng 1 chút muối vào luộc cùng cho đậm đà, đối với cà rốt cũng vậy. Luộc đến khi chín thì vớt ra và để ráo nước.\r\n\r\n- Các nguyên liệu sau khi luộc xong thì để cho nguội. Trứng bóc vỏ, thái hạt lựu, để lại 1 quả thì để trình bày cùng món ăn. Khoai tây và cà rốt đã nguội thì đem ra thái hạt lựu. \r\n\r\n- Dưa chuột ngâm chua, hành tây và thịt nguội  thái thành các miếng vuông nhỏ.\r\n\r\n- Với đậu Hà lan, dùng loại đậu tươi thì bạn cũng đem luộc chín như khoai tây, nếu là loại hộp đóng sẵn thì bạn đổ ra ngoài cho ráo nước.\r\n\r\n- Sau khi đã chuẩn bị đầy đủ các nguyên liệu sử dụng một cái bát to rồi đổ tất cả các nguyên liệu đã thái nhỏ vào với nhau, sau đó cho mayonnaise vào salad.\r\n', '- Luộc riêng cà rốt và khoai tây để không bị mất vị và màu của từng loại.\r\n- Khi luộc trứng dùng đũa khuấy đều trứng trong nồi để lòng đỏ trứng luôn ở chính giữa khi thái ra trình bày sẽ được đẹp hơn.', 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(207).jpg'),
(208, 'Sashimi Nhật Bản', '– Cá hồi\r\n– Cá ngừ\r\n– Củ cải\r\n– 1 vài lá Chiso (tía tô)\r\n– 1 con dao sắc\r\n– 1 đĩa lớn\r\n– Mù tạt (wasabi)\r\n– Nước tương Nhật (xì dầu)\r\n– Gừng hồng Nhật\r\n', '- Để có món sashimi hoàn hảo thì yếu tố đầu tiên phải kể đến đó là chọn được những miếng cá tươi ngon. Để chọn được những miếng cá hồi tươi ngon, khi đi mua có thể dùng tay ấn vào thớ thịt, nếu thấy chắc có sự đàn hồi tốt, màu sắc và đường vân tươi sáng thì đó là cá tươi. Cá sau khi chọn được miếng tươi ngon, các nàng rửa sạch, sau đó dùng khăn sạch thấm khô cá.\r\n\r\n- Chuẩn bị 1 chiếc dao sắc, dùng dao xẻ dọc theo đường giữa 2 thớ cá. Khi chạm đến phần da, miết lưỡi dao sang 2 bên để lọc được riêng phần phi lê cá hồi.\r\n\r\n- Dùng dao sắc cắt cá thành những miếng mỏng có độ dày khoảng 0.5cm. Chú ý không ấn dao mạnh tay nhé và dao phải thật sắc để cắt được miếng cá hồi mà không làm nát cá.\r\n\r\n- Củ cải trắng chúng mình đem rửa sạch, bỏ vỏ, thái mỏng củ cải. Lá tía tô rửa sạch, để ráo nước. Xếp cá lên đĩa cùng củ cải sợi, tía tô, wasabi. Chuẩn bị chanh tươi và nước tương ăn kèm.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(208).jpg'),
(209, 'Sushi Nhật Bản', '– 300g gạo Nhật\r\n– 100g phi-lê cá hồi\r\n– 100g bơ sáp\r\n– 200g dưa chuột\r\n– 30 củ cải trắng và cà-rốt bào sợi\r\n– 50g cọng cải bẹ xanh\r\n– 30g trứng cá\r\n– 60g giấm Nhật\r\n– 2 thìa caphê đường\r\n– 1 thìa cà-phê muối\r\n– 1 gói lá rong biển\r\n– 30g wasabi bột\r\n– Nước tương Nhật.\r\n', '– Gạo vo sạch, cho vào nồi nấu hơi khô. Cơm chín, lấy ra khỏi nồi, tán ra khay cho cơm được ráo.\r\n\r\n– Giấm hòa với đường, muối cho tan, rồi trộn đều với cơm. Dưa chuột bỏ ruột, thái que, dày 1cm. Bơ và cá hồi thái giống dưa chuột.\r\n\r\n– Cọng cải bẹ xanh xay nhuyễn, vắt lấy nước rồi cho vào wasabi bột nhồi cho bột đặc lại là được.\r\n\r\n– Cho rong biển vào khuôn tam giác, cho cơm, xếp cá hồi, dưa chuột vào, rồi cho cơm vào tiếp, phủ kín. Ép chặt khuôn lại. Lấy khúc sushi ra khỏi khuôn, thái miếng dày 1-1,5cm bằng dao đã nhúng qua giấm. Để trứng cá hồi lên mặt mỗi miếng sushi.\r\n\r\n– Khi ăn, bạn chấm sushi với nước tương Nhật pha mù-tạt và củ cải trắng, cà-rốt bào sợi.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(209).jpg'),
(210, 'Bánh Tacos Mexico', '- 5 cái bánh mì tròn\r\n- 300g thịt bò\r\n- 1 búp xà lách\r\n- 1 quả ớt chuông đỏ\r\n- 1 trái bắp Mỹ\r\n- 1 quả cà chua\r\n- 1 củ hành tây\r\n- 200ml giấm\r\n- 50g tương cà\r\n- 17g đường\r\n- 9g muối\r\n- 9g tiêu\r\n- Một ít tương ớt và mayonaise\r\n- 30ml dầu ăn\r\n', '- Thái nhỏ xà lách, rửa sạch, để ráo.\r\n\r\n- Rửa sạch ớt chuông, cắt nhỏ. Tách hạt bắp Mỹ, rửa sạch, để ráo.\r\n\r\n- Rửa sạch hành tây, thái sợi nhỏ. Đặt chảo lên bếp, mở lửa vừa. Cho 200ml giấm, 50ml nước, 15g đường, 5g muối, 5g tiêu cùng hành tây vào tao sơ khoảng 2 phút rồi tắt bếp.\r\n\r\n- Rửa sạch cà chua, xắt hạt lựu. Đặt chảo lên bếp, cho 15ml dầu ăn vào và mở lửa lớn. Khi dầu nóng, thả cà chua vào nấu đến khi cà chua ra nước. Nêm thêm 2g muối, 2g tiêu và cho 50g tương cà vào, trộn đều rồi tắt bếp.\r\n\r\n- Rửa sạch thịt bò, thái nhỏ. Đặt chảo lên bếp, cho 15ml dầu ăn vào, mở lửa vừa. Cho thịt bò, bắp Mỹ, 2g muối, 2g tiêu, 2g đường vào xào đều.\r\n\r\n-Cho nước xốt cà vào trộn đều thêm 2 phút nữa rồi tắt bếp.\r\n\r\n- Cho bánh mì vào chảo chiên đều 2 mặt đến khi bánh giòn.\r\n\r\n- Xếp bánh mì cùng xà lách, ớt chuông, hành muối, thịt bò, bắp cùng xốt ra và thưởng thức. Thêm một ít tương ớt và mayonaise nếu thích.\r\n', NULL, 14, 0, 'https://myteamhus1997.000webhostapp.com/eatingDB/eatingre%20(210).jpg');
